  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 65, '{"itemType":"CLAUSE","clause_number":"10.5","clause_title":"Alcoholic Liquor or Drugs","clause_text":"The Contractor shall not, otherwise than in accordance with the laws and regulations for the time being in force in the Country, import, sell, give, barter or otherwise dispose of any alcoholic liquor or drugs. The Contractor shall use all reasonable endeavours to prevent any such importation, sale, gift, barter or disposal by its employees or agents.","general_condition":"The Contractor shall not, otherwise than in accordance with the laws and regulations for the time being in force in the Country, import, sell, give, barter or otherwise dispose of any alcoholic liquor or drugs. The Contractor shall use all reasonable endeavours to prevent any such importation, sale, gift, barter or disposal by its employees or agents.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall not, otherwise than in accordance with the laws and regulations for the time being in force in the Country, import, sell, give, barter or otherwise dispose of any alcoholic liquor...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":65}'::jsonb),
