INSERT INTO contracts (id, name, timestamp, metadata, uses_subcollections, created_at, updated_at) VALUES
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'Atrium General Conditions', 1769433603347, '{"source": "rebuild_script", "version": "1.0"}'::jsonb, true, '2026-01-26T13:20:03.346Z', '2026-01-26T13:20:03.346Z');
INSERT INTO contract_sections (contract_id, section_type, title) VALUES
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 'General Conditions'),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'PARTICULAR', 'Particular Conditions');
INSERT INTO contract_items (contract_id, section_type, order_index, item_data) VALUES
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 0, '{"itemType":"CLAUSE","clause_number":"1.1","clause_title":"Definitions","clause_text":"In the Contract, as defined in the Particular Conditions attached to these Conditions of Contract as Appendix A, the following words and expressions shall have the meanings hereby assigned to them except where the context otherwise requires: (a) \"Advance Payment Bond\" means the bond named in Clause 7.1 (Provision of Bonds) as the Advance Payment Bond. (b) \"Bills of Quantities\" means the document entitled bills of quantities, duly completed and priced, forming a part of the Contract. (c) \"Conditions of Contract\" means these Conditions of Contract including Appendices forming a part of the Contract. (d) \"Contractor''s Drawings\" means all drawings, calculations, computer programmes and other software, samples, patterns, models, manuals and other documents and information of a technical nature (if any) to be submitted by the Contractor under the Contract. (e) \"Contractor''s Equipment\" means all apparatus, machinery, vehicles and other things of whatsoever nature required for the carrying out and completion of the Works and the remedying of defects therein other than Plant and Free Issue Items. (f) \"Contractor''s Representative\" means the person for the time being in office as the Contractor''s Representative pursuant to Clause 10.1 (Contractor''s Representative). (g) \"Cost\" means all expenditure reasonably incurred (or to be incurred), whether on or off the Site, including overhead and similar charges, but does not include profit. (h) \"Defects Liability Period\" has the meaning assigned by Clause 21.2 (Defects after Taking Over). (i) \"Employer''s Representative\" means the person for the time being appointed by the Employer and notified in writing to the Project Manager, the Supervision Consultant and the Contractor to act on behalf of the Employer for the purposes of the Contract to the extent provided for by the said appointment. (j) \"Extended Warranty\" means any guarantee or warranty either express or implied whether provided for by the Contract or otherwise which extends beyond the expiry of the relevant Defects Liability Period. (k) \"Final Certificate\" means the certificate referred to in Clause 26.4 (Issue of Final Certificates). (l) \"Form of Tender\" means the completed, priced and signed form of tender submitted by the Contractor as part of its Tender and forming a part of the Contract. (m) \"Free Issue Items\" has the meaning assigned by Clause 6A (Free Issue Items). (n) \"Instructions to Tenderers\" means the document entitled instructions to tenderers forming a part of the Contract. (o) \"Interim Certificate\" means the certificate referred to in Clause 26.2 (Issue of Interim Certificates). (q) \"Nominated Subcontractor\" means any person nominated by the Employer (whether in the Contract, pursuant to a power to make such nominations specifically reserved in the Contract or by way of a Variation Order issued pursuant to Clause 22.1 (Variation Orders)) to receive a subcontract from the Contractor for any specified part of the Works when acting within the scope of such subcontract. (r) \"Particular Conditions\" means the terms, conditions and provisions set down in Appendix A to these Conditions of Contract. (s) \"Performance Bond\" means the bond named in Clause 7.1 (Provision of Bonds) as the Performance Bond. (t) \"Plant\" means machinery, apparatus, materials and all other things to be provided by the Contractor under the Contract for incorporation in the Works including the supply-only items (if any) to be supplied by the Contractor under the Contract. (u) \"Portion of the Works\" means a part of a Section of the Works or a part of another Portion of the Works which part, in each case, may from time to time be designated as such by the Employer and notified in writing to the Project Manager, the Supervision Consultant and the Contractor. The residue of the said Section of the Works or said other Portion of the Works shall itself automatically constitute a Portion of the Works. (v) \"Programme\" means the programme submitted by the Contractor in accordance with Clause 17.3 (Programme) and approved by the Project Manager and any revised programme which may be submitted by the Contractor to the Project Manager and approved by the Project Manager from time to time and which shall, upon such approval, be substituted for the previous programme. (w) \"Project Manager''s Representative\" means any representative of the Project Manager appointed from time to time by the Project Manager under Clause 2.5 (Project Manager''s Representative). (x) \"Provisional Sum\" has the meaning assigned by Clause 22A.1 (Definition of Provisional Sum). (y) \"Site\" means the place or places provided or made available by the Employer where work is to be done by the Contractor, or to which Plant or Free Issue Items are to be delivered together with so much of the area surrounding the same as the Contractor shall with the written consent of the Project Manager use in connection with the Works otherwise than merely for the purposes of access. (z) \"Specification\" means the document entitled specification forming a part of the Contract together with any modifications thereof or additions thereto or deletions therefrom as may from time to time be made in accordance with the Contract. (aa) \"Subcontractor\" means any Nominated Subcontractor or any person to whom any part of the Works has been sublet by the Contractor with (where required in accordance with Clause 3.2 (Subletting)) the consent in writing of the Project Manager and the Supervision Consultant and includes the Subcontractor''s legal successors but not assigns. (bb) \"Supervision Consultant''s Representative\" means any representative of the Supervision Consultant appointed from time to time by the Supervision Consultant under Clause 2A.5 (Supervision Consultant''s Representative). (cc) \"Suspension Order\" has the meaning assigned by Clause 18.1 (Suspension Order). (dd) \"Taking-Over Certificate\" has the meaning assigned by Clause 20.1 (Taking-Over Certificate). (ee) \"Tender\" means the Contractor''s priced offer to the Employer for the carrying out and completion of the Works and the remedying of defects therein. (ff) \"Tests on Completion\" means such tests to be made by the Contractor before the relevant Section or Portion of the Works is taken over by the Employer as are provided for in the Contract or otherwise agreed between the Employer or the Supervision Consultant and the Contractor. (gg) \"Variation Order\" has the meaning assigned by Clause 22.1 (Variation Orders). (hh) \"Works\" means all Plant to be provided and work to be done by the Contractor under the Contract.","general_condition":"In the Contract, as defined in the Particular Conditions attached to these Conditions of Contract as Appendix A, the following words and expressions shall have the meanings hereby assigned to them except where the context otherwise requires: (a) \"Advance Payment Bond\" means the bond named in Clause 7.1 (Provision of Bonds) as the Advance Payment Bond. (b) \"Bills of Quantities\" means the document entitled bills of quantities, duly completed and priced, forming a part of the Contract. (c) \"Conditions of Contract\" means these Conditions of Contract including Appendices forming a part of the Contract. (d) \"Contractor''s Drawings\" means all drawings, calculations, computer programmes and other software, samples, patterns, models, manuals and other documents and information of a technical nature (if any) to be submitted by the Contractor under the Contract. (e) \"Contractor''s Equipment\" means all apparatus, machinery, vehicles and other things of whatsoever nature required for the carrying out and completion of the Works and the remedying of defects therein other than Plant and Free Issue Items. (f) \"Contractor''s Representative\" means the person for the time being in office as the Contractor''s Representative pursuant to Clause 10.1 (Contractor''s Representative). (g) \"Cost\" means all expenditure reasonably incurred (or to be incurred), whether on or off the Site, including overhead and similar charges, but does not include profit. (h) \"Defects Liability Period\" has the meaning assigned by Clause 21.2 (Defects after Taking Over). (i) \"Employer''s Representative\" means the person for the time being appointed by the Employer and notified in writing to the Project Manager, the Supervision Consultant and the Contractor to act on behalf of the Employer for the purposes of the Contract to the extent provided for by the said appointment. (j) \"Extended Warranty\" means any guarantee or warranty either express or implied whether provided for by the Contract or otherwise which extends beyond the expiry of the relevant Defects Liability Period. (k) \"Final Certificate\" means the certificate referred to in Clause 26.4 (Issue of Final Certificates). (l) \"Form of Tender\" means the completed, priced and signed form of tender submitted by the Contractor as part of its Tender and forming a part of the Contract. (m) \"Free Issue Items\" has the meaning assigned by Clause 6A (Free Issue Items). (n) \"Instructions to Tenderers\" means the document entitled instructions to tenderers forming a part of the Contract. (o) \"Interim Certificate\" means the certificate referred to in Clause 26.2 (Issue of Interim Certificates). (q) \"Nominated Subcontractor\" means any person nominated by the Employer (whether in the Contract, pursuant to a power to make such nominations specifically reserved in the Contract or by way of a Variation Order issued pursuant to Clause 22.1 (Variation Orders)) to receive a subcontract from the Contractor for any specified part of the Works when acting within the scope of such subcontract. (r) \"Particular Conditions\" means the terms, conditions and provisions set down in Appendix A to these Conditions of Contract. (s) \"Performance Bond\" means the bond named in Clause 7.1 (Provision of Bonds) as the Performance Bond. (t) \"Plant\" means machinery, apparatus, materials and all other things to be provided by the Contractor under the Contract for incorporation in the Works including the supply-only items (if any) to be supplied by the Contractor under the Contract. (u) \"Portion of the Works\" means a part of a Section of the Works or a part of another Portion of the Works which part, in each case, may from time to time be designated as such by the Employer and notified in writing to the Project Manager, the Supervision Consultant and the Contractor. The residue of the said Section of the Works or said other Portion of the Works shall itself automatically constitute a Portion of the Works. (v) \"Programme\" means the programme submitted by the Contractor in accordance with Clause 17.3 (Programme) and approved by the Project Manager and any revised programme which may be submitted by the Contractor to the Project Manager and approved by the Project Manager from time to time and which shall, upon such approval, be substituted for the previous programme. (w) \"Project Manager''s Representative\" means any representative of the Project Manager appointed from time to time by the Project Manager under Clause 2.5 (Project Manager''s Representative). (x) \"Provisional Sum\" has the meaning assigned by Clause 22A.1 (Definition of Provisional Sum). (y) \"Site\" means the place or places provided or made available by the Employer where work is to be done by the Contractor, or to which Plant or Free Issue Items are to be delivered together with so much of the area surrounding the same as the Contractor shall with the written consent of the Project Manager use in connection with the Works otherwise than merely for the purposes of access. (z) \"Specification\" means the document entitled specification forming a part of the Contract together with any modifications thereof or additions thereto or deletions therefrom as may from time to time be made in accordance with the Contract. (aa) \"Subcontractor\" means any Nominated Subcontractor or any person to whom any part of the Works has been sublet by the Contractor with (where required in accordance with Clause 3.2 (Subletting)) the consent in writing of the Project Manager and the Supervision Consultant and includes the Subcontractor''s legal successors but not assigns. (bb) \"Supervision Consultant''s Representative\" means any representative of the Supervision Consultant appointed from time to time by the Supervision Consultant under Clause 2A.5 (Supervision Consultant''s Representative). (cc) \"Suspension Order\" has the meaning assigned by Clause 18.1 (Suspension Order). (dd) \"Taking-Over Certificate\" has the meaning assigned by Clause 20.1 (Taking-Over Certificate). (ee) \"Tender\" means the Contractor''s priced offer to the Employer for the carrying out and completion of the Works and the remedying of defects therein. (ff) \"Tests on Completion\" means such tests to be made by the Contractor before the relevant Section or Portion of the Works is taken over by the Employer as are provided for in the Contract or otherwise agreed between the Employer or the Supervision Consultant and the Contractor. (gg) \"Variation Order\" has the meaning assigned by Clause 22.1 (Variation Orders). (hh) \"Works\" means all Plant to be provided and work to be done by the Contractor under the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"In the Contract, as defined in the Particular Conditions attached to these Conditions of Contract as Appendix A, the following words and expressions shall have the meanings hereby assigned to them exc...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":0}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 1, '{"itemType":"CLAUSE","clause_number":"1.2","clause_title":"Interpretation","clause_text":"The use of the words persons or parties shall include firms and corporations and any organisation having legal capacity. Except where the context requires otherwise, words indicating one gender include all genders.","general_condition":"The use of the words persons or parties shall include firms and corporations and any organisation having legal capacity. Except where the context requires otherwise, words indicating one gender include all genders.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The use of the words persons or parties shall include firms and corporations and any organisation having legal capacity. Except where the context requires otherwise, words indicating one gender includ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":1}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 2, '{"itemType":"CLAUSE","clause_number":"1.3","clause_title":"Singular and Plural","clause_text":"Except where the context requires otherwise, words indicating the singular also include the plural and words indicating the plural also include the singular.","general_condition":"Except where the context requires otherwise, words indicating the singular also include the plural and words indicating the plural also include the singular.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Except where the context requires otherwise, words indicating the singular also include the plural and words indicating the plural also include the singular.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":2}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 3, '{"itemType":"CLAUSE","clause_number":"1.4","clause_title":"Headings","clause_text":"The headings used in these Conditions of Contract shall not be deemed to be part thereof or be taken into consideration in the interpretation or construction thereof or of the Contract.","general_condition":"The headings used in these Conditions of Contract shall not be deemed to be part thereof or be taken into consideration in the interpretation or construction thereof or of the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The headings used in these Conditions of Contract shall not be deemed to be part thereof or be taken into consideration in the interpretation or construction thereof or of the Contract.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":3}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 4, '{"itemType":"CLAUSE","clause_number":"1.5","clause_title":"References to Clauses","clause_text":"A reference in these Conditions of Contract to any Clause is, except where it is expressly stated to the contrary, a reference to such Clause herein.","general_condition":"A reference in these Conditions of Contract to any Clause is, except where it is expressly stated to the contrary, a reference to such Clause herein.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"A reference in these Conditions of Contract to any Clause is, except where it is expressly stated to the contrary, a reference to such Clause herein.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":4}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 5, '{"itemType":"CLAUSE","clause_number":"1.6","clause_title":"Notices, Consents, Approvals and Agreements","clause_text":"(a) Wherever in the Contract provision is made for the giving or issuing of a notice, consent, approval, certificate, determination or request by any person then unless otherwise specified such notice, consent, approval, certificate, determination or request shall be in writing and the words \"notify\", \"consent\", \"approve\", \"certify\" and \"determine\" shall be construed accordingly. (b) Wherever a provision of the Contract includes the word \"agree\", \"agreed\" or \"agreement\", then unless otherwise specified such provision requires the agreement to be in writing.","general_condition":"(a) Wherever in the Contract provision is made for the giving or issuing of a notice, consent, approval, certificate, determination or request by any person then unless otherwise specified such notice, consent, approval, certificate, determination or request shall be in writing and the words \"notify\", \"consent\", \"approve\", \"certify\" and \"determine\" shall be construed accordingly. (b) Wherever a provision of the Contract includes the word \"agree\", \"agreed\" or \"agreement\", then unless otherwise specified such provision requires the agreement to be in writing.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) Wherever in the Contract provision is made for the giving or issuing of a notice, consent, approval, certificate, determination or request by any person then unless otherwise specified such notice...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":5}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 6, '{"itemType":"CLAUSE","clause_number":"1.7","clause_title":"Communications","clause_text":"(a) Wherever in the Contract provision is made for a communication to be \"written\" or \"in writing\", then unless otherwise specified this means any hand-written, type-written or printed communication including communications by telefax but excluding electronic mail and communications by modem. (b) Any written communication given by the Supervision Consultant under the Contract to any person other than the Project Manager shall also bear the signature of the Project Manager provided that any written communication given by the Supervision Consultant to the Supervision Consultant''s Representative under Clause 2A.6 (Supervision Consultant''s Power to Delegate) and the copying of any such communication to the Contractor and to the Employer shall not require such signature of the Project Manager. (c) The Contractor shall comply with the Project Manager''s procedures for the time being in force relating to communications and any accompanying documentation (and the distribution of copies thereof) between the Employer, the Project Manager, the Supervision Consultant, the Contractor and any other persons notified in writing by the Project Manager to the Contractor.","general_condition":"(a) Wherever in the Contract provision is made for a communication to be \"written\" or \"in writing\", then unless otherwise specified this means any hand-written, type-written or printed communication including communications by telefax but excluding electronic mail and communications by modem. (b) Any written communication given by the Supervision Consultant under the Contract to any person other than the Project Manager shall also bear the signature of the Project Manager provided that any written communication given by the Supervision Consultant to the Supervision Consultant''s Representative under Clause 2A.6 (Supervision Consultant''s Power to Delegate) and the copying of any such communication to the Contractor and to the Employer shall not require such signature of the Project Manager. (c) The Contractor shall comply with the Project Manager''s procedures for the time being in force relating to communications and any accompanying documentation (and the distribution of copies thereof) between the Employer, the Project Manager, the Supervision Consultant, the Contractor and any other persons notified in writing by the Project Manager to the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) Wherever in the Contract provision is made for a communication to be \"written\" or \"in writing\", then unless otherwise specified this means any hand-written, type-written or printed communication i...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":6}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 7, '{"itemType":"CLAUSE","clause_number":"1.8","clause_title":"Gregorian Calendar","clause_text":"All dates and periods of time referred to in the Contract shall be ascertained in accordance with the Gregorian calendar.","general_condition":"All dates and periods of time referred to in the Contract shall be ascertained in accordance with the Gregorian calendar.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"All dates and periods of time referred to in the Contract shall be ascertained in accordance with the Gregorian calendar.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":7}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 8, '{"itemType":"CLAUSE","clause_number":"1.9","clause_title":"Joint and Several","clause_text":"If the Contractor consists of more than one legal person, the persons comprising the same shall be jointly and severally liable to the Employer for each and every liability and obligation under the Contract, and any of the events listed in Clause 30.2 (Definition of Default) shall be deemed to have occurred in relation to the Contractor if it has occurred in relation to any one of the persons comprising the Contractor. Such persons shall designate one of them to act as leader with authority to bind the Contractor and each such person and shall notify the Employer accordingly. The composition or the legal status of the Contractor shall not be altered without the prior consent of the Employer.","general_condition":"If the Contractor consists of more than one legal person, the persons comprising the same shall be jointly and severally liable to the Employer for each and every liability and obligation under the Contract, and any of the events listed in Clause 30.2 (Definition of Default) shall be deemed to have occurred in relation to the Contractor if it has occurred in relation to any one of the persons comprising the Contractor. Such persons shall designate one of them to act as leader with authority to bind the Contractor and each such person and shall notify the Employer accordingly. The composition or the legal status of the Contractor shall not be altered without the prior consent of the Employer.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If the Contractor consists of more than one legal person, the persons comprising the same shall be jointly and severally liable to the Employer for each and every liability and obligation under the Co...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":8}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 9, '{"itemType":"CLAUSE","clause_number":"1.10","clause_title":"Waiver","clause_text":"No failure to exercise and no delay by either the Employer, the Project Manager, the Supervision Consultant or the Contractor in exercising any right, power or privilege under the Contract shall operate as a waiver thereof nor shall any single or partial exercise by either the Employer, the Project Manager, the Supervision Consultant or the Contractor of any right, power or privilege preclude any other or further exercise thereof or the exercise of any other right, power or privilege.","general_condition":"No failure to exercise and no delay by either the Employer, the Project Manager, the Supervision Consultant or the Contractor in exercising any right, power or privilege under the Contract shall operate as a waiver thereof nor shall any single or partial exercise by either the Employer, the Project Manager, the Supervision Consultant or the Contractor of any right, power or privilege preclude any other or further exercise thereof or the exercise of any other right, power or privilege.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"No failure to exercise and no delay by either the Employer, the Project Manager, the Supervision Consultant or the Contractor in exercising any right, power or privilege under the Contract shall opera...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":9}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 10, '{"itemType":"CLAUSE","clause_number":"1.11","clause_title":"Entire Agreement","clause_text":"The Contract constitutes the entire agreement between the Employer and the Contractor concerning the Works and accordingly supersedes and cancels all discussions and other documents in whatever form concerning the same.","general_condition":"The Contract constitutes the entire agreement between the Employer and the Contractor concerning the Works and accordingly supersedes and cancels all discussions and other documents in whatever form concerning the same.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contract constitutes the entire agreement between the Employer and the Contractor concerning the Works and accordingly supersedes and cancels all discussions and other documents in whatever form c...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":10}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 11, '{"itemType":"CLAUSE","clause_number":"1.12","clause_title":"Amendments","clause_text":"The Contract shall not be amended except by a written document signed by duly authorised representatives of the Employer and the Contractor.","general_condition":"The Contract shall not be amended except by a written document signed by duly authorised representatives of the Employer and the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contract shall not be amended except by a written document signed by duly authorised representatives of the Employer and the Contractor.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":11}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 12, '{"itemType":"CLAUSE","clause_number":"2.1","clause_title":"Project Manager''s Powers and Duties","clause_text":"The Project Manager shall have such powers, discretions, functions and authorities as are specified in the Contract and as may be implied therefrom and shall carry out such duties (including without limitation issuing instructions, decisions, certificates, orders, determinations, consents, notices and approvals) as are specified in the Contract. Provided that: (a) the Project Manager shall have no authority to amend the Contract; and (b) the Project Manager shall have no authority to relieve the Contractor of any of its duties, obligations or responsibilities under the Contract, nor to order any work involving delay in completion or any extra payment to the Contractor by the Employer nor to make any variation to the Works.","general_condition":"The Project Manager shall have such powers, discretions, functions and authorities as are specified in the Contract and as may be implied therefrom and shall carry out such duties (including without limitation issuing instructions, decisions, certificates, orders, determinations, consents, notices and approvals) as are specified in the Contract. Provided that: (a) the Project Manager shall have no authority to amend the Contract; and (b) the Project Manager shall have no authority to relieve the Contractor of any of its duties, obligations or responsibilities under the Contract, nor to order any work involving delay in completion or any extra payment to the Contractor by the Employer nor to make any variation to the Works.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Project Manager shall have such powers, discretions, functions and authorities as are specified in the Contract and as may be implied therefrom and shall carry out such duties (including without l...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":12}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 13, '{"itemType":"CLAUSE","clause_number":"2.2","clause_title":"Project Manager''s Decisions, Orders and Instructions","clause_text":"The Contractor shall comply with the decisions, orders and instructions given by the Project Manager in accordance with the Contract.","general_condition":"The Contractor shall comply with the decisions, orders and instructions given by the Project Manager in accordance with the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall comply with the decisions, orders and instructions given by the Project Manager in accordance with the Contract.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":13}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 14, '{"itemType":"CLAUSE","clause_number":"2.3","clause_title":"Unwritten Decisions","clause_text":"If the Contractor shall, within (but not after) two (2) working days after being given by the Project Manager any decision, order or instruction otherwise than in writing, request the Project Manager in writing to confirm such decision, order or instruction in writing then that decision, order or instruction shall not be effective until such written confirmation has been received by the Contractor.","general_condition":"If the Contractor shall, within (but not after) two (2) working days after being given by the Project Manager any decision, order or instruction otherwise than in writing, request the Project Manager in writing to confirm such decision, order or instruction in writing then that decision, order or instruction shall not be effective until such written confirmation has been received by the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If the Contractor shall, within (but not after) two (2) working days after being given by the Project Manager any decision, order or instruction otherwise than in writing, request the Project Manager ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":14}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 15, '{"itemType":"CLAUSE","clause_number":"2.4","clause_title":"Reconfirmation of Decisions","clause_text":"If the Contractor shall, by written notice to the Project Manager within (but not after) seven (7) days after receiving any decision, order or instruction of the Project Manager in writing or written confirmation under Clause 2.3 (Unwritten Decisions), dispute or question the decision, order or instruction giving its reasons for so doing it shall serve notice on the Project Manager in accordance with Clause 32 (Notices) and the Project Manager shall reconsider his decision, order or instruction and within a further period of seven days by notice in writing, with reasons therefor, to the Contractor and the Employer, confirm, reverse or vary such decision, order or instruction.","general_condition":"If the Contractor shall, by written notice to the Project Manager within (but not after) seven (7) days after receiving any decision, order or instruction of the Project Manager in writing or written confirmation under Clause 2.3 (Unwritten Decisions), dispute or question the decision, order or instruction giving its reasons for so doing it shall serve notice on the Project Manager in accordance with Clause 32 (Notices) and the Project Manager shall reconsider his decision, order or instruction and within a further period of seven days by notice in writing, with reasons therefor, to the Contractor and the Employer, confirm, reverse or vary such decision, order or instruction.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If the Contractor shall, by written notice to the Project Manager within (but not after) seven (7) days after receiving any decision, order or instruction of the Project Manager in writing or written ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":15}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 16, '{"itemType":"CLAUSE","clause_number":"2.5","clause_title":"Project Manager''s Representative","clause_text":"The Project Manager''s Representative shall be appointed in writing by and be responsible to the Project Manager and shall carry out only such duties and have such powers, discretions, functions and authorities as may be delegated to him by the Project Manager under Clause 2.6 (Project Manager''s Power to Delegate).","general_condition":"The Project Manager''s Representative shall be appointed in writing by and be responsible to the Project Manager and shall carry out only such duties and have such powers, discretions, functions and authorities as may be delegated to him by the Project Manager under Clause 2.6 (Project Manager''s Power to Delegate).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Project Manager''s Representative shall be appointed in writing by and be responsible to the Project Manager and shall carry out only such duties and have such powers, discretions, functions and au...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":16}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 17, '{"itemType":"CLAUSE","clause_number":"2.6","clause_title":"Project Manager''s Power to Delegate","clause_text":"The Project Manager may from time to time in writing delegate to the Project Manager''s Representative any of the powers, discretions, functions and/or authorities vested in him or any of his duties and he may at any time revoke any such delegation in writing. The Project Manager shall deliver to the Contractor, to the Employer and to the Supervision Consultant a copy of any such written delegation or revocation and the same shall not have effect until all such copies have been so delivered. Any written communication given by the Project Manager''s Representative to the Contractor in accordance with such delegation shall have the same effect as though it had been given by the Project Manager Provided always that if the Contractor questions any communication of the Project Manager''s Representative the Contractor shall be entitled within (but not after) four (4) days of the receipt thereof by the Contractor to refer the matter to the Project Manager who will thereupon confirm, reverse or vary the content of such communication and such confirmation, reversal or variation shall (subject to the provisions relating to the resolution of disputes contained in the Particular Conditions) be final notwithstanding the provisions of Clause 2.4 (Reconfirmation of Decisions).","general_condition":"The Project Manager may from time to time in writing delegate to the Project Manager''s Representative any of the powers, discretions, functions and/or authorities vested in him or any of his duties and he may at any time revoke any such delegation in writing. The Project Manager shall deliver to the Contractor, to the Employer and to the Supervision Consultant a copy of any such written delegation or revocation and the same shall not have effect until all such copies have been so delivered. Any written communication given by the Project Manager''s Representative to the Contractor in accordance with such delegation shall have the same effect as though it had been given by the Project Manager Provided always that if the Contractor questions any communication of the Project Manager''s Representative the Contractor shall be entitled within (but not after) four (4) days of the receipt thereof by the Contractor to refer the matter to the Project Manager who will thereupon confirm, reverse or vary the content of such communication and such confirmation, reversal or variation shall (subject to the provisions relating to the resolution of disputes contained in the Particular Conditions) be final notwithstanding the provisions of Clause 2.4 (Reconfirmation of Decisions).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Project Manager may from time to time in writing delegate to the Project Manager''s Representative any of the powers, discretions, functions and/or authorities vested in him or any of his duties an...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":17}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 18, '{"itemType":"CLAUSE","clause_number":"3.1","clause_title":"Assignment","clause_text":"(a) Except as otherwise provided in the Contract, the Contractor shall not assign the Contract or any part thereof or any benefit, obligation or interest therein or thereunder. If the Contractor wishes to obtain financing for the performance of any of its obligations under the Contract and as a condition of such financing it is required to assign or charge by way of security to a bank or other financial institution all or any of the monies due or to become due to it under the Contract it shall obtain the Employer''s prior written consent to such assignment or charge. (b) The Employer shall have the right to assign the Contract or any part thereof or any benefit, obligation or interest therein or thereunder.","general_condition":"(a) Except as otherwise provided in the Contract, the Contractor shall not assign the Contract or any part thereof or any benefit, obligation or interest therein or thereunder. If the Contractor wishes to obtain financing for the performance of any of its obligations under the Contract and as a condition of such financing it is required to assign or charge by way of security to a bank or other financial institution all or any of the monies due or to become due to it under the Contract it shall obtain the Employer''s prior written consent to such assignment or charge. (b) The Employer shall have the right to assign the Contract or any part thereof or any benefit, obligation or interest therein or thereunder.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) Except as otherwise provided in the Contract, the Contractor shall not assign the Contract or any part thereof or any benefit, obligation or interest therein or thereunder. If the Contractor wishe...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":18}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 19, '{"itemType":"CLAUSE","clause_number":"3.2","clause_title":"Subletting","clause_text":"The Contractor shall not sublet the whole of the Works. Except in the case of subcontracts to Nominated Subcontractors and except in any case where the Contract specifically permits subcontracting, the Contractor shall not sublet any part of the Works (whether that part involves the carrying out of any work or the provision of any Plant or a combination of such activities) without the prior written consent of the Project Manager and the Supervision Consultant and only (such prior written consent having been obtained) upon such terms as the Project Manager may require. The Contractor shall obtain the prior written consent of the Project Manager and the Supervision Consultant to proposed Subcontractors. The Contractor shall give the Project Manager not less than twenty eight (28) days'' notice of the intended date of the commencement of each Subcontractor''s work and of the commencement of such work on the Site. No subcontract whether with a Nominated Subcontractor, a Subcontractor to whom the Contract specifically permits subcontracting or a Subcontractor to whose subcontract the Project Manager and the Supervision Consultant have given prior written consent shall relieve the Contractor from any liability or obligation under the Contract and the Contractor shall be responsible for the acts, defaults and neglects of each Subcontractor, its agents or employees as fully as if they were the acts, defaults or neglects of the Contractor, its agents or employees. The Contractor shall be responsible for the observance by all Subcontractors of all provisions of the Contract. Every subcontract shall contain a prohibition on assignment in the same terms as Clause 3.1 (Assignment) and a total prohibition on subletting the whole of the works covered thereby and a prohibition on subletting any part of the works covered thereby without the prior written consent of the Project Manager and the Supervision Consultant.","general_condition":"The Contractor shall not sublet the whole of the Works. Except in the case of subcontracts to Nominated Subcontractors and except in any case where the Contract specifically permits subcontracting, the Contractor shall not sublet any part of the Works (whether that part involves the carrying out of any work or the provision of any Plant or a combination of such activities) without the prior written consent of the Project Manager and the Supervision Consultant and only (such prior written consent having been obtained) upon such terms as the Project Manager may require. The Contractor shall obtain the prior written consent of the Project Manager and the Supervision Consultant to proposed Subcontractors. The Contractor shall give the Project Manager not less than twenty eight (28) days'' notice of the intended date of the commencement of each Subcontractor''s work and of the commencement of such work on the Site. No subcontract whether with a Nominated Subcontractor, a Subcontractor to whom the Contract specifically permits subcontracting or a Subcontractor to whose subcontract the Project Manager and the Supervision Consultant have given prior written consent shall relieve the Contractor from any liability or obligation under the Contract and the Contractor shall be responsible for the acts, defaults and neglects of each Subcontractor, its agents or employees as fully as if they were the acts, defaults or neglects of the Contractor, its agents or employees. The Contractor shall be responsible for the observance by all Subcontractors of all provisions of the Contract. Every subcontract shall contain a prohibition on assignment in the same terms as Clause 3.1 (Assignment) and a total prohibition on subletting the whole of the works covered thereby and a prohibition on subletting any part of the works covered thereby without the prior written consent of the Project Manager and the Supervision Consultant.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall not sublet the whole of the Works. Except in the case of subcontracts to Nominated Subcontractors and except in any case where the Contract specifically permits subcontracting, th...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":19}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 20, '{"itemType":"CLAUSE","clause_number":"4.1","clause_title":"Languages","clause_text":"(a) The Contract has been prepared and agreed in the Ruling Language and all documents delivered thereunder or in connection therewith (including without limitation all correspondence, drawings, specifications, notices, certificates, instructions, orders, decisions, determinations, consents, approvals and other documents submitted to any of the Employer, the Project Manager, the Supervision Consultant or the Contractor) shall also be in the Ruling Language. In the event of any translation of the Contract or any part thereof or of any such documents as aforesaid into any other language, the same shall continue to be construed and interpreted according to the Ruling Language version which shall (to the extent allowed by law) therefore prevail in the event of any conflict. If, notwithstanding the foregoing, any competent court should hold that any such other translation shall prevail over the relevant Ruling Language version, that Ruling Language version shall be referred to in order to assist in interpretation of the other translation. (b) The Contractor shall at its own Cost comply with such language requirements as may from time to time be applicable to dealings between the Contractor and any governmental ministry, department, authority, corporation or municipality. (c) All the Contractor''s and Subcontractors'' superintending and technical personnel working in connection with the performance of the Works shall be fluent in the Ruling Language.","general_condition":"(a) The Contract has been prepared and agreed in the Ruling Language and all documents delivered thereunder or in connection therewith (including without limitation all correspondence, drawings, specifications, notices, certificates, instructions, orders, decisions, determinations, consents, approvals and other documents submitted to any of the Employer, the Project Manager, the Supervision Consultant or the Contractor) shall also be in the Ruling Language. In the event of any translation of the Contract or any part thereof or of any such documents as aforesaid into any other language, the same shall continue to be construed and interpreted according to the Ruling Language version which shall (to the extent allowed by law) therefore prevail in the event of any conflict. If, notwithstanding the foregoing, any competent court should hold that any such other translation shall prevail over the relevant Ruling Language version, that Ruling Language version shall be referred to in order to assist in interpretation of the other translation. (b) The Contractor shall at its own Cost comply with such language requirements as may from time to time be applicable to dealings between the Contractor and any governmental ministry, department, authority, corporation or municipality. (c) All the Contractor''s and Subcontractors'' superintending and technical personnel working in connection with the performance of the Works shall be fluent in the Ruling Language.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) The Contract has been prepared and agreed in the Ruling Language and all documents delivered thereunder or in connection therewith (including without limitation all correspondence, drawings, speci...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":20}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 21, '{"itemType":"CLAUSE","clause_number":"4.2","clause_title":"Governing Law","clause_text":"The Contract shall be governed by and construed in accordance with the laws and regulations of, and from time to time applicable in, the Country.","general_condition":"The Contract shall be governed by and construed in accordance with the laws and regulations of, and from time to time applicable in, the Country.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contract shall be governed by and construed in accordance with the laws and regulations of, and from time to time applicable in, the Country.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":21}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 22, '{"itemType":"CLAUSE","clause_number":"5.1","clause_title":"Governing Law","clause_text":"The Contractor shall submit to the Project Manager for approval by the Supervision Consultant: (a) within the times given in the Specification, such drawings, specifications, calculations, computer programmes and other software, manuals, samples, patterns, models and other documents and information of a technical nature as may be called for in the Specification, and in the numbers therein required; (b) during the progress of the Works, by such dates as the Project Manager or the Supervision Consultant may require, such drawings of the general arrangement and details of the Works and such specifications, calculations, computer programmes and other software, manuals, samples, patterns, models and other documents and information of a technical nature as specified in the Contract and such further drawings and technical documents and information as the Supervision Consultant may require. The Supervision Consultant shall within such period of time after receipt of any of the aforesaid drawings, specifications, calculations, computer programmes and other software, manuals, samples, patterns, models and other documents and information of a technical nature as may be stated in the Specification (or in any case where there is no period of time so stated within the period after such receipt stated in the Particular Conditions subject to the volume of such submissions at the relevant time being reasonable, having regard to the current Programme and the Specification) either approve the same in writing (which in the case of a drawing, specification, calculation or manual shall be done by returning one copy of the same to the Contractor with such approval endorsed thereon) or notify the Contractor of his disapproval thereof or of any comments or queries which he may have thereon. If the Supervision Consultant shall disapprove any drawing, specification, calculation, computer programme or other software, manual, sample, pattern, model or other documents or information of a technical nature so provided, the same shall be forthwith modified to meet the requirements of the Supervision Consultant and shall be re-submitted whereupon the provisions of this paragraph shall apply again to such re-submission. No such approval shall release the Contractor from any of its obligations or liabilities under the Contract. The Contractor shall supply additional copies of approved drawings in the form and numbers required by the Contract.","general_condition":"The Contractor shall submit to the Project Manager for approval by the Supervision Consultant: (a) within the times given in the Specification, such drawings, specifications, calculations, computer programmes and other software, manuals, samples, patterns, models and other documents and information of a technical nature as may be called for in the Specification, and in the numbers therein required; (b) during the progress of the Works, by such dates as the Project Manager or the Supervision Consultant may require, such drawings of the general arrangement and details of the Works and such specifications, calculations, computer programmes and other software, manuals, samples, patterns, models and other documents and information of a technical nature as specified in the Contract and such further drawings and technical documents and information as the Supervision Consultant may require. The Supervision Consultant shall within such period of time after receipt of any of the aforesaid drawings, specifications, calculations, computer programmes and other software, manuals, samples, patterns, models and other documents and information of a technical nature as may be stated in the Specification (or in any case where there is no period of time so stated within the period after such receipt stated in the Particular Conditions subject to the volume of such submissions at the relevant time being reasonable, having regard to the current Programme and the Specification) either approve the same in writing (which in the case of a drawing, specification, calculation or manual shall be done by returning one copy of the same to the Contractor with such approval endorsed thereon) or notify the Contractor of his disapproval thereof or of any comments or queries which he may have thereon. If the Supervision Consultant shall disapprove any drawing, specification, calculation, computer programme or other software, manual, sample, pattern, model or other documents or information of a technical nature so provided, the same shall be forthwith modified to meet the requirements of the Supervision Consultant and shall be re-submitted whereupon the provisions of this paragraph shall apply again to such re-submission. No such approval shall release the Contractor from any of its obligations or liabilities under the Contract. The Contractor shall supply additional copies of approved drawings in the form and numbers required by the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall submit to the Project Manager for approval by the Supervision Consultant: (a) within the times given in the Specification, such drawings, specifications, calculations, computer pr...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":22}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 23, '{"itemType":"CLAUSE","clause_number":"5.2","clause_title":"Departure from Drawings and Specification","clause_text":"Save as herein otherwise provided no Drawing and no part of the Specification shall be departed from except as provided in Clause 22 (Variations).","general_condition":"Save as herein otherwise provided no Drawing and no part of the Specification shall be departed from except as provided in Clause 22 (Variations).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Save as herein otherwise provided no Drawing and no part of the Specification shall be departed from except as provided in Clause 22 (Variations).","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":23}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 24, '{"itemType":"CLAUSE","clause_number":"5.3","clause_title":"Inspection of Documents","clause_text":"One copy of the Contract, the subsequent Drawings, publications named in the Specification, Variation Orders and other communications given under the Contract shall be kept by the Contractor on an appropriate part of the Site and on its premises and the same shall at all reasonable times be available for inspection and use by the Supervision Consultant, the Supervision Consultant''s Representative, the Project Manager, the Project Manager''s Representative, the Employer and by any other person authorised by the Project Manager in writing.","general_condition":"One copy of the Contract, the subsequent Drawings, publications named in the Specification, Variation Orders and other communications given under the Contract shall be kept by the Contractor on an appropriate part of the Site and on its premises and the same shall at all reasonable times be available for inspection and use by the Supervision Consultant, the Supervision Consultant''s Representative, the Project Manager, the Project Manager''s Representative, the Employer and by any other person authorised by the Project Manager in writing.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"One copy of the Contract, the subsequent Drawings, publications named in the Specification, Variation Orders and other communications given under the Contract shall be kept by the Contractor on an app...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":24}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 25, '{"itemType":"CLAUSE","clause_number":"5.4","clause_title":"Further Drawings and Specification Modifications","clause_text":"Subject to provisos (a) and (b) in Clause 2.1 (Project Manager''s Powers and Duties), the Project Manager shall have full power and authority to issue to the Contractor from time to time such further Drawings, such modifications and additions to and such deletions from the Specification and such instructions as shall be necessary for the purpose of the proper and adequate carrying out of the Works and the remedying of defects therein and the Contractor shall comply therewith.","general_condition":"Subject to provisos (a) and (b) in Clause 2.1 (Project Manager''s Powers and Duties), the Project Manager shall have full power and authority to issue to the Contractor from time to time such further Drawings, such modifications and additions to and such deletions from the Specification and such instructions as shall be necessary for the purpose of the proper and adequate carrying out of the Works and the remedying of defects therein and the Contractor shall comply therewith.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Subject to provisos (a) and (b) in Clause 2.1 (Project Manager''s Powers and Duties), the Project Manager shall have full power and authority to issue to the Contractor from time to time such further D...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":25}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 26, '{"itemType":"CLAUSE","clause_number":"5.5","clause_title":"Disruption of Progress","clause_text":"The Contractor shall give written notice to the Project Manager and a copy thereof to the Supervision Consultant whenever planning or progress of the Works, in its opinion, is likely to be delayed or disrupted unless any Drawing, instruction or approval is issued by the Supervision Consultant or the Project Manager (as applicable) within a reasonable time. The notice shall include details of the Drawing, instruction or approval required and of why and by when it is required and details of the nature and amount of the delay or disruption which in its opinion is likely to be suffered if it is late.","general_condition":"The Contractor shall give written notice to the Project Manager and a copy thereof to the Supervision Consultant whenever planning or progress of the Works, in its opinion, is likely to be delayed or disrupted unless any Drawing, instruction or approval is issued by the Supervision Consultant or the Project Manager (as applicable) within a reasonable time. The notice shall include details of the Drawing, instruction or approval required and of why and by when it is required and details of the nature and amount of the delay or disruption which in its opinion is likely to be suffered if it is late.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall give written notice to the Project Manager and a copy thereof to the Supervision Consultant whenever planning or progress of the Works, in its opinion, is likely to be delayed or ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":26}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 27, '{"itemType":"CLAUSE","clause_number":"5.6","clause_title":"Errors in Drawings","clause_text":"(a) The Contractor shall be responsible for and fully and effectually indemnify the Employer against any Costs arising from any delay in providing Contractor''s Drawings or other information or from any error, discrepancy or omission in the Contractor''s Drawings or other information whether or not approved by the Supervision Consultant or the Project Manager (as applicable). (b) The Contractor shall at its own Cost carry out any alterations or remedial work necessary to correct any error, discrepancy or omission for which it is directly or indirectly responsible as aforesaid and modify the Contractor''s Drawings and such other information accordingly. Such carrying out and performance of the indemnity hereinbefore contained shall not relieve the Contractor of any other liability it may have under the Contract for delay in completion.","general_condition":"(a) The Contractor shall be responsible for and fully and effectually indemnify the Employer against any Costs arising from any delay in providing Contractor''s Drawings or other information or from any error, discrepancy or omission in the Contractor''s Drawings or other information whether or not approved by the Supervision Consultant or the Project Manager (as applicable). (b) The Contractor shall at its own Cost carry out any alterations or remedial work necessary to correct any error, discrepancy or omission for which it is directly or indirectly responsible as aforesaid and modify the Contractor''s Drawings and such other information accordingly. Such carrying out and performance of the indemnity hereinbefore contained shall not relieve the Contractor of any other liability it may have under the Contract for delay in completion.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) The Contractor shall be responsible for and fully and effectually indemnify the Employer against any Costs arising from any delay in providing Contractor''s Drawings or other information or from an...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":27}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 28, '{"itemType":"CLAUSE","clause_number":"5.7","clause_title":"As-built Drawings, Operations and Maintenance Manuals","clause_text":"(a) It shall be a condition precedent to the issue of the Taking-Over Certificate under Clause 20.1 (Taking-Over Certificate) for any Section or Portion of the Works that the Contractor shall have prepared and delivered to the Project Manager for approval by the Supervision Consultant: (i) all Drawings of that Section or Portion of the Works marked up to show the as-built status as required by the Specification; and (ii) comprehensive drafts of all operations and maintenance manuals (including without limitation charts and spare parts information) relating to that Section or Portion of the Works as required by the Specification. Such marked up Drawings and draft operations and maintenance manuals shall include but shall not be limited to details sufficient to enable the Employer to operate, maintain, dismantle, reassemble, adjust and repair all parts of the relevant Section or Portion of the Works. (b) It shall be a condition precedent to the issue of the Final Certificate under Clause 26.4 (Issue of Final Certificates) for any Section or Portion of the Works that the Contractor shall have prepared and delivered to the Project Manager for approval by the Supervision Consultant all as-built drawings and operations and maintenance manuals relating to that Section or Portion of the Works as required by the Specification and such as-built drawings and operations and maintenance manuals have been approved by the Supervision Consultant.","general_condition":"(a) It shall be a condition precedent to the issue of the Taking-Over Certificate under Clause 20.1 (Taking-Over Certificate) for any Section or Portion of the Works that the Contractor shall have prepared and delivered to the Project Manager for approval by the Supervision Consultant: (i) all Drawings of that Section or Portion of the Works marked up to show the as-built status as required by the Specification; and (ii) comprehensive drafts of all operations and maintenance manuals (including without limitation charts and spare parts information) relating to that Section or Portion of the Works as required by the Specification. Such marked up Drawings and draft operations and maintenance manuals shall include but shall not be limited to details sufficient to enable the Employer to operate, maintain, dismantle, reassemble, adjust and repair all parts of the relevant Section or Portion of the Works. (b) It shall be a condition precedent to the issue of the Final Certificate under Clause 26.4 (Issue of Final Certificates) for any Section or Portion of the Works that the Contractor shall have prepared and delivered to the Project Manager for approval by the Supervision Consultant all as-built drawings and operations and maintenance manuals relating to that Section or Portion of the Works as required by the Specification and such as-built drawings and operations and maintenance manuals have been approved by the Supervision Consultant.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) It shall be a condition precedent to the issue of the Taking-Over Certificate under Clause 20.1 (Taking-Over Certificate) for any Section or Portion of the Works that the Contractor shall have pre...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":28}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 29, '{"itemType":"CLAUSE","clause_number":"6.1","clause_title":"General Obligations","clause_text":"(a) The Contractor shall carry out and complete the Works within the applicable Time for Completion and remedy defects therein all in accordance with the Contract and to the satisfaction of the Supervision Consultant and the Project Manager. Those parts of the Works (if any) as completed by the Contractor and for which the Contractor has the responsibility for the design shall be fit for the purposes for which they are intended. The Contractor shall provide all required information, superintendence, labour, Plant and Contractor''s Equipment specified in or reasonably to be inferred from the Contract. The Contractor shall give prompt notice to the Project Manager (with a copy to the Supervision Consultant) of any error, discrepancy or omission in the design of the Works or in the technical content of any document prepared for use in carrying out the Works which he discovers during the currency of the Contract irrespective of whether such design or document was supplied or specified by the Employer, the Project Manager, the Supervision Consultant or the Contractor. (b) Design by the Contractor shall be carried out by qualified employees who are architects, engineers or other professionals and who comply with the criteria (if any) stated in the Contract. Unless otherwise stated in the Contract, the Contractor shall submit to the Project Manager for approval the names and particulars of each proposed employee and design Subcontractor. Nothing contained in the Contract shall create any contractual relationship or professional obligations between any such employee, or a design Subcontractor, and the Employer save for any warranties given by such Subcontractors to the Employer. The Contractor warrants that itself, its employees and design Subcontractors have the experience and capability necessary for the design. The Contractor undertakes that the employees and design Subcontractors shall be available to attend discussions with the Supervision Consultant and the Project Manager both on and off the Site as required by the Supervision Consultant or the Project Manager.","general_condition":"(a) The Contractor shall carry out and complete the Works within the applicable Time for Completion and remedy defects therein all in accordance with the Contract and to the satisfaction of the Supervision Consultant and the Project Manager. Those parts of the Works (if any) as completed by the Contractor and for which the Contractor has the responsibility for the design shall be fit for the purposes for which they are intended. The Contractor shall provide all required information, superintendence, labour, Plant and Contractor''s Equipment specified in or reasonably to be inferred from the Contract. The Contractor shall give prompt notice to the Project Manager (with a copy to the Supervision Consultant) of any error, discrepancy or omission in the design of the Works or in the technical content of any document prepared for use in carrying out the Works which he discovers during the currency of the Contract irrespective of whether such design or document was supplied or specified by the Employer, the Project Manager, the Supervision Consultant or the Contractor. (b) Design by the Contractor shall be carried out by qualified employees who are architects, engineers or other professionals and who comply with the criteria (if any) stated in the Contract. Unless otherwise stated in the Contract, the Contractor shall submit to the Project Manager for approval the names and particulars of each proposed employee and design Subcontractor. Nothing contained in the Contract shall create any contractual relationship or professional obligations between any such employee, or a design Subcontractor, and the Employer save for any warranties given by such Subcontractors to the Employer. The Contractor warrants that itself, its employees and design Subcontractors have the experience and capability necessary for the design. The Contractor undertakes that the employees and design Subcontractors shall be available to attend discussions with the Supervision Consultant and the Project Manager both on and off the Site as required by the Supervision Consultant or the Project Manager.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) The Contractor shall carry out and complete the Works within the applicable Time for Completion and remedy defects therein all in accordance with the Contract and to the satisfaction of the Superv...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":29}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 30, '{"itemType":"CLAUSE","clause_number":"6.2","clause_title":"Performance","clause_text":"The Contractor warrants to the Employer that the Contractor will carry out and complete the Works and remedy defects therein and perform all its other obligations under the Contract strictly in accordance with the terms, conditions and provisions of the Contract.","general_condition":"The Contractor warrants to the Employer that the Contractor will carry out and complete the Works and remedy defects therein and perform all its other obligations under the Contract strictly in accordance with the terms, conditions and provisions of the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor warrants to the Employer that the Contractor will carry out and complete the Works and remedy defects therein and perform all its other obligations under the Contract strictly in accord...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":30}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 31, '{"itemType":"CLAUSE","clause_number":"6.3","clause_title":"Operations and Methods of Execution","clause_text":"(a) The Contractor shall comply with all applicable rules, regulations and procedures relating to safety matters in its access arrangements and Site operations. (b) Without prejudice to any other obligations under the Contract the Contractor shall take full responsibility for the supervision, adequacy, stability and safety of all Site operations, of all methods of execution and of all the Works, irrespective of any approval or consent by the Supervision Consultant or the Project Manager. (c) When working on the Site, any area for the time being occupied by the Employer or any area designated by the Project Manager the Contractor shall strictly observe and perform each and every provision of the Employer''s and the Project Manager''s rules, regulations and procedures relating to safety matters for the time being in force. (d) The Contractor shall take all necessary steps to protect the environment (both on and off the Site) and to avoid damage and nuisance to people and property resulting from pollution, noise and other results of its operations. This shall include the Contractor providing temporary roadways, footways, guards and fences which may be rendered necessary, by reason of the carrying out of the Works, for the use and protection of the public and of owners and occupiers of adjacent land. The Contractor shall ensure that emissions, surface discharges and effluent from the Contractor''s activities shall not exceed the values indicated in the Contract and shall not exceed the values prescribed by the applicable laws. (ii) the access to and use and occupation of all roads and footpaths, irrespective of whether they are public or in the possession of the Employer or of others. The Contractor shall indemnify and hold the Employer harmless against and from all damages, losses and expenses (including legal fees and expenses) resulting from any such interference.","general_condition":"(a) The Contractor shall comply with all applicable rules, regulations and procedures relating to safety matters in its access arrangements and Site operations. (b) Without prejudice to any other obligations under the Contract the Contractor shall take full responsibility for the supervision, adequacy, stability and safety of all Site operations, of all methods of execution and of all the Works, irrespective of any approval or consent by the Supervision Consultant or the Project Manager. (c) When working on the Site, any area for the time being occupied by the Employer or any area designated by the Project Manager the Contractor shall strictly observe and perform each and every provision of the Employer''s and the Project Manager''s rules, regulations and procedures relating to safety matters for the time being in force. (d) The Contractor shall take all necessary steps to protect the environment (both on and off the Site) and to avoid damage and nuisance to people and property resulting from pollution, noise and other results of its operations. This shall include the Contractor providing temporary roadways, footways, guards and fences which may be rendered necessary, by reason of the carrying out of the Works, for the use and protection of the public and of owners and occupiers of adjacent land. The Contractor shall ensure that emissions, surface discharges and effluent from the Contractor''s activities shall not exceed the values indicated in the Contract and shall not exceed the values prescribed by the applicable laws. (ii) the access to and use and occupation of all roads and footpaths, irrespective of whether they are public or in the possession of the Employer or of others. The Contractor shall indemnify and hold the Employer harmless against and from all damages, losses and expenses (including legal fees and expenses) resulting from any such interference.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) The Contractor shall comply with all applicable rules, regulations and procedures relating to safety matters in its access arrangements and Site operations. (b) Without prejudice to any other obli...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":31}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 32, '{"itemType":"CLAUSE","clause_number":"6.4","clause_title":"Work to Satisfaction of Supervision Consultant","clause_text":"The Contractor shall carry out and complete the Works and remedy defects therein in strict accordance with the Contract to the satisfaction of the Supervision Consultant and, subject to provisos (a) and (b) in Clause 2.1 (Project Manager''s Powers and Duties), shall comply with and adhere strictly to the Project Manager''s instructions, decisions and orders on any matter, whether mentioned in the Contract or not, touching or concerning the Works.","general_condition":"The Contractor shall carry out and complete the Works and remedy defects therein in strict accordance with the Contract to the satisfaction of the Supervision Consultant and, subject to provisos (a) and (b) in Clause 2.1 (Project Manager''s Powers and Duties), shall comply with and adhere strictly to the Project Manager''s instructions, decisions and orders on any matter, whether mentioned in the Contract or not, touching or concerning the Works.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall carry out and complete the Works and remedy defects therein in strict accordance with the Contract to the satisfaction of the Supervision Consultant and, subject to provisos (a) a...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":32}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 33, '{"itemType":"CLAUSE","clause_number":"6.5","clause_title":"Sufficiency and Investigation","clause_text":"Regardless of any information provided by the Employer, the Project Manager or the Supervision Consultant, the Contractor shall be deemed by its own means and at its own responsibility to have satisfied itself as to the correctness and sufficiency of and all conditions and circumstances affecting the Contract Sum and the rates and prices stated in the Bills of Quantities, as to the Site and its surroundings, as to climatic conditions, as to the extent and nature of the Works, as to the superintendence, the labour, the Plant and Contractor''s Equipment and all other things, whether of a temporary or permanent nature, required in and for the carrying out and completion of the Works and the remedying of defects therein, as to the applicable laws, statutes, ordinances and other laws, other provisions having the force of law, bye-laws and regulations, as to the Time for Completion for the whole of the Works and for each Section of the Works, as to the milestone dates (if any) stated in the Particular Conditions and generally as to all other circumstances, risks and contingencies which may be relevant to the performance and observance of all the Contractor''s obligations under the Contract (including those under Provisional Sums, if any) and to the payments to be made to the Contractor in respect thereof.","general_condition":"Regardless of any information provided by the Employer, the Project Manager or the Supervision Consultant, the Contractor shall be deemed by its own means and at its own responsibility to have satisfied itself as to the correctness and sufficiency of and all conditions and circumstances affecting the Contract Sum and the rates and prices stated in the Bills of Quantities, as to the Site and its surroundings, as to climatic conditions, as to the extent and nature of the Works, as to the superintendence, the labour, the Plant and Contractor''s Equipment and all other things, whether of a temporary or permanent nature, required in and for the carrying out and completion of the Works and the remedying of defects therein, as to the applicable laws, statutes, ordinances and other laws, other provisions having the force of law, bye-laws and regulations, as to the Time for Completion for the whole of the Works and for each Section of the Works, as to the milestone dates (if any) stated in the Particular Conditions and generally as to all other circumstances, risks and contingencies which may be relevant to the performance and observance of all the Contractor''s obligations under the Contract (including those under Provisional Sums, if any) and to the payments to be made to the Contractor in respect thereof.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Regardless of any information provided by the Employer, the Project Manager or the Supervision Consultant, the Contractor shall be deemed by its own means and at its own responsibility to have satisfi...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":33}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 34, '{"itemType":"CLAUSE","clause_number":"6.6","clause_title":"Cash Flow Estimate","clause_text":"The Contractor shall within the period after the Commencement Date stated in the Particular Conditions provide to the Project Manager for his information a detailed cash flow estimate, in monthly periods, of all payments to which the Contractor will be entitled under the Contract and the Contractor shall subsequently supply at the end of each month revised cash flow estimates in monthly periods. The cash flow estimates shall be in such form and in such detail as the Project Manager shall require. The provision of such estimates shall not relieve the Contractor of any of its duties or responsibilities under the Contract.","general_condition":"The Contractor shall within the period after the Commencement Date stated in the Particular Conditions provide to the Project Manager for his information a detailed cash flow estimate, in monthly periods, of all payments to which the Contractor will be entitled under the Contract and the Contractor shall subsequently supply at the end of each month revised cash flow estimates in monthly periods. The cash flow estimates shall be in such form and in such detail as the Project Manager shall require. The provision of such estimates shall not relieve the Contractor of any of its duties or responsibilities under the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall within the period after the Commencement Date stated in the Particular Conditions provide to the Project Manager for his information a detailed cash flow estimate, in monthly peri...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":34}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 35, '{"itemType":"CLAUSE","clause_number":"6.7","clause_title":"Confidential Details","clause_text":"The Contractor shall disclose all such confidential and other information as the Project Manager or the Employer may require to verify the Contractor''s compliance with the Contract.","general_condition":"The Contractor shall disclose all such confidential and other information as the Project Manager or the Employer may require to verify the Contractor''s compliance with the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall disclose all such confidential and other information as the Project Manager or the Employer may require to verify the Contractor''s compliance with the Contract.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":35}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 36, '{"itemType":"CLAUSE","clause_number":"6.8","clause_title":"Quality Assurance","clause_text":"The Contractor shall implement a quality assurance system to demonstrate compliance with the requirements of the Contract. The system shall be in accordance with the details (if any) stated in the Contract. The Project Manager and the Supervision Consultant shall be entitled to audit any aspect of the system. Details of all procedures and compliance documents shall be submitted to the Project Manager for information before each design and execution stage is commenced. When any document of a technical nature is issued to the Project Manager, evidence of the prior approval by the Contractor shall be apparent on such document. Compliance with the quality assurance system shall not release the Contractor from any of its obligations or liabilities under the Contract.","general_condition":"The Contractor shall implement a quality assurance system to demonstrate compliance with the requirements of the Contract. The system shall be in accordance with the details (if any) stated in the Contract. The Project Manager and the Supervision Consultant shall be entitled to audit any aspect of the system. Details of all procedures and compliance documents shall be submitted to the Project Manager for information before each design and execution stage is commenced. When any document of a technical nature is issued to the Project Manager, evidence of the prior approval by the Contractor shall be apparent on such document. Compliance with the quality assurance system shall not release the Contractor from any of its obligations or liabilities under the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall implement a quality assurance system to demonstrate compliance with the requirements of the Contract. The system shall be in accordance with the details (if any) stated in the Con...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":36}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 37, '{"itemType":"CLAUSE","clause_number":"6.9","clause_title":"Future Events or Circumstances","clause_text":"As soon as the Contractor becomes aware of such events or circumstances, the Contractor shall give written notice to the Project Manager, with a copy to the Employer, of specific probable future events or circumstances which may adversely affect the Works, involve an extra payment to the Contractor by the Employer or delay completion. The Contractor shall within the period required by the Project Manager provide details of its estimate of the anticipated effect of the future event or circumstance if requested by the Project Manager.","general_condition":"As soon as the Contractor becomes aware of such events or circumstances, the Contractor shall give written notice to the Project Manager, with a copy to the Employer, of specific probable future events or circumstances which may adversely affect the Works, involve an extra payment to the Contractor by the Employer or delay completion. The Contractor shall within the period required by the Project Manager provide details of its estimate of the anticipated effect of the future event or circumstance if requested by the Project Manager.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"As soon as the Contractor becomes aware of such events or circumstances, the Contractor shall give written notice to the Project Manager, with a copy to the Employer, of specific probable future event...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":37}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 38, '{"itemType":"CLAUSE","clause_number":"7.1","clause_title":"Provision of Bonds","clause_text":"The Contractor undertakes to obtain and deliver to the Project Manager within fourteen (14) days after the Commencement Date for approval by the Employer: (a) a Performance Bond, in the form attached to these Conditions of Contract as Appendix B (the \"Performance Bond\"), which shall be issued by a bank in the Country approved by the Employer for the purpose. Such Bond shall be in the amount stated in the Particular Conditions; and (b) an Advance Payment Bond in the form attached to these Conditions of Contract as Appendix C (the \"Advance Payment Bond\") which shall be issued by a bank in the Country approved by the Employer for the purpose. Such Bond shall be in the amount equal to the Advance Payment.","general_condition":"The Contractor undertakes to obtain and deliver to the Project Manager within fourteen (14) days after the Commencement Date for approval by the Employer: (a) a Performance Bond, in the form attached to these Conditions of Contract as Appendix B (the \"Performance Bond\"), which shall be issued by a bank in the Country approved by the Employer for the purpose. Such Bond shall be in the amount stated in the Particular Conditions; and (b) an Advance Payment Bond in the form attached to these Conditions of Contract as Appendix C (the \"Advance Payment Bond\") which shall be issued by a bank in the Country approved by the Employer for the purpose. Such Bond shall be in the amount equal to the Advance Payment.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor undertakes to obtain and deliver to the Project Manager within fourteen (14) days after the Commencement Date for approval by the Employer: (a) a Performance Bond, in the form attached ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":38}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 39, '{"itemType":"CLAUSE","clause_number":"7.2","clause_title":"Cost of Providing Bonds","clause_text":"All costs of obtaining and keeping in force the Performance Bond and the Advance Payment Bond shall be borne and paid by the Contractor.","general_condition":"All costs of obtaining and keeping in force the Performance Bond and the Advance Payment Bond shall be borne and paid by the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"All costs of obtaining and keeping in force the Performance Bond and the Advance Payment Bond shall be borne and paid by the Contractor.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":39}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 40, '{"itemType":"CLAUSE","clause_number":"7.3","clause_title":"Apportionment of Performance Bond","clause_text":"An appropriate proportion of the Performance Bond as determined from time to time by the Project Manager after reference to the Bills of Quantities and the valuation of the Works under Clause 25.2 (Works to be Measured) shall apply to each Section and Portion of the Works. Such proportion as determined by the Project Manager shall be notified in writing by the Project Manager to the Employer and the Contractor.","general_condition":"An appropriate proportion of the Performance Bond as determined from time to time by the Project Manager after reference to the Bills of Quantities and the valuation of the Works under Clause 25.2 (Works to be Measured) shall apply to each Section and Portion of the Works. Such proportion as determined by the Project Manager shall be notified in writing by the Project Manager to the Employer and the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"An appropriate proportion of the Performance Bond as determined from time to time by the Project Manager after reference to the Bills of Quantities and the valuation of the Works under Clause 25.2 (Wo...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":40}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 41, '{"itemType":"CLAUSE","clause_number":"7.4","clause_title":"Reduction of Advance Payment Bond","clause_text":"The value of the Advance Payment Bond shall be reduced by the amount of Advance Payment recovered under Clause 26.2 (Issue of Interim Certificates).","general_condition":"The value of the Advance Payment Bond shall be reduced by the amount of Advance Payment recovered under Clause 26.2 (Issue of Interim Certificates).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The value of the Advance Payment Bond shall be reduced by the amount of Advance Payment recovered under Clause 26.2 (Issue of Interim Certificates).","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":41}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 42, '{"itemType":"CLAUSE","clause_number":"8.1","clause_title":"Pre-condition and Payment","clause_text":"Subject to receipt by the Project Manager of the Performance Bond and the Advance Payment Bond and to such Bonds having been approved by the Employer in writing, the Employer shall in accordance with the terms of the Contract pay to the Contractor the Advance Payment.","general_condition":"Subject to receipt by the Project Manager of the Performance Bond and the Advance Payment Bond and to such Bonds having been approved by the Employer in writing, the Employer shall in accordance with the terms of the Contract pay to the Contractor the Advance Payment.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Subject to receipt by the Project Manager of the Performance Bond and the Advance Payment Bond and to such Bonds having been approved by the Employer in writing, the Employer shall in accordance with ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":42}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 43, '{"itemType":"CLAUSE","clause_number":"8.2","clause_title":"Employer''s Cost","clause_text":"The Employer shall be responsible for all bank charges incurred in connection with the transfer of the Advance Payment to the bank account nominated by the Contractor under Clause 27.4 (Place of Payment).","general_condition":"The Employer shall be responsible for all bank charges incurred in connection with the transfer of the Advance Payment to the bank account nominated by the Contractor under Clause 27.4 (Place of Payment).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Employer shall be responsible for all bank charges incurred in connection with the transfer of the Advance Payment to the bank account nominated by the Contractor under Clause 27.4 (Place of Payme...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":43}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 44, '{"itemType":"CLAUSE","clause_number":"9.1","clause_title":"Information and Inspection","clause_text":"Insofar as the Employer may have made available to the Contractor prior to the Contractor submitting its Tender any information concerning the Site and its surroundings or conditions applicable thereto the Employer accepts no liability for the sufficiency or accuracy of such information and the Contractor shall be responsible for its own interpretation thereof. For the avoidance or removal of doubt, the Contractor hereby accepts the entire risk as to the condition of the Site and all data produced in respect thereof and hereby agrees that it will not submit any claim nor commence any proceedings in connection therewith. The Contractor shall be deemed to have inspected and examined the Site and its surroundings and information available in connection therewith and to have satisfied itself prior to submitting its Tender as to: (a) the form and nature of the Site and its surroundings, including the sub-surface, hydrological, hydrographic and climatic conditions thereof; Accordingly, the Contract Sum and the rates and prices in the Bills of Quantities shall be deemed to take account of all such matters and the Contractor shall not be entitled to make any claim in relation thereto whether for variation, extension of time or otherwise.","general_condition":"Insofar as the Employer may have made available to the Contractor prior to the Contractor submitting its Tender any information concerning the Site and its surroundings or conditions applicable thereto the Employer accepts no liability for the sufficiency or accuracy of such information and the Contractor shall be responsible for its own interpretation thereof. For the avoidance or removal of doubt, the Contractor hereby accepts the entire risk as to the condition of the Site and all data produced in respect thereof and hereby agrees that it will not submit any claim nor commence any proceedings in connection therewith. The Contractor shall be deemed to have inspected and examined the Site and its surroundings and information available in connection therewith and to have satisfied itself prior to submitting its Tender as to: (a) the form and nature of the Site and its surroundings, including the sub-surface, hydrological, hydrographic and climatic conditions thereof; Accordingly, the Contract Sum and the rates and prices in the Bills of Quantities shall be deemed to take account of all such matters and the Contractor shall not be entitled to make any claim in relation thereto whether for variation, extension of time or otherwise.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Insofar as the Employer may have made available to the Contractor prior to the Contractor submitting its Tender any information concerning the Site and its surroundings or conditions applicable theret...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":44}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 45, '{"itemType":"CLAUSE","clause_number":"9.2","clause_title":"Possession of Site","clause_text":"The Employer shall, subsequent to the Commencement Date and in reasonable time thereafter, give to the Contractor possession of so much of the Site as may be required to enable the Contractor to proceed with the carrying out of the Works in accordance with the Programme, the requirements of the Particular Conditions and otherwise in accordance with such reasonable proposals as the Contractor may, by written notice to the Project Manager, with a copy to the Employer, make. The Employer will, from time to time as the Works proceed, give to the Contractor possession of such further portions of the Site as may be required to enable the Contractor to proceed with the carrying out of the Works in accordance with the Programme, the requirements of the Particular Conditions or such proposals as the case may be. Possession of the Site or any portion thereof shall not be exclusive to the Contractor but shall only be such as to enable the Contractor to carry out the Works.","general_condition":"The Employer shall, subsequent to the Commencement Date and in reasonable time thereafter, give to the Contractor possession of so much of the Site as may be required to enable the Contractor to proceed with the carrying out of the Works in accordance with the Programme, the requirements of the Particular Conditions and otherwise in accordance with such reasonable proposals as the Contractor may, by written notice to the Project Manager, with a copy to the Employer, make. The Employer will, from time to time as the Works proceed, give to the Contractor possession of such further portions of the Site as may be required to enable the Contractor to proceed with the carrying out of the Works in accordance with the Programme, the requirements of the Particular Conditions or such proposals as the case may be. Possession of the Site or any portion thereof shall not be exclusive to the Contractor but shall only be such as to enable the Contractor to carry out the Works.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Employer shall, subsequent to the Commencement Date and in reasonable time thereafter, give to the Contractor possession of so much of the Site as may be required to enable the Contractor to proce...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":45}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 46, '{"itemType":"CLAUSE","clause_number":"9.3","clause_title":"Entry to Site","clause_text":"The Contractor shall not at any time obstruct entry by the Employer, the Project Manager, the Supervision Consultant or their employees, agents or visitors to the Site. The Contractor shall at all times permit other contractors or suppliers (and their employees and agents) engaged by the Employer or by others to enter the Site for the purpose of carrying out work or delivering goods. The Project Manager, however, shall endeavour to make sure that the performance of the Contractor is not unreasonably impeded by such entries upon the Site. The Contractor shall not permit other persons whose presence on the Site is not necessary for the carrying out of the Works and/or the remedying of defects therein as appropriate to enter the Site without the prior consent of the Project Manager.","general_condition":"The Contractor shall not at any time obstruct entry by the Employer, the Project Manager, the Supervision Consultant or their employees, agents or visitors to the Site. The Contractor shall at all times permit other contractors or suppliers (and their employees and agents) engaged by the Employer or by others to enter the Site for the purpose of carrying out work or delivering goods. The Project Manager, however, shall endeavour to make sure that the performance of the Contractor is not unreasonably impeded by such entries upon the Site. The Contractor shall not permit other persons whose presence on the Site is not necessary for the carrying out of the Works and/or the remedying of defects therein as appropriate to enter the Site without the prior consent of the Project Manager.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall not at any time obstruct entry by the Employer, the Project Manager, the Supervision Consultant or their employees, agents or visitors to the Site. The Contractor shall at all tim...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":46}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 47, '{"itemType":"CLAUSE","clause_number":"9.4","clause_title":"Opportunities for Other Contractors","clause_text":"The Contractor shall, in accordance with the requirements of the Project Manager, afford all reasonable opportunities to other contractors (and their employees and agents) employed by the Employer or by others and to the employees and agents of the Employer, the Project Manager, the Supervision Consultant and to duly constituted authorities for carrying out their work on Site.","general_condition":"The Contractor shall, in accordance with the requirements of the Project Manager, afford all reasonable opportunities to other contractors (and their employees and agents) employed by the Employer or by others and to the employees and agents of the Employer, the Project Manager, the Supervision Consultant and to duly constituted authorities for carrying out their work on Site.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall, in accordance with the requirements of the Project Manager, afford all reasonable opportunities to other contractors (and their employees and agents) employed by the Employer or ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":47}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 48, '{"itemType":"CLAUSE","clause_number":"9.5","clause_title":"Facilities outside Site","clause_text":"The Contractor shall bear and pay all Costs and charges for special and/or temporary rights-of way which it may require, including those for access to the Site. The Contractor shall also obtain, at its risk and Cost, any additional facilities outside the Site which it may require for the purposes of the Works.","general_condition":"The Contractor shall bear and pay all Costs and charges for special and/or temporary rights-of way which it may require, including those for access to the Site. The Contractor shall also obtain, at its risk and Cost, any additional facilities outside the Site which it may require for the purposes of the Works.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall bear and pay all Costs and charges for special and/or temporary rights-of way which it may require, including those for access to the Site. The Contractor shall also obtain, at it...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":48}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 49, '{"itemType":"CLAUSE","clause_number":"9.6","clause_title":"Notices, Permits and Fees","clause_text":"Subject to any provision to the contrary contained in the Particular Conditions, the Contractor shall give all such notices, obtain and keep in force all such permits, no objection certificates, licences, consents and approvals and bear and pay all Costs and fees in relation thereto and pay all such taxes and duties as may, for the carrying out and completion of the Works, the remedying of defects therein and the performance of all the Contractor''s other obligations under the Contract, be required pursuant to any applicable laws, statutes, ordinances and other laws, other provisions having the force of law, bye-laws and regulations.","general_condition":"Subject to any provision to the contrary contained in the Particular Conditions, the Contractor shall give all such notices, obtain and keep in force all such permits, no objection certificates, licences, consents and approvals and bear and pay all Costs and fees in relation thereto and pay all such taxes and duties as may, for the carrying out and completion of the Works, the remedying of defects therein and the performance of all the Contractor''s other obligations under the Contract, be required pursuant to any applicable laws, statutes, ordinances and other laws, other provisions having the force of law, bye-laws and regulations.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Subject to any provision to the contrary contained in the Particular Conditions, the Contractor shall give all such notices, obtain and keep in force all such permits, no objection certificates, licen...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":49}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 50, '{"itemType":"CLAUSE","clause_number":"9.7","clause_title":"Setting-Out","clause_text":"The Contractor shall be responsible for the accurate setting-out of the Works in relation to original points, lines, levels of reference and bench-marks given or prescribed by the Supervision Consultant in writing and for the correctness of the positions, levels, dimensions and alignment of all parts of the Works and for the provision of all necessary instruments, appliances and labour in connection therewith. Before commencing the carrying out of the Works, the Contractor shall satisfy itself as to such original points, lines, levels of reference and bench-marks and shall give notice to the Project Manager (with a copy to the Supervision Consultant) of any error in such original points, lines, levels of reference and bench-marks. After receipt of any such notice, the Project Manager shall arrange for the Supervision Consultant to rectify such error and the Supervision Consultant shall then instruct the Contractor thereon. If, at any time during the progress of the Works, any error shall appear or arise in the positions, levels, dimensions or alignment of any part of the Works, the Contractor, on being required so to do by the Supervision Consultant, shall, at its own Cost, rectify such error to the satisfaction of the Supervision Consultant. The checking of any setting out or of any line or level by the Supervision Consultant shall not in any way relieve the Contractor of its responsibility for the accuracy and correctness thereof. The Contractor shall carefully protect and preserve all bench-marks, sight-rails, pegs and other things used in setting-out the Works.","general_condition":"The Contractor shall be responsible for the accurate setting-out of the Works in relation to original points, lines, levels of reference and bench-marks given or prescribed by the Supervision Consultant in writing and for the correctness of the positions, levels, dimensions and alignment of all parts of the Works and for the provision of all necessary instruments, appliances and labour in connection therewith. Before commencing the carrying out of the Works, the Contractor shall satisfy itself as to such original points, lines, levels of reference and bench-marks and shall give notice to the Project Manager (with a copy to the Supervision Consultant) of any error in such original points, lines, levels of reference and bench-marks. After receipt of any such notice, the Project Manager shall arrange for the Supervision Consultant to rectify such error and the Supervision Consultant shall then instruct the Contractor thereon. If, at any time during the progress of the Works, any error shall appear or arise in the positions, levels, dimensions or alignment of any part of the Works, the Contractor, on being required so to do by the Supervision Consultant, shall, at its own Cost, rectify such error to the satisfaction of the Supervision Consultant. The checking of any setting out or of any line or level by the Supervision Consultant shall not in any way relieve the Contractor of its responsibility for the accuracy and correctness thereof. The Contractor shall carefully protect and preserve all bench-marks, sight-rails, pegs and other things used in setting-out the Works.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall be responsible for the accurate setting-out of the Works in relation to original points, lines, levels of reference and bench-marks given or prescribed by the Supervision Consulta...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":50}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 51, '{"itemType":"CLAUSE","clause_number":"9.8","clause_title":"Photographs","clause_text":"The Contractor shall not (save as provided in the Specification) take or permit to be taken any photograph of the Site or the Works without the prior written consent of the Project Manager. No photograph shall be used for purposes of publicity without the prior written consent of the Employer.","general_condition":"The Contractor shall not (save as provided in the Specification) take or permit to be taken any photograph of the Site or the Works without the prior written consent of the Project Manager. No photograph shall be used for purposes of publicity without the prior written consent of the Employer.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall not (save as provided in the Specification) take or permit to be taken any photograph of the Site or the Works without the prior written consent of the Project Manager. No photogr...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":51}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 52, '{"itemType":"CLAUSE","clause_number":"9.9","clause_title":"Existing Facilities","clause_text":"The Contractor shall be responsible for any disturbance of and damage to existing facilities, permanent or temporary, which may be caused by its carrying out of the Works, remedying of defects therein or other activities under the Contract. Any repair work thereto and any temporary facility required in order to avoid such disturbance or damage shall be the Contractor''s responsibility and shall be carried out at the Contractor''s Cost.","general_condition":"The Contractor shall be responsible for any disturbance of and damage to existing facilities, permanent or temporary, which may be caused by its carrying out of the Works, remedying of defects therein or other activities under the Contract. Any repair work thereto and any temporary facility required in order to avoid such disturbance or damage shall be the Contractor''s responsibility and shall be carried out at the Contractor''s Cost.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall be responsible for any disturbance of and damage to existing facilities, permanent or temporary, which may be caused by its carrying out of the Works, remedying of defects therein...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":52}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 53, '{"itemType":"CLAUSE","clause_number":"9.10","clause_title":"Fencing, Lighting, Guarding and Watching","clause_text":"The Contractor shall be responsible at its own Cost for the proper fencing, lighting, guarding and watching of all the Works on the Site when and where necessary or required by the Project Manager or by any duly constituted authority and for the proper provision of temporary roadways, footways, guards and fences to the extent that the same may be rendered necessary by reason of the carrying out of the Works. No naked flame shall without specific permission in writing from the Project Manager, be used by the Contractor within any area for the time being occupied by the Employer, any other contractor or any duly constituted authority.","general_condition":"The Contractor shall be responsible at its own Cost for the proper fencing, lighting, guarding and watching of all the Works on the Site when and where necessary or required by the Project Manager or by any duly constituted authority and for the proper provision of temporary roadways, footways, guards and fences to the extent that the same may be rendered necessary by reason of the carrying out of the Works. No naked flame shall without specific permission in writing from the Project Manager, be used by the Contractor within any area for the time being occupied by the Employer, any other contractor or any duly constituted authority.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall be responsible at its own Cost for the proper fencing, lighting, guarding and watching of all the Works on the Site when and where necessary or required by the Project Manager or ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":53}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 54, '{"itemType":"CLAUSE","clause_number":"9.11","clause_title":"Urgent Repairs","clause_text":"If, by reason of any accident, failure or other event occurring to, in or in connection with the Works, or any part thereof, either during the carrying out of the Works or during the Defects Liability Period (or to, in or in connection with any part of the Works the subject of an Extended Warranty, during its extension beyond the Defects Liability Period), any remedial or other work or repair shall, in the opinion of the Supervision Consultant or the Project Manager, be urgently necessary for the safety of the Works and the Contractor is unable or unwilling at once to do such work or repair, the Employer may itself and/or may employ any other contractor or contractors to carry out such work or repair (at the Contractor''s risk) as the Supervision Consultant or the Project Manager may consider necessary. If the work or repair so carried out is work or repair which, in the opinion of the Project Manager, the Contractor was liable to do at its own Cost under the Contract, all Costs incurred by the Employer in so doing as determined by the Project Manager shall be recoverable from the Contractor by the Employer as a debt due from the Contractor, or may be deducted by the Employer from any monies due or which may become due to the Contractor. Provided always that the Project Manager shall, as soon after the occurrence of any such emergency as may be reasonably practicable, notify the Contractor thereof in writing.","general_condition":"If, by reason of any accident, failure or other event occurring to, in or in connection with the Works, or any part thereof, either during the carrying out of the Works or during the Defects Liability Period (or to, in or in connection with any part of the Works the subject of an Extended Warranty, during its extension beyond the Defects Liability Period), any remedial or other work or repair shall, in the opinion of the Supervision Consultant or the Project Manager, be urgently necessary for the safety of the Works and the Contractor is unable or unwilling at once to do such work or repair, the Employer may itself and/or may employ any other contractor or contractors to carry out such work or repair (at the Contractor''s risk) as the Supervision Consultant or the Project Manager may consider necessary. If the work or repair so carried out is work or repair which, in the opinion of the Project Manager, the Contractor was liable to do at its own Cost under the Contract, all Costs incurred by the Employer in so doing as determined by the Project Manager shall be recoverable from the Contractor by the Employer as a debt due from the Contractor, or may be deducted by the Employer from any monies due or which may become due to the Contractor. Provided always that the Project Manager shall, as soon after the occurrence of any such emergency as may be reasonably practicable, notify the Contractor thereof in writing.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If, by reason of any accident, failure or other event occurring to, in or in connection with the Works, or any part thereof, either during the carrying out of the Works or during the Defects Liability...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":54}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 55, '{"itemType":"CLAUSE","clause_number":"9.12","clause_title":"Electricity and Water","clause_text":"Subject to any provision to the contrary contained in the Contract, the Contractor shall provide at its own Cost supplies of electricity, water (including potable water), gas, compressed air and other services required for the carrying out of the Works.","general_condition":"Subject to any provision to the contrary contained in the Contract, the Contractor shall provide at its own Cost supplies of electricity, water (including potable water), gas, compressed air and other services required for the carrying out of the Works.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Subject to any provision to the contrary contained in the Contract, the Contractor shall provide at its own Cost supplies of electricity, water (including potable water), gas, compressed air and other...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":55}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 56, '{"itemType":"CLAUSE","clause_number":"9.13","clause_title":"Fossils","clause_text":"All fossils, coins, articles of value or antiquity and structures and other remains or things of geological or archaeological interest discovered on the Site shall, as between the Employer and the Contractor, be deemed to be the absolute property of the Employer. The Contractor shall take reasonable precautions to prevent its workmen or any other persons from removing or damaging any such article or thing and shall immediately upon discovery thereof and, before removal, acquaint the Project Manager of such discovery and carry out the Project Manager''s instructions for dealing with the same. If by reason of such instructions, the Contractor incurs additional Costs the Project Manager shall determine such Costs which shall be paid to the Contractor in accordance with Clause 26 (Interim and Final Certificates).","general_condition":"All fossils, coins, articles of value or antiquity and structures and other remains or things of geological or archaeological interest discovered on the Site shall, as between the Employer and the Contractor, be deemed to be the absolute property of the Employer. The Contractor shall take reasonable precautions to prevent its workmen or any other persons from removing or damaging any such article or thing and shall immediately upon discovery thereof and, before removal, acquaint the Project Manager of such discovery and carry out the Project Manager''s instructions for dealing with the same. If by reason of such instructions, the Contractor incurs additional Costs the Project Manager shall determine such Costs which shall be paid to the Contractor in accordance with Clause 26 (Interim and Final Certificates).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"All fossils, coins, articles of value or antiquity and structures and other remains or things of geological or archaeological interest discovered on the Site shall, as between the Employer and the Con...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":56}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 57, '{"itemType":"CLAUSE","clause_number":"9.14","clause_title":"Site Clearance - General","clause_text":"The Contractor shall at all times keep the Site and the Works in a neat, clean and safe condition to the reasonable satisfaction of the Project Manager and shall from time to time clear away and remove from the Site all rubbish, wreckage, surplus Plant and any Contractor''s Equipment no longer required for the carrying out of the Works and the remedying of defects therein. If the Contractor shall fail to comply with these obligations within forty eight (48) hours after written notice from the Project Manager specifying the failure then (without prejudice to any other remedy) the Employer may itself and/or may employ any other contractor or contractors to take such action (at the Contractor''s risk) in regard to such obligations as the Project Manager considers necessary. The Costs incurred by the Employer in regard to such action shall be determined by the Project Manager and the Employer may deduct the Costs incurred from any monies due or which may become due to the Contractor or recover the same as a debt due from the Contractor.","general_condition":"The Contractor shall at all times keep the Site and the Works in a neat, clean and safe condition to the reasonable satisfaction of the Project Manager and shall from time to time clear away and remove from the Site all rubbish, wreckage, surplus Plant and any Contractor''s Equipment no longer required for the carrying out of the Works and the remedying of defects therein. If the Contractor shall fail to comply with these obligations within forty eight (48) hours after written notice from the Project Manager specifying the failure then (without prejudice to any other remedy) the Employer may itself and/or may employ any other contractor or contractors to take such action (at the Contractor''s risk) in regard to such obligations as the Project Manager considers necessary. The Costs incurred by the Employer in regard to such action shall be determined by the Project Manager and the Employer may deduct the Costs incurred from any monies due or which may become due to the Contractor or recover the same as a debt due from the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall at all times keep the Site and the Works in a neat, clean and safe condition to the reasonable satisfaction of the Project Manager and shall from time to time clear away and remov...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":57}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 58, '{"itemType":"CLAUSE","clause_number":"9.15","clause_title":"Removal of Plant and Contractor''s Equipment","clause_text":"All Plant and all Contractor''s Equipment shall when brought onto the Site be deemed to be intended exclusively for the carrying out of the Works and the remedying of defects therein. No Plant shall be removed from the Site without the prior written consent of the Project Manager but this shall not be refused if in the Supervision Consultant''s opinion: (a) the relevant Plant is not in accordance with the Contract and it is necessary to remove the same for the purpose of modification or replacement; or (b) the relevant Plant is surplus to the requirements of the Works. The Contractor shall not remove any Contractor''s Equipment from the Site without the prior written consent of the Project Manager but this shall not be refused if in the Project Manager''s opinion the relevant item is no longer required for the carrying out of the Works and the remedying of defects therein.","general_condition":"All Plant and all Contractor''s Equipment shall when brought onto the Site be deemed to be intended exclusively for the carrying out of the Works and the remedying of defects therein. No Plant shall be removed from the Site without the prior written consent of the Project Manager but this shall not be refused if in the Supervision Consultant''s opinion: (a) the relevant Plant is not in accordance with the Contract and it is necessary to remove the same for the purpose of modification or replacement; or (b) the relevant Plant is surplus to the requirements of the Works. The Contractor shall not remove any Contractor''s Equipment from the Site without the prior written consent of the Project Manager but this shall not be refused if in the Project Manager''s opinion the relevant item is no longer required for the carrying out of the Works and the remedying of defects therein.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"All Plant and all Contractor''s Equipment shall when brought onto the Site be deemed to be intended exclusively for the carrying out of the Works and the remedying of defects therein. No Plant shall be...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":58}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 59, '{"itemType":"CLAUSE","clause_number":"9.16","clause_title":"Clearance on Taking Over","clause_text":"Prior to and as a condition precedent to the issue by the Supervision Consultant of the Taking-Over Certificate for any Section or Portion of the Works in accordance with Clause 20.1 (Taking-Over Certificate) the Contractor shall to the satisfaction of the Project Manager: (a) clear away and remove from that part of the Site and the Works to which such Taking-Over Certificate relates all rubbish and wreckage and all Contractor''s Equipment, surplus Plant and surplus Free Issue Items other than such as may be necessary for the proper performance of the Contractor''s obligations to remedy defects in and otherwise in relation to the applicable Section or Portion of the Works; and (b) leave such part of the Site and the Works in a clean and safe condition.","general_condition":"Prior to and as a condition precedent to the issue by the Supervision Consultant of the Taking-Over Certificate for any Section or Portion of the Works in accordance with Clause 20.1 (Taking-Over Certificate) the Contractor shall to the satisfaction of the Project Manager: (a) clear away and remove from that part of the Site and the Works to which such Taking-Over Certificate relates all rubbish and wreckage and all Contractor''s Equipment, surplus Plant and surplus Free Issue Items other than such as may be necessary for the proper performance of the Contractor''s obligations to remedy defects in and otherwise in relation to the applicable Section or Portion of the Works; and (b) leave such part of the Site and the Works in a clean and safe condition.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Prior to and as a condition precedent to the issue by the Supervision Consultant of the Taking-Over Certificate for any Section or Portion of the Works in accordance with Clause 20.1 (Taking-Over Cert...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":59}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 60, '{"itemType":"CLAUSE","clause_number":"9.17","clause_title":"Clearance on Final Certificate","clause_text":"Prior to and as a condition precedent to the issue by the Project Manager of the Final Certificate for any Section or Portion of the Works in accordance with Clause 26.4 (Issue of Final Certificates), the Contractor shall to the satisfaction of the Project Manager clear away and remove from that part of the Site and the Works to which such Final Certificate relates all rubbish and wreckage and all Contractor''s Equipment, surplus Plant and surplus Free Issue Items remaining thereon.","general_condition":"Prior to and as a condition precedent to the issue by the Project Manager of the Final Certificate for any Section or Portion of the Works in accordance with Clause 26.4 (Issue of Final Certificates), the Contractor shall to the satisfaction of the Project Manager clear away and remove from that part of the Site and the Works to which such Final Certificate relates all rubbish and wreckage and all Contractor''s Equipment, surplus Plant and surplus Free Issue Items remaining thereon.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Prior to and as a condition precedent to the issue by the Project Manager of the Final Certificate for any Section or Portion of the Works in accordance with Clause 26.4 (Issue of Final Certificates),...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":60}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 61, '{"itemType":"CLAUSE","clause_number":"10.1","clause_title":"Contractor''s Representative","clause_text":"Immediately following the Commencement Date, the Contractor shall appoint one of its suitably competent employees approved in writing by the Project Manager as the Contractor''s Representative and shall notify the Project Manager, the Supervision Consultant and the Employer of such appointment. The Contractor shall not revoke the appointment of the Contractor''s Representative without the prior written approval of the Project Manager. The Contractor''s Representative shall give the whole of his working time to directing the Contractor''s performance of the Contract. The Contractor''s Representative shall have sufficient authority to act on behalf of and to bind the Contractor in all respects in connection with the Contract and the Works and to receive on the Contractor''s behalf decisions, orders and instructions from the Project Manager, the Supervision Consultant and the Employer. The Project Manager''s approval of the Contractor''s Representative may at any time be withdrawn and in such event the Contractor shall as soon as practicable remove the said Representative from the Works, shall not thereafter employ him again on the Works in any capacity and shall replace him by a suitably competent employee approved in writing by the Project Manager. The Contractor shall ensure that a Contractor''s Representative remains in office and duly approved by the Project Manager at all times until the issue of the last Final Certificate for any Section or Portion of the Works under Clause 26.4 (Issue of Final Certificates). If the Contractor''s Representative is to be temporarily absent from the Works, the Contractor shall appoint another of its suitably competent employees approved in writing by the Project Manager to act as the Contractor''s Representative during such temporary absence and shall notify the Project Manager, the Supervision Consultant and the Employer of such appointment.","general_condition":"Immediately following the Commencement Date, the Contractor shall appoint one of its suitably competent employees approved in writing by the Project Manager as the Contractor''s Representative and shall notify the Project Manager, the Supervision Consultant and the Employer of such appointment. The Contractor shall not revoke the appointment of the Contractor''s Representative without the prior written approval of the Project Manager. The Contractor''s Representative shall give the whole of his working time to directing the Contractor''s performance of the Contract. The Contractor''s Representative shall have sufficient authority to act on behalf of and to bind the Contractor in all respects in connection with the Contract and the Works and to receive on the Contractor''s behalf decisions, orders and instructions from the Project Manager, the Supervision Consultant and the Employer. The Project Manager''s approval of the Contractor''s Representative may at any time be withdrawn and in such event the Contractor shall as soon as practicable remove the said Representative from the Works, shall not thereafter employ him again on the Works in any capacity and shall replace him by a suitably competent employee approved in writing by the Project Manager. The Contractor shall ensure that a Contractor''s Representative remains in office and duly approved by the Project Manager at all times until the issue of the last Final Certificate for any Section or Portion of the Works under Clause 26.4 (Issue of Final Certificates). If the Contractor''s Representative is to be temporarily absent from the Works, the Contractor shall appoint another of its suitably competent employees approved in writing by the Project Manager to act as the Contractor''s Representative during such temporary absence and shall notify the Project Manager, the Supervision Consultant and the Employer of such appointment.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Immediately following the Commencement Date, the Contractor shall appoint one of its suitably competent employees approved in writing by the Project Manager as the Contractor''s Representative and shal...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":61}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 62, '{"itemType":"CLAUSE","clause_number":"10.2","clause_title":"Health and Safety","clause_text":"The Contractor shall at all times take all necessary precautions to maintain the health and safety of the Contractor''s employees. The Contractor shall appoint a full time Safety Officer at the Site, responsible on behalf of the Contractor for maintaining the safety, and protection against accidents, of all persons on the Site and its surroundings. The Safety Officer shall be qualified for this responsibility, shall report directly to the Contractor''s Representative and shall have the authority to issue instructions and to take protective measures to prevent accidents. Throughout the carrying out of the Works, the Contractor shall provide whatever is required by the Safety Officer to exercise this responsibility and authority. The Contractor shall send to the Project Manager, with a copy to the Employer, details of any accident as soon as practicable after its occurrence. In the case of any fatality or serious accident, the Contractor shall, in addition, notify the Project Manager and the Employer immediately by the quickest available means. The Contractor shall maintain such records and make such reports concerning health, safety and welfare of persons, and damage to property, as the Project Manager or the Employer may from time to time require. In the event of any outbreak of illness of an epidemic nature, the Contractor shall comply with and carry out such regulations, orders and requirements as may be made by the Government, or the local medical or sanitary authorities for the purpose of dealing with and overcoming the same.","general_condition":"The Contractor shall at all times take all necessary precautions to maintain the health and safety of the Contractor''s employees. The Contractor shall appoint a full time Safety Officer at the Site, responsible on behalf of the Contractor for maintaining the safety, and protection against accidents, of all persons on the Site and its surroundings. The Safety Officer shall be qualified for this responsibility, shall report directly to the Contractor''s Representative and shall have the authority to issue instructions and to take protective measures to prevent accidents. Throughout the carrying out of the Works, the Contractor shall provide whatever is required by the Safety Officer to exercise this responsibility and authority. The Contractor shall send to the Project Manager, with a copy to the Employer, details of any accident as soon as practicable after its occurrence. In the case of any fatality or serious accident, the Contractor shall, in addition, notify the Project Manager and the Employer immediately by the quickest available means. The Contractor shall maintain such records and make such reports concerning health, safety and welfare of persons, and damage to property, as the Project Manager or the Employer may from time to time require. In the event of any outbreak of illness of an epidemic nature, the Contractor shall comply with and carry out such regulations, orders and requirements as may be made by the Government, or the local medical or sanitary authorities for the purpose of dealing with and overcoming the same.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall at all times take all necessary precautions to maintain the health and safety of the Contractor''s employees. The Contractor shall appoint a full time Safety Officer at the Site, r...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":62}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 63, '{"itemType":"CLAUSE","clause_number":"10.3","clause_title":"Employees","clause_text":"The Contractor shall provide and employ (or cause to be employed) in connection with the carrying out of the Works and the remedying of defects therein all such qualified and experienced superintending and technical staff and all such skilled, semi-skilled and unskilled labour as may be necessary for the performance and discharge by the Contractor of all its obligations under the Contract with skill, care and diligence and within the required time all as provided herein. The Contractor shall not recruit, or attempt to recruit, his staff and labour from amongst persons in the service of the Employer, the Project Manager or the Supervision Consultant. The Employer and the Project Manager shall individually be at liberty to object to and require the Contractor to remove (or cause to be removed) forthwith from the Site any person employed by the Contractor in or about the carrying out of the Works or the remedying of defects therein who, in the opinion of the Employer or the Project Manager, misconducts himself, or is incompetent or negligent in the proper performance of his duties, or fails to conform with any provisions of the Contract, or conducts himself in a manner which is prejudicial to safety, health or the protection of the environment, or whose employment is otherwise considered by the Employer or the Project Manager to be undesirable; and such person shall not be again employed upon the Works without the prior written permission of the Employer and the Project Manager. If appropriate, the Contractor shall then provide and employ (or cause to be employed) a suitable replacement person. Such removal and subsequent replacement shall be at the Contractor''s Cost and no extension of time shall be granted to the Contractor for any delay which has been or may be caused by such removal and any such replacement. The Contractor shall comply with all relevant labour laws applicable to its employees including without limitation laws relating to their employment, health, safety, welfare, immigration and emigration and shall allow them all their legal rights. The Contractor shall require its employees to comply with all applicable laws, statues, ordinances and other laws, other provisions having the force of law, bye-laws and regulations, including those concerning safety at work.","general_condition":"The Contractor shall provide and employ (or cause to be employed) in connection with the carrying out of the Works and the remedying of defects therein all such qualified and experienced superintending and technical staff and all such skilled, semi-skilled and unskilled labour as may be necessary for the performance and discharge by the Contractor of all its obligations under the Contract with skill, care and diligence and within the required time all as provided herein. The Contractor shall not recruit, or attempt to recruit, his staff and labour from amongst persons in the service of the Employer, the Project Manager or the Supervision Consultant. The Employer and the Project Manager shall individually be at liberty to object to and require the Contractor to remove (or cause to be removed) forthwith from the Site any person employed by the Contractor in or about the carrying out of the Works or the remedying of defects therein who, in the opinion of the Employer or the Project Manager, misconducts himself, or is incompetent or negligent in the proper performance of his duties, or fails to conform with any provisions of the Contract, or conducts himself in a manner which is prejudicial to safety, health or the protection of the environment, or whose employment is otherwise considered by the Employer or the Project Manager to be undesirable; and such person shall not be again employed upon the Works without the prior written permission of the Employer and the Project Manager. If appropriate, the Contractor shall then provide and employ (or cause to be employed) a suitable replacement person. Such removal and subsequent replacement shall be at the Contractor''s Cost and no extension of time shall be granted to the Contractor for any delay which has been or may be caused by such removal and any such replacement. The Contractor shall comply with all relevant labour laws applicable to its employees including without limitation laws relating to their employment, health, safety, welfare, immigration and emigration and shall allow them all their legal rights. The Contractor shall require its employees to comply with all applicable laws, statues, ordinances and other laws, other provisions having the force of law, bye-laws and regulations, including those concerning safety at work.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall provide and employ (or cause to be employed) in connection with the carrying out of the Works and the remedying of defects therein all such qualified and experienced superintendin...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":63}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 64, '{"itemType":"CLAUSE","clause_number":"10.4","clause_title":"Payment and Services","clause_text":"(a) The Contractor shall be fully and exclusively liable for the payment of all salaries, wages and other remuneration and benefits of every kind which may become due to or payable in respect of all persons employed by the Contractor in connection with the carrying out and completion of the Works and the remedying of defects therein, and for the provision to such persons of all housing, transportation, feeding and support services. In the event of any failure by the Contractor to carry out in full its obligations under this Sub-Clause 10.4(a), the Employer may (without prejudice to any other rights) itself make such payment or provision as aforesaid and deduct the Cost thereof as determined by the Project Manager from any monies due or which may become due to the Contractor or recover the same as a debt due from the Contractor. (b) The Contractor shall pay rates of wages, and observe conditions of labour, which are not lower than those established for the trade or industry where the work is carried out. If no established rates or conditions are applicable, the Contractor shall pay rates of wages and observe conditions which are not lower than the general level of wages and conditions observed locally by employers whose trade or industry is similar to that of the Contractor.","general_condition":"(a) The Contractor shall be fully and exclusively liable for the payment of all salaries, wages and other remuneration and benefits of every kind which may become due to or payable in respect of all persons employed by the Contractor in connection with the carrying out and completion of the Works and the remedying of defects therein, and for the provision to such persons of all housing, transportation, feeding and support services. In the event of any failure by the Contractor to carry out in full its obligations under this Sub-Clause 10.4(a), the Employer may (without prejudice to any other rights) itself make such payment or provision as aforesaid and deduct the Cost thereof as determined by the Project Manager from any monies due or which may become due to the Contractor or recover the same as a debt due from the Contractor. (b) The Contractor shall pay rates of wages, and observe conditions of labour, which are not lower than those established for the trade or industry where the work is carried out. If no established rates or conditions are applicable, the Contractor shall pay rates of wages and observe conditions which are not lower than the general level of wages and conditions observed locally by employers whose trade or industry is similar to that of the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) The Contractor shall be fully and exclusively liable for the payment of all salaries, wages and other remuneration and benefits of every kind which may become due to or payable in respect of all p...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":64}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 65, '{"itemType":"CLAUSE","clause_number":"10.5","clause_title":"Alcoholic Liquor or Drugs","clause_text":"The Contractor shall not, otherwise than in accordance with the laws and regulations for the time being in force in the Country, import, sell, give, barter or otherwise dispose of any alcoholic liquor or drugs. The Contractor shall use all reasonable endeavours to prevent any such importation, sale, gift, barter or disposal by its employees or agents.","general_condition":"The Contractor shall not, otherwise than in accordance with the laws and regulations for the time being in force in the Country, import, sell, give, barter or otherwise dispose of any alcoholic liquor or drugs. The Contractor shall use all reasonable endeavours to prevent any such importation, sale, gift, barter or disposal by its employees or agents.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall not, otherwise than in accordance with the laws and regulations for the time being in force in the Country, import, sell, give, barter or otherwise dispose of any alcoholic liquor...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":65}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 66, '{"itemType":"CLAUSE","clause_number":"10.6","clause_title":"Arms and Ammunition","clause_text":"The Contractor shall not in respect of the Country, import, sell, give, barter or otherwise dispose of to any person or persons, any arms or ammunition of any kind. The Contractor shall use all reasonable endeavours to prevent any such importation, sale, gift, barter or disposal by its employees or agents.","general_condition":"The Contractor shall not in respect of the Country, import, sell, give, barter or otherwise dispose of to any person or persons, any arms or ammunition of any kind. The Contractor shall use all reasonable endeavours to prevent any such importation, sale, gift, barter or disposal by its employees or agents.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall not in respect of the Country, import, sell, give, barter or otherwise dispose of to any person or persons, any arms or ammunition of any kind. The Contractor shall use all reason...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":66}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 67, '{"itemType":"CLAUSE","clause_number":"10.7","clause_title":"Festivals and Religious Customs","clause_text":"The Contractor shall respect all recognised festivals, days of rest and religious or other customs in the Country.","general_condition":"The Contractor shall respect all recognised festivals, days of rest and religious or other customs in the Country.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall respect all recognised festivals, days of rest and religious or other customs in the Country.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":67}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 68, '{"itemType":"CLAUSE","clause_number":"10.8","clause_title":"Disorderly Conduct","clause_text":"The Contractor shall at all times take all reasonable precautions to prevent any unlawful, riotous or disorderly conduct by or amongst its employees and agents and to preserve peace and protection of persons and property on and near the Site.","general_condition":"The Contractor shall at all times take all reasonable precautions to prevent any unlawful, riotous or disorderly conduct by or amongst its employees and agents and to preserve peace and protection of persons and property on and near the Site.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall at all times take all reasonable precautions to prevent any unlawful, riotous or disorderly conduct by or amongst its employees and agents and to preserve peace and protection of ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":68}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 69, '{"itemType":"CLAUSE","clause_number":"10.9","clause_title":"Medical Fitness","clause_text":"All Contractor''s employees engaged on the Works shall be medically fit and free from diseases. At any time during the Contract period the Project Manager shall, at his absolute discretion, be entitled to direct any Contractor''s employee to be medically examined.","general_condition":"All Contractor''s employees engaged on the Works shall be medically fit and free from diseases. At any time during the Contract period the Project Manager shall, at his absolute discretion, be entitled to direct any Contractor''s employee to be medically examined.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"All Contractor''s employees engaged on the Works shall be medically fit and free from diseases. At any time during the Contract period the Project Manager shall, at his absolute discretion, be entitled...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":69}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 70, '{"itemType":"CLAUSE","clause_number":"10.10","clause_title":"Transportation","clause_text":"The Contractor shall be responsible for the transportation to Site of all persons employed by the Contractor in connection with the carrying out and completion of the Works. The vehicles to be used in such transportation shall be subject to the prior approval of the Project Manager. The Contractor shall comply with all applicable laws and regulations for the time being in force in the Country in relation to the transportation of such persons.","general_condition":"The Contractor shall be responsible for the transportation to Site of all persons employed by the Contractor in connection with the carrying out and completion of the Works. The vehicles to be used in such transportation shall be subject to the prior approval of the Project Manager. The Contractor shall comply with all applicable laws and regulations for the time being in force in the Country in relation to the transportation of such persons.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall be responsible for the transportation to Site of all persons employed by the Contractor in connection with the carrying out and completion of the Works. The vehicles to be used in...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":70}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 71, '{"itemType":"CLAUSE","clause_number":"10.11","clause_title":"Observance by Subcontractors","clause_text":"The Contractor shall (without limiting the generality of the requirements of Clause 3.2 (Subletting)) be responsible for observance by its Subcontractors of the provisions of Clauses 10.2 to 10.10 inclusive and shall hold the Employer fully and effectually indemnified accordingly.","general_condition":"The Contractor shall (without limiting the generality of the requirements of Clause 3.2 (Subletting)) be responsible for observance by its Subcontractors of the provisions of Clauses 10.2 to 10.10 inclusive and shall hold the Employer fully and effectually indemnified accordingly.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall (without limiting the generality of the requirements of Clause 3.2 (Subletting)) be responsible for observance by its Subcontractors of the provisions of Clauses 10.2 to 10.10 inc...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":71}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 72, '{"itemType":"CLAUSE","clause_number":"10.12","clause_title":"Visas","clause_text":"The Contractor shall obtain the visas and other permits needed by its and its Subcontractors'' personnel to enter into and reside and work in the Country as necessary for the purposes of carrying out the Works and remedying any defects therein.","general_condition":"The Contractor shall obtain the visas and other permits needed by its and its Subcontractors'' personnel to enter into and reside and work in the Country as necessary for the purposes of carrying out the Works and remedying any defects therein.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall obtain the visas and other permits needed by its and its Subcontractors'' personnel to enter into and reside and work in the Country as necessary for the purposes of carrying out t...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":72}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 73, '{"itemType":"CLAUSE","clause_number":"10.13","clause_title":"Records of Contractor''s Employees","clause_text":"The Contractor shall deliver to the Project Manager at his Site office, details in such form and at such intervals as the Project Manager may prescribe showing the names and numbers of the superintending and technical staff and the numbers of the several classes of labour from time to time employed by the Contractor and its Subcontractors on the Site.","general_condition":"The Contractor shall deliver to the Project Manager at his Site office, details in such form and at such intervals as the Project Manager may prescribe showing the names and numbers of the superintending and technical staff and the numbers of the several classes of labour from time to time employed by the Contractor and its Subcontractors on the Site.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall deliver to the Project Manager at his Site office, details in such form and at such intervals as the Project Manager may prescribe showing the names and numbers of the superintend...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":73}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 74, '{"itemType":"CLAUSE","clause_number":"11.1","clause_title":"General","clause_text":"All Plant to be provided shall be manufactured and all work to be done under the Contract shall be carried out (b) in a proper, workmanlike and careful manner in accordance with recognised good practices; (c) with properly equipped facilities and (unless otherwise specified in the Contract) without using hazardous materials; and (d) to the satisfaction of the Supervision Consultant and the Project Manager.","general_condition":"All Plant to be provided shall be manufactured and all work to be done under the Contract shall be carried out (b) in a proper, workmanlike and careful manner in accordance with recognised good practices; (c) with properly equipped facilities and (unless otherwise specified in the Contract) without using hazardous materials; and (d) to the satisfaction of the Supervision Consultant and the Project Manager.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"All Plant to be provided shall be manufactured and all work to be done under the Contract shall be carried out (b) in a proper, workmanlike and careful manner in accordance with recognised good practi...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":74}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 75, '{"itemType":"CLAUSE","clause_number":"11.2","clause_title":"Inspection and Testing","clause_text":"The Supervision Consultant and the Project Manager shall be entitled at any time to inspect, examine, measure and/or test any workmanship and/or Plant, including the materials for the manufacture or fabrication thereof and/or samples of such materials, whether on the Site, on the Contractor''s premises or on or at any other premises or places where the same may be. In the event of the Supervision Consultant wishing to inspect, examine, measure and/or test on or at any such other premises or places as aforesaid, the Contractor shall obtain for the Supervision Consultant and any person authorised by him all such permissions and facilities as the Contractor would have been bound to provide were the same being carried out on the Contractor''s premises. The Contractor shall (at its own Cost save as provided in Clause 11.3 (Covering and Uncovering the Works)) provide or procure the provision of such documents and other information, assistance, instruments, apparatus, machines, labour, materials, fuel, stores, samples, utilities and consumables as may be required by the Supervision Consultant for the purpose of any such inspection, examination, measurement and/or testing as aforesaid. The Contractor shall afford full opportunity for the Supervision Consultant to inspect, examine, measure and/or test any work on Site or wherever carried out. No inspection, examination, measurement and/or testing shall release the Contractor from any of its obligations under the Contract.","general_condition":"The Supervision Consultant and the Project Manager shall be entitled at any time to inspect, examine, measure and/or test any workmanship and/or Plant, including the materials for the manufacture or fabrication thereof and/or samples of such materials, whether on the Site, on the Contractor''s premises or on or at any other premises or places where the same may be. In the event of the Supervision Consultant wishing to inspect, examine, measure and/or test on or at any such other premises or places as aforesaid, the Contractor shall obtain for the Supervision Consultant and any person authorised by him all such permissions and facilities as the Contractor would have been bound to provide were the same being carried out on the Contractor''s premises. The Contractor shall (at its own Cost save as provided in Clause 11.3 (Covering and Uncovering the Works)) provide or procure the provision of such documents and other information, assistance, instruments, apparatus, machines, labour, materials, fuel, stores, samples, utilities and consumables as may be required by the Supervision Consultant for the purpose of any such inspection, examination, measurement and/or testing as aforesaid. The Contractor shall afford full opportunity for the Supervision Consultant to inspect, examine, measure and/or test any work on Site or wherever carried out. No inspection, examination, measurement and/or testing shall release the Contractor from any of its obligations under the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Supervision Consultant and the Project Manager shall be entitled at any time to inspect, examine, measure and/or test any workmanship and/or Plant, including the materials for the manufacture or f...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":75}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 76, '{"itemType":"CLAUSE","clause_number":"11.3","clause_title":"Covering and Uncovering the Works","clause_text":"No part of the Works shall be covered up or put out of sight without the approval of the Supervision Consultant and the Contractor shall afford full opportunity for the Supervision Consultant to inspect, examine, measure and/or test any such part of the Works which is about to be covered up or put out of view and to examine foundations before any part of the Works is placed thereon. The Contractor shall give due notice to the Project Manager (with a copy to the Supervision Consultant) whenever any such part of the Works or foundations is or are ready or about to be ready for examination and the Supervision Consultant shall, without unreasonable delay, unless he considers it unnecessary and advises the Contractor accordingly in writing, attend for the purpose of inspecting, examining and (if he thinks appropriate) measuring and testing such part of the Works or of examining such foundations. The Contractor shall uncover any part of the Works or make openings in or through the same as the Supervision Consultant may from time to time instruct and shall reinstate and make good the same to the satisfaction of the Supervision Consultant. If any such part of the Works has been covered up or put out of view after compliance with the requirements of the first paragraph of this Clause and is found to have been carried out in accordance with the Contract, the Costs of uncovering, making openings in or through, reinstating and making good the same shall be determined by the Project Manager and paid to the Contractor in accordance with Clause 26 (Interim and Final Certificates), but in any other case all Costs shall be borne by the Contractor. Provided that if any such instruction of the Supervision Consultant is due to his opinion that such part of the Works covered up or put out of view may not have been carried out in accordance with the Contract as a result of other parts of the Works having been found by the Supervision Consultant not to have been carried out in accordance with the Contract the Costs of uncovering, making openings in or through, reinstating and making good such part of the Works covered up or put out of view shall in all cases be borne by the Contractor.","general_condition":"No part of the Works shall be covered up or put out of sight without the approval of the Supervision Consultant and the Contractor shall afford full opportunity for the Supervision Consultant to inspect, examine, measure and/or test any such part of the Works which is about to be covered up or put out of view and to examine foundations before any part of the Works is placed thereon. The Contractor shall give due notice to the Project Manager (with a copy to the Supervision Consultant) whenever any such part of the Works or foundations is or are ready or about to be ready for examination and the Supervision Consultant shall, without unreasonable delay, unless he considers it unnecessary and advises the Contractor accordingly in writing, attend for the purpose of inspecting, examining and (if he thinks appropriate) measuring and testing such part of the Works or of examining such foundations. The Contractor shall uncover any part of the Works or make openings in or through the same as the Supervision Consultant may from time to time instruct and shall reinstate and make good the same to the satisfaction of the Supervision Consultant. If any such part of the Works has been covered up or put out of view after compliance with the requirements of the first paragraph of this Clause and is found to have been carried out in accordance with the Contract, the Costs of uncovering, making openings in or through, reinstating and making good the same shall be determined by the Project Manager and paid to the Contractor in accordance with Clause 26 (Interim and Final Certificates), but in any other case all Costs shall be borne by the Contractor. Provided that if any such instruction of the Supervision Consultant is due to his opinion that such part of the Works covered up or put out of view may not have been carried out in accordance with the Contract as a result of other parts of the Works having been found by the Supervision Consultant not to have been carried out in accordance with the Contract the Costs of uncovering, making openings in or through, reinstating and making good such part of the Works covered up or put out of view shall in all cases be borne by the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"No part of the Works shall be covered up or put out of sight without the approval of the Supervision Consultant and the Contractor shall afford full opportunity for the Supervision Consultant to inspe...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":76}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 77, '{"itemType":"CLAUSE","clause_number":"11.4","clause_title":"Cost of Testing","clause_text":"The Cost of making any test shall be borne by the Contractor. The Contractor shall provide or procure the provision of all documents and other information necessary for testing and such suitably qualified and experienced staff, assistance, instruments, apparatus, machines, labour, materials, fuel, stores, samples, utilities and consumables as are necessary to carry out such tests efficiently.","general_condition":"The Cost of making any test shall be borne by the Contractor. The Contractor shall provide or procure the provision of all documents and other information necessary for testing and such suitably qualified and experienced staff, assistance, instruments, apparatus, machines, labour, materials, fuel, stores, samples, utilities and consumables as are necessary to carry out such tests efficiently.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Cost of making any test shall be borne by the Contractor. The Contractor shall provide or procure the provision of all documents and other information necessary for testing and such suitably quali...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":77}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 78, '{"itemType":"CLAUSE","clause_number":"11.5","clause_title":"Dates for Testing","clause_text":"The Contractor shall give to the Project Manager (with a copy to the Supervision Consultant) not less than fourteen (14) days'' prior written notice of its readiness to carry out any test and shall agree with the Project Manager and the Supervision Consultant the date, time and place at which the same is to be carried out. The Contractor shall carry out such test on the date and at the time and place agreed. If the Contractor fails to carry out any such test on the date and at the time and place so agreed, any additional Costs consequently incurred by the Employer as determined by the Project Manager may be deducted by the Employer from any monies due or which may become due to the Contractor or the Employer may recover the same from the Contractor as a debt due from the Contractor. After carrying out any test the Contractor shall forthwith forward to the Project Manager (with a copy to the Supervision Consultant) a duly certified report of the test results. The provisions of this Clause 11.5 shall not apply to Tests on Completion, to which the provisions of Clause 19A.1 (Time for Tests) shall apply.","general_condition":"The Contractor shall give to the Project Manager (with a copy to the Supervision Consultant) not less than fourteen (14) days'' prior written notice of its readiness to carry out any test and shall agree with the Project Manager and the Supervision Consultant the date, time and place at which the same is to be carried out. The Contractor shall carry out such test on the date and at the time and place agreed. If the Contractor fails to carry out any such test on the date and at the time and place so agreed, any additional Costs consequently incurred by the Employer as determined by the Project Manager may be deducted by the Employer from any monies due or which may become due to the Contractor or the Employer may recover the same from the Contractor as a debt due from the Contractor. After carrying out any test the Contractor shall forthwith forward to the Project Manager (with a copy to the Supervision Consultant) a duly certified report of the test results. The provisions of this Clause 11.5 shall not apply to Tests on Completion, to which the provisions of Clause 19A.1 (Time for Tests) shall apply.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall give to the Project Manager (with a copy to the Supervision Consultant) not less than fourteen (14) days'' prior written notice of its readiness to carry out any test and shall agr...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":78}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 79, '{"itemType":"CLAUSE","clause_number":"11.6","clause_title":"Test Certificates","clause_text":"As and when any Plant shall have passed any particular test the Supervision Consultant shall if so requested by the Contractor sign (jointly with the Contractor) a certificate in writing furnished by the Contractor to that effect.","general_condition":"As and when any Plant shall have passed any particular test the Supervision Consultant shall if so requested by the Contractor sign (jointly with the Contractor) a certificate in writing furnished by the Contractor to that effect.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"As and when any Plant shall have passed any particular test the Supervision Consultant shall if so requested by the Contractor sign (jointly with the Contractor) a certificate in writing furnished by ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":79}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 80, '{"itemType":"CLAUSE","clause_number":"11.7","clause_title":"Rejection and Remedy","clause_text":"If at any time the Supervision Consultant shall as a result of any inspection, examination, measurement and/or test decide that any workmanship and/or Plant (including materials for the manufacture or fabrication thereof and/or samples of such materials) is defective or otherwise not in accordance with the Contract he shall, notwithstanding any earlier inspection, examination, measurement and/or test or any interim payment, have power to order the Contractor in writing to: (a) make good the defect or otherwise take such steps as may be necessary to ensure that the workmanship or Plant complies with the Contract; and/or (b) remove from the Site, within such time as may be specified in the order, any Plant which in his opinion is defective or otherwise not in accordance with the Contract and substitute proper and suitable Plant; and the Contractor shall comply with any such order in a timely manner and at its sole Cost. Thereafter the Supervision Consultant may require any relevant testing to be repeated and may stipulate a reasonable period, having regard to the requirements of the Programme, within which such repeated testing must be carried out and completed. The Cost of.....................","general_condition":"If at any time the Supervision Consultant shall as a result of any inspection, examination, measurement and/or test decide that any workmanship and/or Plant (including materials for the manufacture or fabrication thereof and/or samples of such materials) is defective or otherwise not in accordance with the Contract he shall, notwithstanding any earlier inspection, examination, measurement and/or test or any interim payment, have power to order the Contractor in writing to: (a) make good the defect or otherwise take such steps as may be necessary to ensure that the workmanship or Plant complies with the Contract; and/or (b) remove from the Site, within such time as may be specified in the order, any Plant which in his opinion is defective or otherwise not in accordance with the Contract and substitute proper and suitable Plant; and the Contractor shall comply with any such order in a timely manner and at its sole Cost. Thereafter the Supervision Consultant may require any relevant testing to be repeated and may stipulate a reasonable period, having regard to the requirements of the Programme, within which such repeated testing must be carried out and completed. The Cost of.....................","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If at any time the Supervision Consultant shall as a result of any inspection, examination, measurement and/or test decide that any workmanship and/or Plant (including materials for the manufacture or...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":80}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 81, '{"itemType":"CLAUSE","clause_number":"12.1","clause_title":"Passage of Title","clause_text":"The legal title to any item of Plant provided or to be provided pursuant to the Contract shall pass to the Employer upon the first to occur of the following events, namely (b) when the Contractor becomes entitled to payment of any portion of the value of the Plant; and (c) that item otherwise becoming expressly or impliedly appropriated to the Contract whether or not the Employer or the Project Manager has assented to that appropriation. The Contractor shall as beneficial owner sign such documents and do all such things necessary to pass to the Employer absolute legal title to such item of Plant free from all charges, liens and other encumbrances of any kind whatsoever.","general_condition":"The legal title to any item of Plant provided or to be provided pursuant to the Contract shall pass to the Employer upon the first to occur of the following events, namely (b) when the Contractor becomes entitled to payment of any portion of the value of the Plant; and (c) that item otherwise becoming expressly or impliedly appropriated to the Contract whether or not the Employer or the Project Manager has assented to that appropriation. The Contractor shall as beneficial owner sign such documents and do all such things necessary to pass to the Employer absolute legal title to such item of Plant free from all charges, liens and other encumbrances of any kind whatsoever.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The legal title to any item of Plant provided or to be provided pursuant to the Contract shall pass to the Employer upon the first to occur of the following events, namely (b) when the Contractor beco...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":81}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 82, '{"itemType":"CLAUSE","clause_number":"12.2","clause_title":"Setting Aside and Marking","clause_text":"Where in accordance with Clause 12.1 (Passage of Title) the legal title to any item of Plant passes to the Employer prior to the delivery to Site of such item, the Contractor shall so far as is practicable and to the satisfaction of the Project Manager set aside and mark the item and all component parts thereof and designated materials therefor as the property of the Employer. In the event of the Contractor failing so to set aside and mark as aforesaid the Project Manager shall be entitled to exclude from any Interim Certificate the value of any such items (and all component parts thereof and designated materials therefor) which the Contractor may otherwise be entitled to have included in such Interim Certificate.","general_condition":"Where in accordance with Clause 12.1 (Passage of Title) the legal title to any item of Plant passes to the Employer prior to the delivery to Site of such item, the Contractor shall so far as is practicable and to the satisfaction of the Project Manager set aside and mark the item and all component parts thereof and designated materials therefor as the property of the Employer. In the event of the Contractor failing so to set aside and mark as aforesaid the Project Manager shall be entitled to exclude from any Interim Certificate the value of any such items (and all component parts thereof and designated materials therefor) which the Contractor may otherwise be entitled to have included in such Interim Certificate.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Where in accordance with Clause 12.1 (Passage of Title) the legal title to any item of Plant passes to the Employer prior to the delivery to Site of such item, the Contractor shall so far as is practi...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":82}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 83, '{"itemType":"CLAUSE","clause_number":"12.3","clause_title":"Care and Possession; and Reversion","clause_text":"Any item of Plant the legal title to which has passed to the Employer in accordance with Clause 12.1 (Passage of Title) and Free Issue Items shall be in the care and possession of the Contractor solely for the purposes of the Contract and shall not be within the ownership or disposition of the Contractor. Any Interim Certificate issued by the Project Manager pursuant to Clause 26.2 (Issue of Interim Certificates) shall be without prejudice to the exercise of any power of the Supervision Consultant contained in the Contract to reject any item of Plant covered thereby which is not in accordance with the Contract and upon any such rejection the legal title to the rejected item of Plant shall immediately revert to the Contractor. The legal title to any Plant removed from the Site in accordance with Clause 9.15 (Removal of Plant and Contractor''s Equipment) shall revert to the Contractor immediately upon such removal.","general_condition":"Any item of Plant the legal title to which has passed to the Employer in accordance with Clause 12.1 (Passage of Title) and Free Issue Items shall be in the care and possession of the Contractor solely for the purposes of the Contract and shall not be within the ownership or disposition of the Contractor. Any Interim Certificate issued by the Project Manager pursuant to Clause 26.2 (Issue of Interim Certificates) shall be without prejudice to the exercise of any power of the Supervision Consultant contained in the Contract to reject any item of Plant covered thereby which is not in accordance with the Contract and upon any such rejection the legal title to the rejected item of Plant shall immediately revert to the Contractor. The legal title to any Plant removed from the Site in accordance with Clause 9.15 (Removal of Plant and Contractor''s Equipment) shall revert to the Contractor immediately upon such removal.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Any item of Plant the legal title to which has passed to the Employer in accordance with Clause 12.1 (Passage of Title) and Free Issue Items shall be in the care and possession of the Contractor solel...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":83}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 84, '{"itemType":"CLAUSE","clause_number":"12.4","clause_title":"Title to Documents","clause_text":"(a) The legal title to and any copyright in the Specification, the Drawings, the Contractor''s Drawings, and all manuals, source codes, computer programmes and other software, diskettes, samples, patterns, models and other documents and information of a technical nature shall at all times remain vested in the Employer or shall become so vested forthwith upon their preparation and they shall not be copied, used or communicated to a third party by the Contractor except as necessary for the purposes of the Contract. (b) The Contractor shall pass to the Employer the right to use, copy and communicate any Drawings, manuals, samples, patterns, models or other technical documents or information (including making and using modifications) for which the Contractor has a copyright, a patent or legal title, for the operation, maintenance, dismantling, reassembly, adjustment, repair, refurbishment, modification, alteration and extension of the Works.","general_condition":"(a) The legal title to and any copyright in the Specification, the Drawings, the Contractor''s Drawings, and all manuals, source codes, computer programmes and other software, diskettes, samples, patterns, models and other documents and information of a technical nature shall at all times remain vested in the Employer or shall become so vested forthwith upon their preparation and they shall not be copied, used or communicated to a third party by the Contractor except as necessary for the purposes of the Contract. (b) The Contractor shall pass to the Employer the right to use, copy and communicate any Drawings, manuals, samples, patterns, models or other technical documents or information (including making and using modifications) for which the Contractor has a copyright, a patent or legal title, for the operation, maintenance, dismantling, reassembly, adjustment, repair, refurbishment, modification, alteration and extension of the Works.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) The legal title to and any copyright in the Specification, the Drawings, the Contractor''s Drawings, and all manuals, source codes, computer programmes and other software, diskettes, samples, patte...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":84}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 85, '{"itemType":"CLAUSE","clause_number":"12.5","clause_title":"Risk and Care","clause_text":"(a) The Works or any Section or Portion of the Works and any Plant or any Free Issue Items shall be at the risk of the Contractor until the issue of the Taking-Over Certificate under Clause 20.1 (Taking-Over Certificate) in respect of the relevant Section or Portion and accordingly until such time the Contractor shall take full responsibility for the care thereof. Provided that the Contractor shall continue to take full responsibility for the care of any outstanding work (and any Plant and Free Issue Items relating thereto) which it shall have undertaken to finish during the Defects Liability Period until such outstanding work is completed. If while the Contractor shall be responsible for the care thereof of any Section or Portion of the Works or any Plant or any Free Issue Items any loss or damage shall happen to any Section or Portion of the Works or to any relevant Plant or to any Free Issue Items from any cause whatsoever (save and except the excepted risks as defined in paragraph (b) of this Clause) then: (i) in respect of any such Section or Portion or Plant the Contractor shall at its own Cost replace, repair and make good the same to the satisfaction of the Supervision Consultant; and (ii) in respect of any such Free Issue Items the Employer may himself replace, repair and make good the same or the Project Manager may instruct the Contractor in writing at the Contractor''s Cost to carry out such replacement, repair and making good to the satisfaction of the Supervision Consultant. All Costs which the Employer incurs as a result of such replacement, repair and making good by the Employer and/or the Contractor shall be recoverable from the Contractor by the Employer as a debt due from the Contractor, or may be deducted by the Employer from any monies due or which may become due to the Contractor. The Contractor shall also at its own Cost replace, repair and make good to the satisfaction of the Supervision Consultant any loss or damage which may happen to the Works or any part thereof or any Plant or any Free Issue Items if such loss or damage is occasioned by it or by any Subcontractor in the course of any operations carried out by it or by such Subcontractor for the purpose of completing any outstanding work or complying with its obligations under Clauses 21.2 (Defects after Taking Over) or 21.7 (Contractor to Search) or complying with its obligations under any Extended Warranty. (i) (insofar as they directly interfere with the progress of the Works and relate to and directly affect the Country) war, hostilities (whether war be declared or not), invasion, act of foreign enemies, rebellion, revolution, insurrection, military or usurped power, civil war, or (unless solely restricted to employees of the Contractor or of its Subcontractors and arising from the conduct of the Works) riot, commotion or disorder in the Country; or (ii) without prejudice to the Contractor''s responsibilities and liabilities under the Contract and the applicable Law, a cause solely due to a design supplied or specified by the Employer, the Project Manager or the Supervision Consultant if the Contractor can demonstrate that it had taken all necessary steps to satisfy itself as to the sufficiency of the said design and had promptly notified the Project Manager (with a copy to the Supervision Consultant) in writing after discovering any insufficiency in the said design; or (iii) ionising radiation or contamination by radio-activity from any nuclear fuel or from any nuclear waste from the combustion of nuclear fuel, radio-active toxic explosives or other hazardous properties of any explosive nuclear assembly or nuclear components thereof; or (iv) pressure waves caused by aircraft or other aerial devices travelling at sonic or supersonic speeds; or (v) any occurrence of the forces of nature (excluding any quantity of rainfall) that an experienced contractor could not foresee, or (if foreseeable) could not reasonably make provision for. (c) In the event of any such loss or damage as aforesaid arising from or occasioned by any of the excepted risks: (i) in respect of the relevant part of the Works and Plant the Contractor shall be entitled to payment as if such loss or damage had not occurred; and (ii) in respect of the relevant part of the Works, Plant and Free Issue Items the same shall, if required by the Employer, be replaced, repaired and made good by the Contractor but at the Cost of the Employer at a price to be determined in accordance with Clause 22.3 (Valuation of Variation Orders). If not so required by the Employer, the Employer may at his own Cost replace, repair and make good such loss or damage. (d) The Contractor''s risks are all risks other than the \"excepted risks\" as defined in paragraph (b) of this Clause 12.5.","general_condition":"(a) The Works or any Section or Portion of the Works and any Plant or any Free Issue Items shall be at the risk of the Contractor until the issue of the Taking-Over Certificate under Clause 20.1 (Taking-Over Certificate) in respect of the relevant Section or Portion and accordingly until such time the Contractor shall take full responsibility for the care thereof. Provided that the Contractor shall continue to take full responsibility for the care of any outstanding work (and any Plant and Free Issue Items relating thereto) which it shall have undertaken to finish during the Defects Liability Period until such outstanding work is completed. If while the Contractor shall be responsible for the care thereof of any Section or Portion of the Works or any Plant or any Free Issue Items any loss or damage shall happen to any Section or Portion of the Works or to any relevant Plant or to any Free Issue Items from any cause whatsoever (save and except the excepted risks as defined in paragraph (b) of this Clause) then: (i) in respect of any such Section or Portion or Plant the Contractor shall at its own Cost replace, repair and make good the same to the satisfaction of the Supervision Consultant; and (ii) in respect of any such Free Issue Items the Employer may himself replace, repair and make good the same or the Project Manager may instruct the Contractor in writing at the Contractor''s Cost to carry out such replacement, repair and making good to the satisfaction of the Supervision Consultant. All Costs which the Employer incurs as a result of such replacement, repair and making good by the Employer and/or the Contractor shall be recoverable from the Contractor by the Employer as a debt due from the Contractor, or may be deducted by the Employer from any monies due or which may become due to the Contractor. The Contractor shall also at its own Cost replace, repair and make good to the satisfaction of the Supervision Consultant any loss or damage which may happen to the Works or any part thereof or any Plant or any Free Issue Items if such loss or damage is occasioned by it or by any Subcontractor in the course of any operations carried out by it or by such Subcontractor for the purpose of completing any outstanding work or complying with its obligations under Clauses 21.2 (Defects after Taking Over) or 21.7 (Contractor to Search) or complying with its obligations under any Extended Warranty. (i) (insofar as they directly interfere with the progress of the Works and relate to and directly affect the Country) war, hostilities (whether war be declared or not), invasion, act of foreign enemies, rebellion, revolution, insurrection, military or usurped power, civil war, or (unless solely restricted to employees of the Contractor or of its Subcontractors and arising from the conduct of the Works) riot, commotion or disorder in the Country; or (ii) without prejudice to the Contractor''s responsibilities and liabilities under the Contract and the applicable Law, a cause solely due to a design supplied or specified by the Employer, the Project Manager or the Supervision Consultant if the Contractor can demonstrate that it had taken all necessary steps to satisfy itself as to the sufficiency of the said design and had promptly notified the Project Manager (with a copy to the Supervision Consultant) in writing after discovering any insufficiency in the said design; or (iii) ionising radiation or contamination by radio-activity from any nuclear fuel or from any nuclear waste from the combustion of nuclear fuel, radio-active toxic explosives or other hazardous properties of any explosive nuclear assembly or nuclear components thereof; or (iv) pressure waves caused by aircraft or other aerial devices travelling at sonic or supersonic speeds; or (v) any occurrence of the forces of nature (excluding any quantity of rainfall) that an experienced contractor could not foresee, or (if foreseeable) could not reasonably make provision for. (c) In the event of any such loss or damage as aforesaid arising from or occasioned by any of the excepted risks: (i) in respect of the relevant part of the Works and Plant the Contractor shall be entitled to payment as if such loss or damage had not occurred; and (ii) in respect of the relevant part of the Works, Plant and Free Issue Items the same shall, if required by the Employer, be replaced, repaired and made good by the Contractor but at the Cost of the Employer at a price to be determined in accordance with Clause 22.3 (Valuation of Variation Orders). If not so required by the Employer, the Employer may at his own Cost replace, repair and make good such loss or damage. (d) The Contractor''s risks are all risks other than the \"excepted risks\" as defined in paragraph (b) of this Clause 12.5.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) The Works or any Section or Portion of the Works and any Plant or any Free Issue Items shall be at the risk of the Contractor until the issue of the Taking-Over Certificate under Clause 20.1 (Taki...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":85}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 86, '{"itemType":"CLAUSE","clause_number":"12.6","clause_title":"General Indemnity","clause_text":"(a) The Contractor shall indemnify the Employer (including without limitation any consultant, servant, agent or employee of the Employer) against any and all losses, liabilities, costs, claims, actions or demands which it may incur or which may be made against it as a result of or in connection with any loss, injury (including death) or damage to persons or property (other than property forming part of the Works and Free Issue Items not yet taken over) which may result from or occur in connection with the carrying out of the Works and/or the remedying of defects therein and/or the Contractor''s observance or performance of or failure to observe or perform its other obligations under the Contract. Provided that the foregoing indemnity shall not apply with regard to any such matters incurred or made as a result of or in connection with: (i) losses, injuries or damage to persons or property which are in the opinion of the Project Manager the unavoidable result of the carrying out of the Works in accordance with the Contract or the remedying of defects therein but this exclusion shall only apply with regard to any such matter of which the Contractor has notified the Project Manager in writing not less than fourteen (14) days prior to the occurrence of the relevant loss, injury or damage; (ii) losses, injuries or damage to persons or property directly resulting from any act or neglect of the Employer, its agents, employees or other contractors not being employed by the Contractor; or (iii) losses, injuries or damage to persons or property contributed to by any such act or neglect as is referred to in paragraph (ii) above, to the extent of an equitable proportion thereof having regard to the degree of such contribution. Without in any way limiting its generality, the above indemnity shall apply to claims, actions and demands brought or made by employees or agents of the Contractor, of any of its Subcontractors or of the Employer. In the event of any claim or demand being made against or any action being taken against either party to the Contract which claim, demand or action is within the scope of the relevant indemnity contained in this Clause 12.6, the party liable to indemnify shall be promptly notified in writing by the other party and the party liable to indemnify shall at its own Cost conduct all negotiations for the settlement of the same and any litigation that may arise therefrom. The party to be indemnified shall not unless and until the other party shall have failed to take over the conduct of the negotiations or litigation make any admission which may be prejudicial thereto. The conduct (by the party liable to indemnify) of such negotiations or litigation shall be conditional upon that party having first given to the other party such reasonable security as shall from time to time be required by the other party to cover the amount ascertained or agreed or estimated, as the case may be, of any losses, liabilities and costs which the other party may incur or for which it may become liable. The other party shall, at the request of the party liable to indemnify, afford all available assistance for any such purpose and shall be repaid all reasonable Costs incurred in so doing. (b) The Contractor shall indemnify the Employer against any loss or damage (whether direct or indirect actual or consequential) resulting from the installation of software or firmware into equipment at the Site which has not been tested for and shown to be clear of viruses or like agents.","general_condition":"(a) The Contractor shall indemnify the Employer (including without limitation any consultant, servant, agent or employee of the Employer) against any and all losses, liabilities, costs, claims, actions or demands which it may incur or which may be made against it as a result of or in connection with any loss, injury (including death) or damage to persons or property (other than property forming part of the Works and Free Issue Items not yet taken over) which may result from or occur in connection with the carrying out of the Works and/or the remedying of defects therein and/or the Contractor''s observance or performance of or failure to observe or perform its other obligations under the Contract. Provided that the foregoing indemnity shall not apply with regard to any such matters incurred or made as a result of or in connection with: (i) losses, injuries or damage to persons or property which are in the opinion of the Project Manager the unavoidable result of the carrying out of the Works in accordance with the Contract or the remedying of defects therein but this exclusion shall only apply with regard to any such matter of which the Contractor has notified the Project Manager in writing not less than fourteen (14) days prior to the occurrence of the relevant loss, injury or damage; (ii) losses, injuries or damage to persons or property directly resulting from any act or neglect of the Employer, its agents, employees or other contractors not being employed by the Contractor; or (iii) losses, injuries or damage to persons or property contributed to by any such act or neglect as is referred to in paragraph (ii) above, to the extent of an equitable proportion thereof having regard to the degree of such contribution. Without in any way limiting its generality, the above indemnity shall apply to claims, actions and demands brought or made by employees or agents of the Contractor, of any of its Subcontractors or of the Employer. In the event of any claim or demand being made against or any action being taken against either party to the Contract which claim, demand or action is within the scope of the relevant indemnity contained in this Clause 12.6, the party liable to indemnify shall be promptly notified in writing by the other party and the party liable to indemnify shall at its own Cost conduct all negotiations for the settlement of the same and any litigation that may arise therefrom. The party to be indemnified shall not unless and until the other party shall have failed to take over the conduct of the negotiations or litigation make any admission which may be prejudicial thereto. The conduct (by the party liable to indemnify) of such negotiations or litigation shall be conditional upon that party having first given to the other party such reasonable security as shall from time to time be required by the other party to cover the amount ascertained or agreed or estimated, as the case may be, of any losses, liabilities and costs which the other party may incur or for which it may become liable. The other party shall, at the request of the party liable to indemnify, afford all available assistance for any such purpose and shall be repaid all reasonable Costs incurred in so doing. (b) The Contractor shall indemnify the Employer against any loss or damage (whether direct or indirect actual or consequential) resulting from the installation of software or firmware into equipment at the Site which has not been tested for and shown to be clear of viruses or like agents.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) The Contractor shall indemnify the Employer (including without limitation any consultant, servant, agent or employee of the Employer) against any and all losses, liabilities, costs, claims, action...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":86}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 87, '{"itemType":"CLAUSE","clause_number":"12.7","clause_title":"Loss, Injury or Damage to Workmen","clause_text":"The Employer (including without limitation any consultant, servant, agent or employee of the Employer) shall not be liable for and the Contractor shall indemnify it against any and all losses, liabilities, costs, claims, actions or demands which it may incur or which may be made against it as a result of or in connection with any loss, injury (including death) or damage to any workman or other person in the employment of the Contractor or any of its Subcontractors save and except any such matters which may arise from any act or neglect of the Employer, its consultants, servants, agents, employees or other contractors not being employed by the Contractor. The provisions of the last paragraph of Sub-Clause (a) of Clause 12.6 (General Indemnity) shall also apply to the indemnity in this Clause 12.7.","general_condition":"The Employer (including without limitation any consultant, servant, agent or employee of the Employer) shall not be liable for and the Contractor shall indemnify it against any and all losses, liabilities, costs, claims, actions or demands which it may incur or which may be made against it as a result of or in connection with any loss, injury (including death) or damage to any workman or other person in the employment of the Contractor or any of its Subcontractors save and except any such matters which may arise from any act or neglect of the Employer, its consultants, servants, agents, employees or other contractors not being employed by the Contractor. The provisions of the last paragraph of Sub-Clause (a) of Clause 12.6 (General Indemnity) shall also apply to the indemnity in this Clause 12.7.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Employer (including without limitation any consultant, servant, agent or employee of the Employer) shall not be liable for and the Contractor shall indemnify it against any and all losses, liabili...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":87}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 88, '{"itemType":"CLAUSE","clause_number":"12.8","clause_title":"Loss or Damage to Contractor''s Equipment","clause_text":"The Employer (including without limitation any consultant, servant, agent or employee of the Employer) shall not in any circumstances be liable to the Contractor for any loss of or damage to any of the Contractor''s Equipment or for any losses, liabilities, costs, claims, actions or demands which the Contractor may incur or which may be made against it as a result of or in connection with any such loss or damage. Provided that this exclusion shall not apply if and to the extent that such loss or damage directly results from any act or neglect of the Employer, its consultants, servants, agents, employees or other contractors not being employed by the Contractor.","general_condition":"The Employer (including without limitation any consultant, servant, agent or employee of the Employer) shall not in any circumstances be liable to the Contractor for any loss of or damage to any of the Contractor''s Equipment or for any losses, liabilities, costs, claims, actions or demands which the Contractor may incur or which may be made against it as a result of or in connection with any such loss or damage. Provided that this exclusion shall not apply if and to the extent that such loss or damage directly results from any act or neglect of the Employer, its consultants, servants, agents, employees or other contractors not being employed by the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Employer (including without limitation any consultant, servant, agent or employee of the Employer) shall not in any circumstances be liable to the Contractor for any loss of or damage to any of th...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":88}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 89, '{"itemType":"CLAUSE","clause_number":"12.9","clause_title":"Insurance","clause_text":"Without in any way limiting or detracting from the Contractor''s liabilities under Clauses 12.5 (Risk and Care), 12.6 (General Indemnity), or 12.7 (Loss, Injury or Damage to Workmen) or under any other provision of the Contract the Contractor shall at its own Cost take out as from the commencement of the Works and maintain until the issue by the Project Manager of the last Final Certificate in accordance with Clause 26.4 (Issue of Final Certificates) the following insurances with insurers acceptable to the Employer and under one or more policies in such form and with only such exclusions and restrictions as the Employer may approve, namely: (a) Plant and Contractor''s Equipment - being coverage in respect of all risks of physical loss or damage which may occur: (i) to any Plant prior to its delivery to and during its unloading at the Site; and The said Plant and Contractor''s Equipment shall be covered wherever in the world the same may for the time being be situated and even if the same be in storage or in transit except that Plant shall cease to be so covered under this policy upon its delivery to and it having been unloaded at the Site. Such coverage shall have a limit of liability of not less than the current replacement value of all relevant Plant and Contractor''s Equipment, and shall include all labour charges, transportation charges, professional fees, debris removal and demolition Costs, and other Costs. In the case of physical loss or damage occurring to any Plant prior to its delivery to and during its unloading at the Site, the policy of insurance shall be an all risks marine insurance policy in accordance with the Institute Cargo Clauses \"A\". The insurance on Plant shall be in the joint names of the Employer and the Contractor and with the Employer named as \"First Loss Payee\". The Contractor shall following the delivery to and unloading at the Site of any Plant, promptly inspect such Plant to determine whether any physical loss or damage has occurred thereto and shall immediately notify the Project Manager in writing with a copy to the Employer of any such loss or damage that has so occurred. Such notification shall be accompanied by full details of the said loss or damage. (b) Contractor''s All Risks - being coverage in respect of all risks (other than the excepted risks as defined in paragraph (b) of Clause 12.5 (Risk and Care)) of physical loss or damage which may occur: (i) to any Section or Portion of the Works or any relevant Plant or any relevant Free Issue Items while the Contractor shall be responsible for the care thereof as provided in the said Clause 12.5 and, with regard to loss or damage arising from a cause occurring prior to the issue of the Taking-Over Certificate under Clause 20.1 (Taking-Over Certificate) for any Section or Portion of the Works, during the Defects Liability Period applicable to that Section or Portion of the Works; and (ii) to the Works, or any part thereof or any Plant or any Free Issue Items if such loss or damage is occasioned by the Contractor or by any Subcontractor in the course of any operations carried out by it or by such Subcontractor for the purpose of completing any outstanding work which it shall have undertaken to finish during the Defects Liability Period or complying with its obligations under Clauses 21.2 (Defects after Taking Over) or 21.7 (Contractor to Search) or complying with its obligations under any Extended Warranty. However the said Plant shall not be covered under these policies prior to its delivery to and during its unloading at the Site. Such coverage shall in the case of sub-paragraph (i) above have a limit of liability of not less than one hundred and fifteen per cent (115%) of the current replacement value of the Works and the Free Issue Items which the Contractor shall for the time being be responsible for the care of. Such coverage shall in the case of sub-paragraph (ii) above have a limit of liability of not less than one hundred and fifteen per cent (115%) of the current replacement value of the whole of the Works and the Free Issue Items. Each such limit of liability as aforesaid shall apply for any one occurrence or series of occurrences arising out of one event. Such coverage shall exclude the first part of any loss or damage not exceeding the amounts stated in the Particular Conditions. Such coverage shall in all cases include all labour charges, transportation charges, professional fees, debris removal and demolition Costs, and other Costs. The insurance shall be in the joint names of the Employer and the Contractor and with the Employer named as \"First Loss Payee\". (c) Public Liability - being coverage in respect of legal liability for loss, injury (including death) or damage to persons or property (other than property forming part of the Works and the Free Issue Items not yet taken over), including in each case claimant''s and defence costs, resulting from or occurring in connection with the carrying out of the Works and/or the remedying of defects therein and/or the Contractor''s observance or performance of or failure to observe or perform its other obligations under the Contract. Such liability shall without limitation include liability to consultants, servants, employees and agents of the Employer. Such coverage shall have a limit of liability of not less than the amount stated in the Particular Conditions for any one occurrence or series of occurrences arising out of one event and may, in respect only of the said loss or damage to property, exclude the first part of any loss or damage not exceeding the amount stated in the Particular Conditions for any one occurrence or such series thereof. The insurance shall be in the joint names of the Employer and the Contractor and with the Employer named as \"First Loss Payee\". The insurance shall include a Cross Liability Clause. (d) Workmen''s Compensation - being coverage as required by the laws and regulations in the Country in respect of personal injuries (including death and including claimant''s and defence costs) to employees of the Contractor and its Subcontractors resulting from or occurring in connection with the carrying out of the Works and/or the remedying of defects therein and/or the Contractor''s observance or performance of or failure to observe or perform its other obligations under the Contract including its Extended Warranty obligations. Such coverage shall be extended to include Employer''s Liability of not less than the statutory amount for each accident or (if no such statutory amount) not less than the amount stated in the Particular Conditions for each accident. The obligations of this paragraph (d) shall with respect to any employees of a Subcontractor be satisfied by a policy otherwise complying with this sub-paragraph but being in the name of the Subcontractor rather than of the Contractor, provided that the Employer is indemnified under the policy; but the Contractor shall be liable for procuring the compliance by the Subcontractor with the provisions of the three paragraphs immediately following paragraph (e) of this Clause 12.9. (e) Motor Vehicle Third Party and Passenger Liability Insurance being coverage in respect of death of or injury to persons and/or loss of or damage to property in respect of motor vehicles used by the Contractor in connection with the carrying out of the Works or the remedying of defects therein. The Contractor shall ensure that Subcontractors maintain such insurance in respect of motor vehicles used by them. Such insurance shall provide an unlimited indemnity for death of or injury to persons and the equivalent of not less than the amount stated in the Particular Conditions for loss of or damage to property which shall be extended as may be necessary to cover any additional cover required by statutory requirements in the Country. The Contractor shall procure that the policy, or each of the policies, of insurance referred to in this Clause 12.9 contains a waiver by the insurers of any and all rights of subrogation they might otherwise be able to exercise against the Employer or any of its consultants, servants, employees or agents. The Contractor shall procure that each of the policies of insurance referred to in paragraphs (d) and (e) of this Clause 12.9 contains a clause indemnifying the Employer or any of its consultants, servants, employees or agents as though they are the Insured. The Employer and the Project Manager shall each be entitled to a copy of the said policy or policies of insurance together with copies of either the premium receipts or certificates from the insurers confirming receipt of premiums as appropriate and from time to time upon demand to inspect the originals of the said policy or policies of insurance and to have produced to them satisfactory evidence that the same are in full force and effect. The said policy or policies shall provide that the Employer and the Project Manager are each entitled to not less than thirty (30) days'' notice from the insurers prior to cancellation, termination or alteration of cover. The Contractor shall not do or omit to do any act or thing whereby the said policy or policies may be or become void or voidable. If the Contractor shall fail to effect and keep in force any of the insurances referred to in this Clause 12.9, or any other insurance which the Contractor may be required to effect under the terms of the Contract, then and in any such case the Employer may (without prejudice to any other remedy) effect and keep in force such insurance and pay such premium or premiums as may be necessary for that purpose and from time to time deduct the amount so paid by the Employer from any monies due or which may become due to the Contractor, or recover the same as a debt due from the Contractor. The Contractor shall notify the insurers of any changes in the nature, extent or programme for the carrying out of the Works with a copy to the Employer and to the Project Manager and ensure the adequacy of the insurances at all times in accordance with the terms of the Contract. Notwithstanding the foregoing provisions it shall be a condition of each of the policies of insurance required to be taken out under the Contract that the insurer will not exercise its right to avoid the insurance cover on the grounds of any non-disclosure, mis-description or misrepresentation in any information relevant to the risks insured under such policy. If the Contractor considers that additional insurance is required either by way of wider cover or higher sums insured or higher limits of indemnity then this shall be taken out by the Contractor at its Cost. The Contractor shall indemnify the Employer against any amount which would otherwise be claimable by the Employer under any of the insurances required to be effected under this Clause 12.9 but which the Employer is unable to claim by virtue of the amount concerned falling within any deductible (Insured''s Retained Liability) applicable to the relevant insurance cover.","general_condition":"Without in any way limiting or detracting from the Contractor''s liabilities under Clauses 12.5 (Risk and Care), 12.6 (General Indemnity), or 12.7 (Loss, Injury or Damage to Workmen) or under any other provision of the Contract the Contractor shall at its own Cost take out as from the commencement of the Works and maintain until the issue by the Project Manager of the last Final Certificate in accordance with Clause 26.4 (Issue of Final Certificates) the following insurances with insurers acceptable to the Employer and under one or more policies in such form and with only such exclusions and restrictions as the Employer may approve, namely: (a) Plant and Contractor''s Equipment - being coverage in respect of all risks of physical loss or damage which may occur: (i) to any Plant prior to its delivery to and during its unloading at the Site; and The said Plant and Contractor''s Equipment shall be covered wherever in the world the same may for the time being be situated and even if the same be in storage or in transit except that Plant shall cease to be so covered under this policy upon its delivery to and it having been unloaded at the Site. Such coverage shall have a limit of liability of not less than the current replacement value of all relevant Plant and Contractor''s Equipment, and shall include all labour charges, transportation charges, professional fees, debris removal and demolition Costs, and other Costs. In the case of physical loss or damage occurring to any Plant prior to its delivery to and during its unloading at the Site, the policy of insurance shall be an all risks marine insurance policy in accordance with the Institute Cargo Clauses \"A\". The insurance on Plant shall be in the joint names of the Employer and the Contractor and with the Employer named as \"First Loss Payee\". The Contractor shall following the delivery to and unloading at the Site of any Plant, promptly inspect such Plant to determine whether any physical loss or damage has occurred thereto and shall immediately notify the Project Manager in writing with a copy to the Employer of any such loss or damage that has so occurred. Such notification shall be accompanied by full details of the said loss or damage. (b) Contractor''s All Risks - being coverage in respect of all risks (other than the excepted risks as defined in paragraph (b) of Clause 12.5 (Risk and Care)) of physical loss or damage which may occur: (i) to any Section or Portion of the Works or any relevant Plant or any relevant Free Issue Items while the Contractor shall be responsible for the care thereof as provided in the said Clause 12.5 and, with regard to loss or damage arising from a cause occurring prior to the issue of the Taking-Over Certificate under Clause 20.1 (Taking-Over Certificate) for any Section or Portion of the Works, during the Defects Liability Period applicable to that Section or Portion of the Works; and (ii) to the Works, or any part thereof or any Plant or any Free Issue Items if such loss or damage is occasioned by the Contractor or by any Subcontractor in the course of any operations carried out by it or by such Subcontractor for the purpose of completing any outstanding work which it shall have undertaken to finish during the Defects Liability Period or complying with its obligations under Clauses 21.2 (Defects after Taking Over) or 21.7 (Contractor to Search) or complying with its obligations under any Extended Warranty. However the said Plant shall not be covered under these policies prior to its delivery to and during its unloading at the Site. Such coverage shall in the case of sub-paragraph (i) above have a limit of liability of not less than one hundred and fifteen per cent (115%) of the current replacement value of the Works and the Free Issue Items which the Contractor shall for the time being be responsible for the care of. Such coverage shall in the case of sub-paragraph (ii) above have a limit of liability of not less than one hundred and fifteen per cent (115%) of the current replacement value of the whole of the Works and the Free Issue Items. Each such limit of liability as aforesaid shall apply for any one occurrence or series of occurrences arising out of one event. Such coverage shall exclude the first part of any loss or damage not exceeding the amounts stated in the Particular Conditions. Such coverage shall in all cases include all labour charges, transportation charges, professional fees, debris removal and demolition Costs, and other Costs. The insurance shall be in the joint names of the Employer and the Contractor and with the Employer named as \"First Loss Payee\". (c) Public Liability - being coverage in respect of legal liability for loss, injury (including death) or damage to persons or property (other than property forming part of the Works and the Free Issue Items not yet taken over), including in each case claimant''s and defence costs, resulting from or occurring in connection with the carrying out of the Works and/or the remedying of defects therein and/or the Contractor''s observance or performance of or failure to observe or perform its other obligations under the Contract. Such liability shall without limitation include liability to consultants, servants, employees and agents of the Employer. Such coverage shall have a limit of liability of not less than the amount stated in the Particular Conditions for any one occurrence or series of occurrences arising out of one event and may, in respect only of the said loss or damage to property, exclude the first part of any loss or damage not exceeding the amount stated in the Particular Conditions for any one occurrence or such series thereof. The insurance shall be in the joint names of the Employer and the Contractor and with the Employer named as \"First Loss Payee\". The insurance shall include a Cross Liability Clause. (d) Workmen''s Compensation - being coverage as required by the laws and regulations in the Country in respect of personal injuries (including death and including claimant''s and defence costs) to employees of the Contractor and its Subcontractors resulting from or occurring in connection with the carrying out of the Works and/or the remedying of defects therein and/or the Contractor''s observance or performance of or failure to observe or perform its other obligations under the Contract including its Extended Warranty obligations. Such coverage shall be extended to include Employer''s Liability of not less than the statutory amount for each accident or (if no such statutory amount) not less than the amount stated in the Particular Conditions for each accident. The obligations of this paragraph (d) shall with respect to any employees of a Subcontractor be satisfied by a policy otherwise complying with this sub-paragraph but being in the name of the Subcontractor rather than of the Contractor, provided that the Employer is indemnified under the policy; but the Contractor shall be liable for procuring the compliance by the Subcontractor with the provisions of the three paragraphs immediately following paragraph (e) of this Clause 12.9. (e) Motor Vehicle Third Party and Passenger Liability Insurance being coverage in respect of death of or injury to persons and/or loss of or damage to property in respect of motor vehicles used by the Contractor in connection with the carrying out of the Works or the remedying of defects therein. The Contractor shall ensure that Subcontractors maintain such insurance in respect of motor vehicles used by them. Such insurance shall provide an unlimited indemnity for death of or injury to persons and the equivalent of not less than the amount stated in the Particular Conditions for loss of or damage to property which shall be extended as may be necessary to cover any additional cover required by statutory requirements in the Country. The Contractor shall procure that the policy, or each of the policies, of insurance referred to in this Clause 12.9 contains a waiver by the insurers of any and all rights of subrogation they might otherwise be able to exercise against the Employer or any of its consultants, servants, employees or agents. The Contractor shall procure that each of the policies of insurance referred to in paragraphs (d) and (e) of this Clause 12.9 contains a clause indemnifying the Employer or any of its consultants, servants, employees or agents as though they are the Insured. The Employer and the Project Manager shall each be entitled to a copy of the said policy or policies of insurance together with copies of either the premium receipts or certificates from the insurers confirming receipt of premiums as appropriate and from time to time upon demand to inspect the originals of the said policy or policies of insurance and to have produced to them satisfactory evidence that the same are in full force and effect. The said policy or policies shall provide that the Employer and the Project Manager are each entitled to not less than thirty (30) days'' notice from the insurers prior to cancellation, termination or alteration of cover. The Contractor shall not do or omit to do any act or thing whereby the said policy or policies may be or become void or voidable. If the Contractor shall fail to effect and keep in force any of the insurances referred to in this Clause 12.9, or any other insurance which the Contractor may be required to effect under the terms of the Contract, then and in any such case the Employer may (without prejudice to any other remedy) effect and keep in force such insurance and pay such premium or premiums as may be necessary for that purpose and from time to time deduct the amount so paid by the Employer from any monies due or which may become due to the Contractor, or recover the same as a debt due from the Contractor. The Contractor shall notify the insurers of any changes in the nature, extent or programme for the carrying out of the Works with a copy to the Employer and to the Project Manager and ensure the adequacy of the insurances at all times in accordance with the terms of the Contract. Notwithstanding the foregoing provisions it shall be a condition of each of the policies of insurance required to be taken out under the Contract that the insurer will not exercise its right to avoid the insurance cover on the grounds of any non-disclosure, mis-description or misrepresentation in any information relevant to the risks insured under such policy. If the Contractor considers that additional insurance is required either by way of wider cover or higher sums insured or higher limits of indemnity then this shall be taken out by the Contractor at its Cost. The Contractor shall indemnify the Employer against any amount which would otherwise be claimable by the Employer under any of the insurances required to be effected under this Clause 12.9 but which the Employer is unable to claim by virtue of the amount concerned falling within any deductible (Insured''s Retained Liability) applicable to the relevant insurance cover.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Without in any way limiting or detracting from the Contractor''s liabilities under Clauses 12.5 (Risk and Care), 12.6 (General Indemnity), or 12.7 (Loss, Injury or Damage to Workmen) or under any other...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":89}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 90, '{"itemType":"CLAUSE","clause_number":"12.10","clause_title":"Inherent Defects","clause_text":"The Contractor shall (unless the governing law of the Contract otherwise provides) in respect of the whole of the Works be liable for inherent defects and for its own design for a period of ten (10) years from the date of issue of the last Final Certificate issued in accordance with Clause 26.4 (Issue of Final Certificates).","general_condition":"The Contractor shall (unless the governing law of the Contract otherwise provides) in respect of the whole of the Works be liable for inherent defects and for its own design for a period of ten (10) years from the date of issue of the last Final Certificate issued in accordance with Clause 26.4 (Issue of Final Certificates).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall (unless the governing law of the Contract otherwise provides) in respect of the whole of the Works be liable for inherent defects and for its own design for a period of ten (10) y...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":90}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 91, '{"itemType":"CLAUSE","clause_number":"13.1","clause_title":"Covenant to Comply","clause_text":"The Contractor shall in the performance of the Contract comply with and shall use all reasonable endeavours to procure that its Subcontractors and its and their officers, agents, employees and workmen comply with all laws, statutes, ordinances and other laws, other provisions having the force of law, bye-laws and regulations in the Country, shall ensure that the Works as carried out comply therewith and (where applicable) shall ensure that the Works are capable of operation in compliance therewith and shall ensure that any remedying of defects in the Works is in compliance therewith. With regard to the manufacture or fabrication of any Plant outside the Country and any other work or activity pursuant to the Contract which may take place outside the Country, the Contractor shall comply with and shall use all reasonable endeavours to procure that such persons as aforesaid comply with all laws, statutes, ordinances and other laws, other provisions having the force of law, bye-laws and regulations in the place of such manufacture, fabrication, work or activity.","general_condition":"The Contractor shall in the performance of the Contract comply with and shall use all reasonable endeavours to procure that its Subcontractors and its and their officers, agents, employees and workmen comply with all laws, statutes, ordinances and other laws, other provisions having the force of law, bye-laws and regulations in the Country, shall ensure that the Works as carried out comply therewith and (where applicable) shall ensure that the Works are capable of operation in compliance therewith and shall ensure that any remedying of defects in the Works is in compliance therewith. With regard to the manufacture or fabrication of any Plant outside the Country and any other work or activity pursuant to the Contract which may take place outside the Country, the Contractor shall comply with and shall use all reasonable endeavours to procure that such persons as aforesaid comply with all laws, statutes, ordinances and other laws, other provisions having the force of law, bye-laws and regulations in the place of such manufacture, fabrication, work or activity.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall in the performance of the Contract comply with and shall use all reasonable endeavours to procure that its Subcontractors and its and their officers, agents, employees and workmen...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":91}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 92, '{"itemType":"CLAUSE","clause_number":"13.2","clause_title":"Contractor''s Indemnity","clause_text":"The Contractor shall fully and effectively indemnify the Employer, the Project Manager and the Supervision Consultant at all times hereafter against any losses, liabilities, costs, claims, actions or demands which the Employer, the Project Manager or the Supervision Consultant may incur or which may be made against the Employer, the Project Manager or the Supervision Consultant as a result of or in connection with any failure by the Contractor to observe and perform strictly the provisions of Clause 9.6 (Notices, Permits and Fees) and Clause 13.1 (Covenant to Comply).","general_condition":"The Contractor shall fully and effectively indemnify the Employer, the Project Manager and the Supervision Consultant at all times hereafter against any losses, liabilities, costs, claims, actions or demands which the Employer, the Project Manager or the Supervision Consultant may incur or which may be made against the Employer, the Project Manager or the Supervision Consultant as a result of or in connection with any failure by the Contractor to observe and perform strictly the provisions of Clause 9.6 (Notices, Permits and Fees) and Clause 13.1 (Covenant to Comply).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall fully and effectively indemnify the Employer, the Project Manager and the Supervision Consultant at all times hereafter against any losses, liabilities, costs, claims, actions or ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":92}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 93, '{"itemType":"CLAUSE","clause_number":"14.1","clause_title":"Indemnity","clause_text":"The Contractor shall fully and effectively indemnify the Employer against all claims, proceedings, royalties, fees, losses, costs and expenses which may be made against it or which it may incur as a result of or in connection with any infringement or alleged infringement or any exercise or alleged exercise of the rights protected by any patent, registered design, copyright, trade mark, trade name or other industrial or intellectual property right protected by law and arising by reason of the carrying out and completion of the Works and the remedying of defects therein or use of the Works (or any Section or Portion thereof) or use of Contractor''s Equipment. Provided that the foregoing indemnity shall not cover any use of any Plant or of the Works (or any Section or Portion thereof) otherwise than for the purpose or purposes indicated by or reasonably to be inferred from the Contract.","general_condition":"The Contractor shall fully and effectively indemnify the Employer against all claims, proceedings, royalties, fees, losses, costs and expenses which may be made against it or which it may incur as a result of or in connection with any infringement or alleged infringement or any exercise or alleged exercise of the rights protected by any patent, registered design, copyright, trade mark, trade name or other industrial or intellectual property right protected by law and arising by reason of the carrying out and completion of the Works and the remedying of defects therein or use of the Works (or any Section or Portion thereof) or use of Contractor''s Equipment. Provided that the foregoing indemnity shall not cover any use of any Plant or of the Works (or any Section or Portion thereof) otherwise than for the purpose or purposes indicated by or reasonably to be inferred from the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall fully and effectively indemnify the Employer against all claims, proceedings, royalties, fees, losses, costs and expenses which may be made against it or which it may incur as a r...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":93}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 94, '{"itemType":"CLAUSE","clause_number":"14.2","clause_title":"Claims in Respect of Patents and other Protected Rights","clause_text":"In the event of any claim being made against or any proceedings being brought against the Employer, which claim or proceedings are within the scope of the indemnity contained in Clause 14.1 (Indemnity), the Contractor shall be promptly notified in writing by the Employer and may at its own Cost conduct all negotiations for the settlement of the same and any litigation that may arise therefrom. The Employer shall not, unless and until the Contractor shall have failed to take over the conduct of the negotiations or litigation, within a reasonable time after having been so requested by the Employer, make any admission which might be prejudicial thereto. The conduct by the Contractor of such negotiations or litigation shall be conditional upon the Contractor having first given to the Employer such reasonable security as shall from time to time be required by the Employer to cover the amount ascertained or agreed or estimated, as the case may be, of any compensation, damages, losses, costs, expenses, royalties and fees which the Employer may incur or for which it may become liable. The Employer shall, at the request of the Contractor, afford all available assistance for the purpose of contesting any such claim or proceedings, and shall be repaid all reasonable Costs incurred in so doing.","general_condition":"In the event of any claim being made against or any proceedings being brought against the Employer, which claim or proceedings are within the scope of the indemnity contained in Clause 14.1 (Indemnity), the Contractor shall be promptly notified in writing by the Employer and may at its own Cost conduct all negotiations for the settlement of the same and any litigation that may arise therefrom. The Employer shall not, unless and until the Contractor shall have failed to take over the conduct of the negotiations or litigation, within a reasonable time after having been so requested by the Employer, make any admission which might be prejudicial thereto. The conduct by the Contractor of such negotiations or litigation shall be conditional upon the Contractor having first given to the Employer such reasonable security as shall from time to time be required by the Employer to cover the amount ascertained or agreed or estimated, as the case may be, of any compensation, damages, losses, costs, expenses, royalties and fees which the Employer may incur or for which it may become liable. The Employer shall, at the request of the Contractor, afford all available assistance for the purpose of contesting any such claim or proceedings, and shall be repaid all reasonable Costs incurred in so doing.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"In the event of any claim being made against or any proceedings being brought against the Employer, which claim or proceedings are within the scope of the indemnity contained in Clause 14.1 (Indemnity...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":94}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 95, '{"itemType":"CLAUSE","clause_number":"14.3","clause_title":"Payment in Respect of Plant","clause_text":"The Contractor shall bear and pay all royalties, fees, rent and other compensation and Costs payable for getting or otherwise obtaining any Plant required for or in relation to the Works and for disposing of material from demolitions and excavations and of other surplus material (whether arising from the Works or from the reparation of loss or damage thereto) save insofar as the Contract shall otherwise provide.","general_condition":"The Contractor shall bear and pay all royalties, fees, rent and other compensation and Costs payable for getting or otherwise obtaining any Plant required for or in relation to the Works and for disposing of material from demolitions and excavations and of other surplus material (whether arising from the Works or from the reparation of loss or damage thereto) save insofar as the Contract shall otherwise provide.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall bear and pay all royalties, fees, rent and other compensation and Costs payable for getting or otherwise obtaining any Plant required for or in relation to the Works and for dispo...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":95}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 96, '{"itemType":"CLAUSE","clause_number":"15.1","clause_title":"General Covenant","clause_text":"Save with the specific prior written consent of the Employer, the Contractor shall not, and shall use its best endeavours to procure that its Subcontractors and its and their employees and agents do not, at any time hereafter whether before or after completion of the Works or termination of the Contract, use or disclose to any person whomsoever or whatsoever: (a) any drawings, documents or other information, whether written or oral, which may be communicated to it or him or of which it or he may otherwise become possessed or aware as a result of or in connection with the Contract or the Works; or (b) in particular (but without prejudice to the generality of paragraph (a) above) any information, whether written or oral, relating to the Contract Sum, the Bills of Quantities or any other pricing or payment matter under the Contract; or (c) any other information, whether written or oral, relating to the business, secrets, dealings, transactions or affairs of the Employer. Provided that the foregoing covenant shall not apply to any information which the Contractor can conclusively demonstrate to the satisfaction of the Project Manager is already generally available and in the public domain nor shall it apply if and insofar as but only insofar as any such use or disclosure may be requisite for the performance by the Contractor of its obligations under the Contract or by any of its Subcontractors of their obligations under their respective subcontracts; but this Proviso shall never be deemed to authorise the disclosure of information within paragraph (b) above save to the Employer itself, the Project Manager, or the Contractor''s own professional advisers and accordingly (save in any such case) the specific prior written consent of the Employer will be required.","general_condition":"Save with the specific prior written consent of the Employer, the Contractor shall not, and shall use its best endeavours to procure that its Subcontractors and its and their employees and agents do not, at any time hereafter whether before or after completion of the Works or termination of the Contract, use or disclose to any person whomsoever or whatsoever: (a) any drawings, documents or other information, whether written or oral, which may be communicated to it or him or of which it or he may otherwise become possessed or aware as a result of or in connection with the Contract or the Works; or (b) in particular (but without prejudice to the generality of paragraph (a) above) any information, whether written or oral, relating to the Contract Sum, the Bills of Quantities or any other pricing or payment matter under the Contract; or (c) any other information, whether written or oral, relating to the business, secrets, dealings, transactions or affairs of the Employer. Provided that the foregoing covenant shall not apply to any information which the Contractor can conclusively demonstrate to the satisfaction of the Project Manager is already generally available and in the public domain nor shall it apply if and insofar as but only insofar as any such use or disclosure may be requisite for the performance by the Contractor of its obligations under the Contract or by any of its Subcontractors of their obligations under their respective subcontracts; but this Proviso shall never be deemed to authorise the disclosure of information within paragraph (b) above save to the Employer itself, the Project Manager, or the Contractor''s own professional advisers and accordingly (save in any such case) the specific prior written consent of the Employer will be required.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Save with the specific prior written consent of the Employer, the Contractor shall not, and shall use its best endeavours to procure that its Subcontractors and its and their employees and agents do n...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":96}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 97, '{"itemType":"CLAUSE","clause_number":"15.2","clause_title":"No Waiver of Rights","clause_text":"Any written consent which the Employer may give for the purpose of Clause 15.1 (General Covenant) shall be confined to the particular use or disclosure and the particular occasion which it specifies.","general_condition":"Any written consent which the Employer may give for the purpose of Clause 15.1 (General Covenant) shall be confined to the particular use or disclosure and the particular occasion which it specifies.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Any written consent which the Employer may give for the purpose of Clause 15.1 (General Covenant) shall be confined to the particular use or disclosure and the particular occasion which it specifies.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":97}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 98, '{"itemType":"CLAUSE","clause_number":"16.1","clause_title":"Interference with Convenience","clause_text":"All operations for or in connection with the Works shall, so far as compliance with the requirements of the Contract permits, be carried on so as not to interfere with the convenience of the public, or the access to or use or occupation of public or private roads or footpaths or to or of properties whether in the possession of the Employer or of any other person. The Contractor shall fully and effectually indemnify the Employer against any losses, liabilities, costs, claims, actions or demands which it may incur or which may be made against it as a result of or in connection with any failure by the Contractor to honour the terms of the preceding sentence of this Clause 16.1.","general_condition":"All operations for or in connection with the Works shall, so far as compliance with the requirements of the Contract permits, be carried on so as not to interfere with the convenience of the public, or the access to or use or occupation of public or private roads or footpaths or to or of properties whether in the possession of the Employer or of any other person. The Contractor shall fully and effectually indemnify the Employer against any losses, liabilities, costs, claims, actions or demands which it may incur or which may be made against it as a result of or in connection with any failure by the Contractor to honour the terms of the preceding sentence of this Clause 16.1.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"All operations for or in connection with the Works shall, so far as compliance with the requirements of the Contract permits, be carried on so as not to interfere with the convenience of the public, o...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":98}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 99, '{"itemType":"CLAUSE","clause_number":"16.2","clause_title":"Damage to Roads or Bridges","clause_text":"(a) The Contractor shall use every reasonable means to prevent any of the roads or bridges connecting with or on the routes to the Site from being damaged or injured by any traffic for or in respect of the Works and, in particular, shall select routes, choose and use vehicles and restrict and distribute loads so that any extraordinary traffic which will inevitably arise from the moving of Plant or Contractor''s Equipment from and to the Site shall be limited as far as possible, and so that no unnecessary damage or injury may be occasioned to such roads or bridges. (b) The Contractor shall be responsible for and shall pay the Cost of strengthening any bridges or altering or improving any road connecting with or on the routes to the Site to facilitate the movement of Plant or Contractor''s Equipment. Whenever the Contractor receives a claim for damage or injury to any such road or bridge it shall immediately report the same to the Project Manager in writing. The Contractor shall indemnify and keep indemnified the Employer against all claims for damage or injury to any such road or bridge caused by or arising from such movement, including such claims as may be made directly against the Employer, and shall negotiate and pay all claims, proceedings, damages, costs and charges in relation thereto.","general_condition":"(a) The Contractor shall use every reasonable means to prevent any of the roads or bridges connecting with or on the routes to the Site from being damaged or injured by any traffic for or in respect of the Works and, in particular, shall select routes, choose and use vehicles and restrict and distribute loads so that any extraordinary traffic which will inevitably arise from the moving of Plant or Contractor''s Equipment from and to the Site shall be limited as far as possible, and so that no unnecessary damage or injury may be occasioned to such roads or bridges. (b) The Contractor shall be responsible for and shall pay the Cost of strengthening any bridges or altering or improving any road connecting with or on the routes to the Site to facilitate the movement of Plant or Contractor''s Equipment. Whenever the Contractor receives a claim for damage or injury to any such road or bridge it shall immediately report the same to the Project Manager in writing. The Contractor shall indemnify and keep indemnified the Employer against all claims for damage or injury to any such road or bridge caused by or arising from such movement, including such claims as may be made directly against the Employer, and shall negotiate and pay all claims, proceedings, damages, costs and charges in relation thereto.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) The Contractor shall use every reasonable means to prevent any of the roads or bridges connecting with or on the routes to the Site from being damaged or injured by any traffic for or in respect o...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":99}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 100, '{"itemType":"CLAUSE","clause_number":"16.3","clause_title":"Waterborne Traffic","clause_text":"Where the Contractor uses waterborne transport for any Plant or Contractor''s Equipment the provisions of Clause 16.2 (Damage to Roads or Bridges) shall be construed as though \"road\" included a lock, dock sea wall or other structure related to a waterway and \"vehicle\" included craft, and shall have effect accordingly.","general_condition":"Where the Contractor uses waterborne transport for any Plant or Contractor''s Equipment the provisions of Clause 16.2 (Damage to Roads or Bridges) shall be construed as though \"road\" included a lock, dock sea wall or other structure related to a waterway and \"vehicle\" included craft, and shall have effect accordingly.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Where the Contractor uses waterborne transport for any Plant or Contractor''s Equipment the provisions of Clause 16.2 (Damage to Roads or Bridges) shall be construed as though \"road\" included a lock, d...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":100}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 101, '{"itemType":"CLAUSE","clause_number":"16.4","clause_title":"Access Routes","clause_text":"The Contractor shall be deemed to have satisfied itself prior to submitting its Tender as to the suitability and availability of access routes to the Site. Subject to any provision to the contrary contained in the Contract: (a) the Contractor shall (as between the Employer and the Contractor) be responsible for and shall pay the Cost of any maintenance which may be required for its use of access routes; (b) the Contractor shall at its own Cost provide all necessary signs or directions along access routes and shall obtain any permission which may be required from the relevant authorities for its use of routes, signs and directions; (c) the Employer shall not be responsible for any claims which may arise from the use or otherwise by the Contractor of any access route; (d) the Employer does not guarantee the suitability or availability of particular access routes; and (e) Costs incurred due to the non-suitability or non-availability of access routes shall be borne by the Contractor.","general_condition":"The Contractor shall be deemed to have satisfied itself prior to submitting its Tender as to the suitability and availability of access routes to the Site. Subject to any provision to the contrary contained in the Contract: (a) the Contractor shall (as between the Employer and the Contractor) be responsible for and shall pay the Cost of any maintenance which may be required for its use of access routes; (b) the Contractor shall at its own Cost provide all necessary signs or directions along access routes and shall obtain any permission which may be required from the relevant authorities for its use of routes, signs and directions; (c) the Employer shall not be responsible for any claims which may arise from the use or otherwise by the Contractor of any access route; (d) the Employer does not guarantee the suitability or availability of particular access routes; and (e) Costs incurred due to the non-suitability or non-availability of access routes shall be borne by the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall be deemed to have satisfied itself prior to submitting its Tender as to the suitability and availability of access routes to the Site. Subject to any provision to the contrary con...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":101}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 102, '{"itemType":"CLAUSE","clause_number":"17.1","clause_title":"Commencement","clause_text":"The Contractor shall commence the Works not later than seven (7) days after the Commencement Date and thereafter shall proceed with the same with due expedition and without delay.","general_condition":"The Contractor shall commence the Works not later than seven (7) days after the Commencement Date and thereafter shall proceed with the same with due expedition and without delay.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall commence the Works not later than seven (7) days after the Commencement Date and thereafter shall proceed with the same with due expedition and without delay.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":102}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 103, '{"itemType":"CLAUSE","clause_number":"17.2","clause_title":"General Descriptions","clause_text":"The Contractor shall whenever required by the Project Manager or the Supervision Consultant prepare in writing and submit to the Project Manager for approval by the Supervision Consultant a general description of the arrangements and methods which the Contractor proposes to adopt for the carrying out of the Works or any part thereof and the remedying of defects therein. After submission to the Project Manager and approval by the Supervision Consultant of any such general description the Contractor shall adhere to the sequence, arrangements and methods stated therein unless it obtains the prior written permission of the Supervision Consultant to vary the same (the request for such written permission having been submitted in writing to the Project Manager with a copy to the Supervision Consultant) but such permission shall not be unreasonably withheld.","general_condition":"The Contractor shall whenever required by the Project Manager or the Supervision Consultant prepare in writing and submit to the Project Manager for approval by the Supervision Consultant a general description of the arrangements and methods which the Contractor proposes to adopt for the carrying out of the Works or any part thereof and the remedying of defects therein. After submission to the Project Manager and approval by the Supervision Consultant of any such general description the Contractor shall adhere to the sequence, arrangements and methods stated therein unless it obtains the prior written permission of the Supervision Consultant to vary the same (the request for such written permission having been submitted in writing to the Project Manager with a copy to the Supervision Consultant) but such permission shall not be unreasonably withheld.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall whenever required by the Project Manager or the Supervision Consultant prepare in writing and submit to the Project Manager for approval by the Supervision Consultant a general de...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":103}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 104, '{"itemType":"CLAUSE","clause_number":"17.3","clause_title":"Programme","clause_text":"The Contractor shall within the period after the Commencement Date stated in the Particular Conditions prepare and submit to the Project Manager for his approval a programme for the carrying out of the Works in accordance with the requirements of the Specification and any further requirements of the Project Manager.","general_condition":"The Contractor shall within the period after the Commencement Date stated in the Particular Conditions prepare and submit to the Project Manager for his approval a programme for the carrying out of the Works in accordance with the requirements of the Specification and any further requirements of the Project Manager.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall within the period after the Commencement Date stated in the Particular Conditions prepare and submit to the Project Manager for his approval a programme for the carrying out of th...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":104}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 105, '{"itemType":"CLAUSE","clause_number":"17.4","clause_title":"Revision of Programme","clause_text":"If at any time it should appear that the actual progress of the Works does not conform to the Programme, the Contractor shall, at the request of the Project Manager and within the period required by the Project Manager, prepare and submit to the Project Manager for his approval a revised programme showing the modifications to the Programme necessary to ensure completion of the whole of the Works and each Section and Portion of the Works within the applicable Time for Completion.","general_condition":"If at any time it should appear that the actual progress of the Works does not conform to the Programme, the Contractor shall, at the request of the Project Manager and within the period required by the Project Manager, prepare and submit to the Project Manager for his approval a revised programme showing the modifications to the Programme necessary to ensure completion of the whole of the Works and each Section and Portion of the Works within the applicable Time for Completion.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If at any time it should appear that the actual progress of the Works does not conform to the Programme, the Contractor shall, at the request of the Project Manager and within the period required by t...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":105}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 106, '{"itemType":"CLAUSE","clause_number":"17.5","clause_title":"No Relief","clause_text":"The submission to the Project Manager and approval by the Supervision Consultant of the general description referred to in Clause 17.2 (General Descriptions) and the submission to and approval by the Project Manager of the programmes referred to in Clauses 17.3 (Programme) and 17.4 (Revision of Programme) shall not relieve the Contractor of any of its duties or responsibilities under the Contract.","general_condition":"The submission to the Project Manager and approval by the Supervision Consultant of the general description referred to in Clause 17.2 (General Descriptions) and the submission to and approval by the Project Manager of the programmes referred to in Clauses 17.3 (Programme) and 17.4 (Revision of Programme) shall not relieve the Contractor of any of its duties or responsibilities under the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The submission to the Project Manager and approval by the Supervision Consultant of the general description referred to in Clause 17.2 (General Descriptions) and the submission to and approval by the ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":106}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 107, '{"itemType":"CLAUSE","clause_number":"17.6","clause_title":"Progress Reports","clause_text":"The Contractor shall within the period after the end of each month stated in the Particular Conditions provide to the Project Manager a Progress Report in accordance with the Specification and in such form and containing such information as the Project Manager may require concerning the progress of the Works up to the end of the said month.","general_condition":"The Contractor shall within the period after the end of each month stated in the Particular Conditions provide to the Project Manager a Progress Report in accordance with the Specification and in such form and containing such information as the Project Manager may require concerning the progress of the Works up to the end of the said month.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall within the period after the end of each month stated in the Particular Conditions provide to the Project Manager a Progress Report in accordance with the Specification and in such...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":107}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 108, '{"itemType":"CLAUSE","clause_number":"17.7","clause_title":"Rate of Progress","clause_text":"If for any reason which does not entitle the Contractor to an extension of time in accordance with Clause 19.2 (Extensions of Time), the rate of progress of the Works or any Section or Portion thereof is at any time, in the opinion of the Project Manager, too slow to ensure completion within the applicable Time for Completion the Project Manager shall so notify the Contractor in writing and the Contractor shall thereupon take such steps as are necessary, subject to the approval of the Project Manager, to expedite progress so as to complete the Works or such Section or Portion thereof within the applicable Time for Completion. The Contractor shall not be entitled to any additional payment for taking such steps. Provided that if any steps taken by the Contractor in meeting its obligations under this Clause involves the Employer in additional Costs, such Costs after due consultation with the Employer and the Contractor shall be determined by the Project Manager and the Employer may deduct the Costs so incurred from any monies due or which may become due to the Contractor or recover the same from the Contractor as a debt due from the Contractor. The Project Manager shall notify the Contractor accordingly with a copy to the Employer.","general_condition":"If for any reason which does not entitle the Contractor to an extension of time in accordance with Clause 19.2 (Extensions of Time), the rate of progress of the Works or any Section or Portion thereof is at any time, in the opinion of the Project Manager, too slow to ensure completion within the applicable Time for Completion the Project Manager shall so notify the Contractor in writing and the Contractor shall thereupon take such steps as are necessary, subject to the approval of the Project Manager, to expedite progress so as to complete the Works or such Section or Portion thereof within the applicable Time for Completion. The Contractor shall not be entitled to any additional payment for taking such steps. Provided that if any steps taken by the Contractor in meeting its obligations under this Clause involves the Employer in additional Costs, such Costs after due consultation with the Employer and the Contractor shall be determined by the Project Manager and the Employer may deduct the Costs so incurred from any monies due or which may become due to the Contractor or recover the same from the Contractor as a debt due from the Contractor. The Project Manager shall notify the Contractor accordingly with a copy to the Employer.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If for any reason which does not entitle the Contractor to an extension of time in accordance with Clause 19.2 (Extensions of Time), the rate of progress of the Works or any Section or Portion thereof...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":108}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 109, '{"itemType":"CLAUSE","clause_number":"17.8","clause_title":"Site Working Hours","clause_text":"The Contractor shall comply with the provisions relating to Site working hours contained in the Particular Conditions.","general_condition":"The Contractor shall comply with the provisions relating to Site working hours contained in the Particular Conditions.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall comply with the provisions relating to Site working hours contained in the Particular Conditions.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":109}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 110, '{"itemType":"CLAUSE","clause_number":"18.1","clause_title":"Suspension Order","clause_text":"The Contractor shall, on the written order of the Project Manager stating the date of suspension and bearing the written consent of the Employer (herein referred to as a \"Suspension Order\"), suspend the progress of the Works or any part thereof for such time or times and in such manner as the Project Manager may consider necessary and shall during such suspension properly protect, store and secure the Works or such part thereof against damage to the satisfaction of the Project Manager. The extra Cost incurred by the Contractor in giving effect to the Project Manager''s instructions under this Clause shall be borne and paid by the Employer unless such suspension is: (b) necessary by reason of some default of or breach of contract by the Contractor, or (c) necessary by reason of an occurrence of the forces of nature which an experienced contractor should have foreseen and provided for or insured against (for the purposes of this Sub-Clause (c) rainfall in any quantity shall be deemed to be such an occurrence), or (d) necessary for the proper carrying out of the Works or for the safety of the Works or any part thereof insofar as such necessity does not arise from any act or default by the Project Manager, the Supervision Consultant or the Employer or from any of the excepted risks (as defined in Clause 12.5 (Risk and Care)). Provided that the Contractor shall not be entitled to claim recovery of any such extra Cost unless, within twenty eight (28) days after receipt of the Suspension Order, it gives to the Project Manager written notice of its intention to make such claim. If such notice is duly given the extra Cost recoverable by the Contractor shall be determined by the Project Manager and paid to the Contractor in accordance with Clause 26 (Interim and Final Certificates).","general_condition":"The Contractor shall, on the written order of the Project Manager stating the date of suspension and bearing the written consent of the Employer (herein referred to as a \"Suspension Order\"), suspend the progress of the Works or any part thereof for such time or times and in such manner as the Project Manager may consider necessary and shall during such suspension properly protect, store and secure the Works or such part thereof against damage to the satisfaction of the Project Manager. The extra Cost incurred by the Contractor in giving effect to the Project Manager''s instructions under this Clause shall be borne and paid by the Employer unless such suspension is: (b) necessary by reason of some default of or breach of contract by the Contractor, or (c) necessary by reason of an occurrence of the forces of nature which an experienced contractor should have foreseen and provided for or insured against (for the purposes of this Sub-Clause (c) rainfall in any quantity shall be deemed to be such an occurrence), or (d) necessary for the proper carrying out of the Works or for the safety of the Works or any part thereof insofar as such necessity does not arise from any act or default by the Project Manager, the Supervision Consultant or the Employer or from any of the excepted risks (as defined in Clause 12.5 (Risk and Care)). Provided that the Contractor shall not be entitled to claim recovery of any such extra Cost unless, within twenty eight (28) days after receipt of the Suspension Order, it gives to the Project Manager written notice of its intention to make such claim. If such notice is duly given the extra Cost recoverable by the Contractor shall be determined by the Project Manager and paid to the Contractor in accordance with Clause 26 (Interim and Final Certificates).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall, on the written order of the Project Manager stating the date of suspension and bearing the written consent of the Employer (herein referred to as a \"Suspension Order\"), suspend t...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":110}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 111, '{"itemType":"CLAUSE","clause_number":"18.2","clause_title":"Suspension Exceeding 90 Days","clause_text":"If the Works or any part thereof is the subject of a Suspension Order and if written permission (bearing the written consent of the Employer) to resume work is not given by the Project Manager within a period of ninety (90) days from the date of suspension then, unless such suspension is within any of paragraphs (a) to (d) inclusive of Clause 18.1 (Suspension Order), the Contractor may serve a written notice on the Project Manager requiring permission within twenty eight (28) days from the receipt thereof to proceed with the Works or that part thereof the subject of the Suspension Order and, if such permission is not within that time granted by the Project Manager in writing and bearing the written consent of the Employer, the Contractor by a further written notice so served may, but is not bound to, elect to treat the suspension where it affects part only of the Works as an omission of such part pursuant to a Variation Order under Clause 22.1 (Variation Orders), or, where it affects the whole of the Works, as a termination of the Contract by the Employer for its convenience under Clause 31.1 (Termination for Convenience) thereby giving the Contractor the rights conferred by Clause 31.3 (Payment on Termination).","general_condition":"If the Works or any part thereof is the subject of a Suspension Order and if written permission (bearing the written consent of the Employer) to resume work is not given by the Project Manager within a period of ninety (90) days from the date of suspension then, unless such suspension is within any of paragraphs (a) to (d) inclusive of Clause 18.1 (Suspension Order), the Contractor may serve a written notice on the Project Manager requiring permission within twenty eight (28) days from the receipt thereof to proceed with the Works or that part thereof the subject of the Suspension Order and, if such permission is not within that time granted by the Project Manager in writing and bearing the written consent of the Employer, the Contractor by a further written notice so served may, but is not bound to, elect to treat the suspension where it affects part only of the Works as an omission of such part pursuant to a Variation Order under Clause 22.1 (Variation Orders), or, where it affects the whole of the Works, as a termination of the Contract by the Employer for its convenience under Clause 31.1 (Termination for Convenience) thereby giving the Contractor the rights conferred by Clause 31.3 (Payment on Termination).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If the Works or any part thereof is the subject of a Suspension Order and if written permission (bearing the written consent of the Employer) to resume work is not given by the Project Manager within ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":111}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 112, '{"itemType":"CLAUSE","clause_number":"19.1","clause_title":"Time for Completion","clause_text":"The Contractor acknowledges that time is of the essence. The Contractor shall complete the whole of the Works and each Section and Portion of the Works within the applicable Time for Completion in accordance with the provisions of Clause 20.1 (Taking Over Certificate). Provided that in the circumstances set out in Clause 19.2 (Extensions of Time) the Employer shall extend any such Time for Completion in accordance with the provisions of that Clause.","general_condition":"The Contractor acknowledges that time is of the essence. The Contractor shall complete the whole of the Works and each Section and Portion of the Works within the applicable Time for Completion in accordance with the provisions of Clause 20.1 (Taking Over Certificate). Provided that in the circumstances set out in Clause 19.2 (Extensions of Time) the Employer shall extend any such Time for Completion in accordance with the provisions of that Clause.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor acknowledges that time is of the essence. The Contractor shall complete the whole of the Works and each Section and Portion of the Works within the applicable Time for Completion in acc...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":112}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 113, '{"itemType":"CLAUSE","clause_number":"19.2","clause_title":"Extensions of Time","clause_text":"After due consultation with the Project Manager, the Supervision Consultant and the Contractor the Employer shall grant and notify to the Contractor, the Project Manager and the Supervision Consultant such extension, if any, of the Time for Completion of the whole of the Works or of any Section or Portion of the Works as may in his opinion be reasonable in respect of such part of any delay in completing the whole of the Works or any such Section or Portion of the Works as is caused solely by any of the following events or circumstances, namely: (a) any variation of the Works made pursuant to a Variation Order issued under Clause 22.1 (Variation Orders) but in evaluating the reasonableness of the period of any extension required as a result of any such variation account shall be taken of any other variation which has caused a decrease in the Works or has otherwise reduced the time the Contractor should reasonably need for completion thereof; or (b) any Suspension Order under Clause 18.1 (Suspension Order) except in relation to a suspension within any of paragraphs (a) to (d) inclusive of Clause 18.1; or (c) any of the excepted risks as defined in Clause 12.5 (Risk and Care); or (d) any other event or circumstance (not otherwise provided for in the Contract) for which the Employer, the Project Manager, the Supervision Consultant or some other contractor employed by the Employer is responsible, for which the Contractor is not directly or indirectly responsible, and which should not have been foreseen and prevented by the Contractor and which is beyond the reasonable control of the Contractor. (1) the Contractor shall not under any circumstances be entitled to any such extension of the Time for Completion of the whole of the Works or of any Section or Portion of the Works and the Employer shall be discharged from any and all liability in connection therewith unless as soon as possible and in any event within twenty eight (28) days after receipt of a Variation Order or a Suspension Order or of the occurrence of a relevant excepted risk or of any other event or circumstance beyond the reasonable control of the Contractor (as the case may be) it gives written notice to the Project Manager (with a copy to the Employer) with full and detailed particulars of any extension of time to which it may consider itself entitled together with any other notice required by the Contract and relevant to such event or circumstance; and (2) it shall be the duty of the Contractor at all times to use all reasonable endeavours to prevent any delay being caused by any of the events or circumstances mentioned in paragraphs (a) to (d) inclusive of this Clause, to minimise any such delay as may be caused thereby, and to do all that may be reasonably required, to the satisfaction of the Project Manager and the Supervision Consultant, to proceed with the Works.","general_condition":"After due consultation with the Project Manager, the Supervision Consultant and the Contractor the Employer shall grant and notify to the Contractor, the Project Manager and the Supervision Consultant such extension, if any, of the Time for Completion of the whole of the Works or of any Section or Portion of the Works as may in his opinion be reasonable in respect of such part of any delay in completing the whole of the Works or any such Section or Portion of the Works as is caused solely by any of the following events or circumstances, namely: (a) any variation of the Works made pursuant to a Variation Order issued under Clause 22.1 (Variation Orders) but in evaluating the reasonableness of the period of any extension required as a result of any such variation account shall be taken of any other variation which has caused a decrease in the Works or has otherwise reduced the time the Contractor should reasonably need for completion thereof; or (b) any Suspension Order under Clause 18.1 (Suspension Order) except in relation to a suspension within any of paragraphs (a) to (d) inclusive of Clause 18.1; or (c) any of the excepted risks as defined in Clause 12.5 (Risk and Care); or (d) any other event or circumstance (not otherwise provided for in the Contract) for which the Employer, the Project Manager, the Supervision Consultant or some other contractor employed by the Employer is responsible, for which the Contractor is not directly or indirectly responsible, and which should not have been foreseen and prevented by the Contractor and which is beyond the reasonable control of the Contractor. (1) the Contractor shall not under any circumstances be entitled to any such extension of the Time for Completion of the whole of the Works or of any Section or Portion of the Works and the Employer shall be discharged from any and all liability in connection therewith unless as soon as possible and in any event within twenty eight (28) days after receipt of a Variation Order or a Suspension Order or of the occurrence of a relevant excepted risk or of any other event or circumstance beyond the reasonable control of the Contractor (as the case may be) it gives written notice to the Project Manager (with a copy to the Employer) with full and detailed particulars of any extension of time to which it may consider itself entitled together with any other notice required by the Contract and relevant to such event or circumstance; and (2) it shall be the duty of the Contractor at all times to use all reasonable endeavours to prevent any delay being caused by any of the events or circumstances mentioned in paragraphs (a) to (d) inclusive of this Clause, to minimise any such delay as may be caused thereby, and to do all that may be reasonably required, to the satisfaction of the Project Manager and the Supervision Consultant, to proceed with the Works.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"After due consultation with the Project Manager, the Supervision Consultant and the Contractor the Employer shall grant and notify to the Contractor, the Project Manager and the Supervision Consultant...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":113}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 114, '{"itemType":"CLAUSE","clause_number":"19.3","clause_title":"Liquidated Damages","clause_text":"(a) (i) Without prejudice to any other rights or remedies available to the Employer in respect of such failure and subject to Sub-Clause (a)(iii) below if the Contractor shall fail to complete any Section or Portion of the Works within its Time for Completion, the Contractor shall pay to the Employer for each day or part of a day of the delay as liquidated damages: (1) in respect of a delay in the completion of any Section of the Works, the applicable amount stated in the Particular Conditions; and (2) in respect of a delay in the completion of any Portion of the Works, the amount calculated by multiplying the amount of liquidated damages per day stated in the Particular Conditions for that Section of the Works of which the said Portion was previously a part by the percentage that the finally determined Contract value of the said Portion is of the finally determined Contract value of the said Section. For this purpose a Section or Portion of the Works shall be deemed to have been completed on the date of its taking over as stated in its Taking-Over Certificate. (ii) Payments shall be made at the end of each week or part of a week of such delay. In the case of such payments to be made in respect of Portions of the Works, the aforesaid Contract values shall be as then determined by the Project Manager and the balance due, if any, shall be paid forthwith upon final determination of such Contract values by the Project Manager. (iii) Subject to the Employer''s right to claim any further costs or damages under the Contract the aggregate amount of all such payments as are made under Sub-Clause (a)(i) above shall not exceed the amount stated in the Particular Conditions. (b) Any such payments as are referred to in Sub-Clause (a)(i) of this Clause 19.3 shall constitute genuine attempts by the parties to pre-estimate the damage or loss which would be suffered by the Employer, but shall be made irrespective of and without need to prove any damage or loss which may actually be so suffered. (c) The Employer may at any time without prior notice to the Contractor recover any unpaid amount of any such payment due from the Contractor under Sub-Clause (a)(i) above by deduction from any monies due or which may become due to the Contractor or recover the same as a debt due from the Contractor. (d) No such payments as are referred to in Sub-Clause (a)(i) above and no such deduction or recovery as is referred to in Sub-Clause (c) above shall release the Contractor from its obligations to complete the whole of the Works (including, without limitation, the relevant Section or Portion of the Works) or from any of its other obligations or liabilities under the Contract.","general_condition":"(a) (i) Without prejudice to any other rights or remedies available to the Employer in respect of such failure and subject to Sub-Clause (a)(iii) below if the Contractor shall fail to complete any Section or Portion of the Works within its Time for Completion, the Contractor shall pay to the Employer for each day or part of a day of the delay as liquidated damages: (1) in respect of a delay in the completion of any Section of the Works, the applicable amount stated in the Particular Conditions; and (2) in respect of a delay in the completion of any Portion of the Works, the amount calculated by multiplying the amount of liquidated damages per day stated in the Particular Conditions for that Section of the Works of which the said Portion was previously a part by the percentage that the finally determined Contract value of the said Portion is of the finally determined Contract value of the said Section. For this purpose a Section or Portion of the Works shall be deemed to have been completed on the date of its taking over as stated in its Taking-Over Certificate. (ii) Payments shall be made at the end of each week or part of a week of such delay. In the case of such payments to be made in respect of Portions of the Works, the aforesaid Contract values shall be as then determined by the Project Manager and the balance due, if any, shall be paid forthwith upon final determination of such Contract values by the Project Manager. (iii) Subject to the Employer''s right to claim any further costs or damages under the Contract the aggregate amount of all such payments as are made under Sub-Clause (a)(i) above shall not exceed the amount stated in the Particular Conditions. (b) Any such payments as are referred to in Sub-Clause (a)(i) of this Clause 19.3 shall constitute genuine attempts by the parties to pre-estimate the damage or loss which would be suffered by the Employer, but shall be made irrespective of and without need to prove any damage or loss which may actually be so suffered. (c) The Employer may at any time without prior notice to the Contractor recover any unpaid amount of any such payment due from the Contractor under Sub-Clause (a)(i) above by deduction from any monies due or which may become due to the Contractor or recover the same as a debt due from the Contractor. (d) No such payments as are referred to in Sub-Clause (a)(i) above and no such deduction or recovery as is referred to in Sub-Clause (c) above shall release the Contractor from its obligations to complete the whole of the Works (including, without limitation, the relevant Section or Portion of the Works) or from any of its other obligations or liabilities under the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) (i) Without prejudice to any other rights or remedies available to the Employer in respect of such failure and subject to Sub-Clause (a)(iii) below if the Contractor shall fail to complete any Sec...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":114}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 115, '{"itemType":"CLAUSE","clause_number":"20.1","clause_title":"Taking-Over Certificate","clause_text":"When: (a) any Section or Portion of the Works has been completed in accordance with the Contract, except for any minor outstanding work and defects which will not affect the use of that Section or Portion of the Works for its intended purpose (either until or whilst such work is completed and such defects are remedied); and (b) all Tests on Completion in respect of that Section or Portion of the Works have been duly passed to the satisfaction of the Supervision Consultant; and (c) paragraph (a) of Clause 5.7 (As-built Drawings, Operations and Maintenance Manuals) has been fully complied with in respect of that Section or Portion of the Works; and (d) all the other conditions precedent to the issue of a Taking-Over Certificate in respect of that Section or Portion of the Works as required by the Contract have been satisfied; the Contractor shall give notice in writing to the Project Manager, (with a copy to the Supervision Consultant and to the Employer), that the aforesaid Section or Portion of the Works is ready for taking over, such notice being accompanied by a written undertaking to complete any minor outstanding work and remedy any minor outstanding defects as mentioned above as soon as practicable during the Defects Liability Period. The Supervision Consultant shall, within twenty eight (28) days after the date of delivery of such notice and undertaking, either issue to the Contractor, with a copy to the Employer and the Project Manager, a certificate (herein called a \"Taking-Over Certificate\") stating the date on which, in his opinion, paragraphs (a) to (d) inclusive of this Clause have been complied with in respect of that Section or Portion of the Works which date shall become the date of its taking over, or give written notice to the Contractor specifying in his opinion all the work which requires to be done and/or defects which require to be remedied and/or Tests on Completion which require to be duly passed and/or items within paragraph (c) which remain to be fully complied with and/or other conditions precedent which remain to be satisfied before the issue of such Taking-Over Certificate. Once all such work has been done and/or defects remedied and/or Tests on Completion duly passed and/or items fully complied with and/or other conditions precedent satisfied, the Contractor shall repeat delivery of the aforesaid notice and undertaking. It shall be a condition precedent to the issue of a Taking-Over Certificate for any Section or Portion of the Works that the Employer as the end-user, is satisfied that the said Section or Portion of the Works is ready to be taken into use by the Employer and has notified the Supervision Consultant (with a copy to the Project Manager) in writing accordingly. No Taking-Over Certificate in respect of a Section or Portion of the Works (other than the last Taking-Over Certificate) shall unless it specifically states to the contrary be deemed to certify completion of any ground or other surfaces requiring treatment or reinstatement.","general_condition":"When: (a) any Section or Portion of the Works has been completed in accordance with the Contract, except for any minor outstanding work and defects which will not affect the use of that Section or Portion of the Works for its intended purpose (either until or whilst such work is completed and such defects are remedied); and (b) all Tests on Completion in respect of that Section or Portion of the Works have been duly passed to the satisfaction of the Supervision Consultant; and (c) paragraph (a) of Clause 5.7 (As-built Drawings, Operations and Maintenance Manuals) has been fully complied with in respect of that Section or Portion of the Works; and (d) all the other conditions precedent to the issue of a Taking-Over Certificate in respect of that Section or Portion of the Works as required by the Contract have been satisfied; the Contractor shall give notice in writing to the Project Manager, (with a copy to the Supervision Consultant and to the Employer), that the aforesaid Section or Portion of the Works is ready for taking over, such notice being accompanied by a written undertaking to complete any minor outstanding work and remedy any minor outstanding defects as mentioned above as soon as practicable during the Defects Liability Period. The Supervision Consultant shall, within twenty eight (28) days after the date of delivery of such notice and undertaking, either issue to the Contractor, with a copy to the Employer and the Project Manager, a certificate (herein called a \"Taking-Over Certificate\") stating the date on which, in his opinion, paragraphs (a) to (d) inclusive of this Clause have been complied with in respect of that Section or Portion of the Works which date shall become the date of its taking over, or give written notice to the Contractor specifying in his opinion all the work which requires to be done and/or defects which require to be remedied and/or Tests on Completion which require to be duly passed and/or items within paragraph (c) which remain to be fully complied with and/or other conditions precedent which remain to be satisfied before the issue of such Taking-Over Certificate. Once all such work has been done and/or defects remedied and/or Tests on Completion duly passed and/or items fully complied with and/or other conditions precedent satisfied, the Contractor shall repeat delivery of the aforesaid notice and undertaking. It shall be a condition precedent to the issue of a Taking-Over Certificate for any Section or Portion of the Works that the Employer as the end-user, is satisfied that the said Section or Portion of the Works is ready to be taken into use by the Employer and has notified the Supervision Consultant (with a copy to the Project Manager) in writing accordingly. No Taking-Over Certificate in respect of a Section or Portion of the Works (other than the last Taking-Over Certificate) shall unless it specifically states to the contrary be deemed to certify completion of any ground or other surfaces requiring treatment or reinstatement.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"When: (a) any Section or Portion of the Works has been completed in accordance with the Contract, except for any minor outstanding work and defects which will not affect the use of that Section or Por...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":115}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 116, '{"itemType":"CLAUSE","clause_number":"20.2","clause_title":"Use before Taking Over","clause_text":"It may be necessary for the Employer to use a part or parts of any Section or Portion of the Works before issuance of the applicable Taking-Over Certificate. In such instance the Contractor shall, for all purposes, continue to have the risk in and to take full responsibility for the care of such part or parts of any Section or Portion of the Works and all outstanding work to be done in respect of the Works prior to the issue of the relevant Taking-Over Certificate.","general_condition":"It may be necessary for the Employer to use a part or parts of any Section or Portion of the Works before issuance of the applicable Taking-Over Certificate. In such instance the Contractor shall, for all purposes, continue to have the risk in and to take full responsibility for the care of such part or parts of any Section or Portion of the Works and all outstanding work to be done in respect of the Works prior to the issue of the relevant Taking-Over Certificate.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"It may be necessary for the Employer to use a part or parts of any Section or Portion of the Works before issuance of the applicable Taking-Over Certificate. In such instance the Contractor shall, for...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":116}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 117, '{"itemType":"CLAUSE","clause_number":"20.3","clause_title":"Designation of Portion","clause_text":"The Employer may at its own discretion (provided that it notifies the same to the Project Manager, the Supervision Consultant and the Contractor in writing) designate any part of the Works as being a Portion of the Works as herein defined.","general_condition":"The Employer may at its own discretion (provided that it notifies the same to the Project Manager, the Supervision Consultant and the Contractor in writing) designate any part of the Works as being a Portion of the Works as herein defined.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Employer may at its own discretion (provided that it notifies the same to the Project Manager, the Supervision Consultant and the Contractor in writing) designate any part of the Works as being a ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":117}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 118, '{"itemType":"CLAUSE","clause_number":"20.4","clause_title":"Draft Final Account","clause_text":"Within the period after the date of issue of a Taking-Over Certificate under Clause 20.1 (Taking-Over Certificate) for a Section or Portion of the Works stated in the Particular Conditions the Contractor shall prepare and submit to the Project Manager in triplicate a draft final account (together with all such documents and evidence as the Project Manager may require in order to substantiate the same) in respect of the said Section or Portion of the Works which shows and breaks down in such form and with such detail as the Project Manager may require all amounts which in the Contractor''s opinion are payable under the Contract in respect of the said Section or Portion of the Works.","general_condition":"Within the period after the date of issue of a Taking-Over Certificate under Clause 20.1 (Taking-Over Certificate) for a Section or Portion of the Works stated in the Particular Conditions the Contractor shall prepare and submit to the Project Manager in triplicate a draft final account (together with all such documents and evidence as the Project Manager may require in order to substantiate the same) in respect of the said Section or Portion of the Works which shows and breaks down in such form and with such detail as the Project Manager may require all amounts which in the Contractor''s opinion are payable under the Contract in respect of the said Section or Portion of the Works.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Within the period after the date of issue of a Taking-Over Certificate under Clause 20.1 (Taking-Over Certificate) for a Section or Portion of the Works stated in the Particular Conditions the Contrac...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":118}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 119, '{"itemType":"CLAUSE","clause_number":"21.1","clause_title":"Completion of Outstanding Work","clause_text":"The Contractor shall with due expedition and without delay complete during the Defects Liability Period for any Section or Portion of the Works any work which is outstanding in respect of that Section or Portion on the date of its taking over stated in the relevant Taking-Over Certificate within such reasonable time as is notified in writing to the Contractor by the Supervision Consultant.","general_condition":"The Contractor shall with due expedition and without delay complete during the Defects Liability Period for any Section or Portion of the Works any work which is outstanding in respect of that Section or Portion on the date of its taking over stated in the relevant Taking-Over Certificate within such reasonable time as is notified in writing to the Contractor by the Supervision Consultant.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall with due expedition and without delay complete during the Defects Liability Period for any Section or Portion of the Works any work which is outstanding in respect of that Section...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":119}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 120, '{"itemType":"CLAUSE","clause_number":"21.2","clause_title":"Defects after Taking Over","clause_text":"The Contractor shall as soon as practicable commence and thereafter carry out with due expedition and without delay all such work as may be necessary to remedy to the satisfaction of the Supervision Consultant any defect of any kind whatsoever in any Section or Portion of the Works of which the Contractor may be notified in writing by the Supervision Consultant, such notification being received either during the Defects Liability Period for that Section or Portion of the Works or within fourteen (14) days after its expiration but (in the latter case) the relevant defect having been found by the Supervision Consultant upon an inspection made by him or on his behalf prior to the expiration of the Defects Liability Period. For this purpose: (a) the word \"defect\" shall be construed to cover any manner in which the relevant Section or Portion of the Works is not in accordance with the Contract to the satisfaction of the Supervision Consultant; and (b) the expression \"Defects Liability Period\" shall mean a period of one (1) year calculated for each Section and Portion of the Works from the date of its taking over as stated in the relevant Taking-Over Certificate but: (i) in respect of any part of the relevant Section or Portion of the Works replaced, repaired or modified pursuant to a notice from the Supervision Consultant under this Clause 21.2, the Defects Liability Period shall be extended so as to run for a period of one (1) year calculated from the date of completion of such replacement, repair or modification to the satisfaction of the Supervision Consultant; and (ii) in respect of any part of the Works which has been taken over (whether in the same or any different Section or Portion of the Works as the defect of which notice has been given by the Supervision Consultant under this Clause 21.2) but which cannot at some time during its Defects Liability Period be used for the purposes for which it is intended by reason of the defect so notified or of work to remedy that defect, its Defects Liability Period shall be extended by a period equal to the time during which the said part of the Works could not be used as aforesaid. The Project Manager shall notify the Contractor in writing with a copy to the Employer and the Supervision Consultant of any extensions to the Defects Liability Period arising pursuant to this Clause 21.2.","general_condition":"The Contractor shall as soon as practicable commence and thereafter carry out with due expedition and without delay all such work as may be necessary to remedy to the satisfaction of the Supervision Consultant any defect of any kind whatsoever in any Section or Portion of the Works of which the Contractor may be notified in writing by the Supervision Consultant, such notification being received either during the Defects Liability Period for that Section or Portion of the Works or within fourteen (14) days after its expiration but (in the latter case) the relevant defect having been found by the Supervision Consultant upon an inspection made by him or on his behalf prior to the expiration of the Defects Liability Period. For this purpose: (a) the word \"defect\" shall be construed to cover any manner in which the relevant Section or Portion of the Works is not in accordance with the Contract to the satisfaction of the Supervision Consultant; and (b) the expression \"Defects Liability Period\" shall mean a period of one (1) year calculated for each Section and Portion of the Works from the date of its taking over as stated in the relevant Taking-Over Certificate but: (i) in respect of any part of the relevant Section or Portion of the Works replaced, repaired or modified pursuant to a notice from the Supervision Consultant under this Clause 21.2, the Defects Liability Period shall be extended so as to run for a period of one (1) year calculated from the date of completion of such replacement, repair or modification to the satisfaction of the Supervision Consultant; and (ii) in respect of any part of the Works which has been taken over (whether in the same or any different Section or Portion of the Works as the defect of which notice has been given by the Supervision Consultant under this Clause 21.2) but which cannot at some time during its Defects Liability Period be used for the purposes for which it is intended by reason of the defect so notified or of work to remedy that defect, its Defects Liability Period shall be extended by a period equal to the time during which the said part of the Works could not be used as aforesaid. The Project Manager shall notify the Contractor in writing with a copy to the Employer and the Supervision Consultant of any extensions to the Defects Liability Period arising pursuant to this Clause 21.2.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall as soon as practicable commence and thereafter carry out with due expedition and without delay all such work as may be necessary to remedy to the satisfaction of the Supervision C...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":120}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 121, '{"itemType":"CLAUSE","clause_number":"21.3","clause_title":"Cost of Remedying Defects","clause_text":"The Cost of all work to remedy any defect shall be borne by the Contractor if, in the opinion of the Supervision Consultant, the defect concerned was due to the use of Plant or workmanship not in accordance with the Contract, to any other neglect or failure by the Contractor to comply strictly with any obligation, expressed or implied, on the Contractor''s part under the Contract or to the use of any design supplied or specified by the Contractor. If, in the opinion of the Supervision Consultant, the defect concerned was due to any other cause the work concerned shall be valued as if it were an addition to the Works under Clause 22.1 (Variation Orders) and accordingly the Contractor shall be paid such value in accordance with Clause 26 (Interim and Final Certificates). Provided that if any steps taken by the Contractor in meeting its obligations to remedy any defect during the Defects Liability Period or thereafter (where the Cost of all work to remedy such defect is to be borne by the Contractor pursuant to this Clause 21.3) involves the Employer in additional Costs, such Costs after due consultation with the Employer and the Contractor shall be determined by the Project Manager and the Employer may deduct the Costs so incurred from any monies due or which may become due to the Contractor or recover the same from the Contractor as a debt due from the Contractor.","general_condition":"The Cost of all work to remedy any defect shall be borne by the Contractor if, in the opinion of the Supervision Consultant, the defect concerned was due to the use of Plant or workmanship not in accordance with the Contract, to any other neglect or failure by the Contractor to comply strictly with any obligation, expressed or implied, on the Contractor''s part under the Contract or to the use of any design supplied or specified by the Contractor. If, in the opinion of the Supervision Consultant, the defect concerned was due to any other cause the work concerned shall be valued as if it were an addition to the Works under Clause 22.1 (Variation Orders) and accordingly the Contractor shall be paid such value in accordance with Clause 26 (Interim and Final Certificates). Provided that if any steps taken by the Contractor in meeting its obligations to remedy any defect during the Defects Liability Period or thereafter (where the Cost of all work to remedy such defect is to be borne by the Contractor pursuant to this Clause 21.3) involves the Employer in additional Costs, such Costs after due consultation with the Employer and the Contractor shall be determined by the Project Manager and the Employer may deduct the Costs so incurred from any monies due or which may become due to the Contractor or recover the same from the Contractor as a debt due from the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Cost of all work to remedy any defect shall be borne by the Contractor if, in the opinion of the Supervision Consultant, the defect concerned was due to the use of Plant or workmanship not in acco...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":121}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 122, '{"itemType":"CLAUSE","clause_number":"21.4","clause_title":"Further Tests on Completion","clause_text":"If the work carried out by the Contractor pursuant to any notice from the Supervision Consultant under Clause 21.2 (Defects after Taking Over) is such that, in the opinion of the Supervision Consultant, any of the Tests on Completion should be repeated, the Supervision Consultant may, either in his notice to remedy the relevant defect or by subsequent written notice at any time not later than one month after the completion by the Contractor of the aforesaid work to the satisfaction of the Supervision Consultant, require the Contractor so to repeat the same and in such event Clause 19A (Tests on Completion) shall (with only such changes therein as may be necessary for the purpose) apply thereto.","general_condition":"If the work carried out by the Contractor pursuant to any notice from the Supervision Consultant under Clause 21.2 (Defects after Taking Over) is such that, in the opinion of the Supervision Consultant, any of the Tests on Completion should be repeated, the Supervision Consultant may, either in his notice to remedy the relevant defect or by subsequent written notice at any time not later than one month after the completion by the Contractor of the aforesaid work to the satisfaction of the Supervision Consultant, require the Contractor so to repeat the same and in such event Clause 19A (Tests on Completion) shall (with only such changes therein as may be necessary for the purpose) apply thereto.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If the work carried out by the Contractor pursuant to any notice from the Supervision Consultant under Clause 21.2 (Defects after Taking Over) is such that, in the opinion of the Supervision Consultan...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":122}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 123, '{"itemType":"CLAUSE","clause_number":"21.5","clause_title":"Access after Taking Over","clause_text":"Save as may in the opinion of the Supervision Consultant be necessary for the purpose of remedying any defect of which the Supervision Consultant may have given notice under Clause 21.2 (Defects after Taking Over) or making any search required by the Supervision Consultant under Clause 21.7 (Contractor to Search) or finishing any work related to any respects in which the relevant Section or Portion of the Works was not completed at the time of issue of the Taking-Over Certificate in respect thereof, the Contractor shall not, and shall procure that its employees, representatives, agents, Subcontractors and visitors do not, enter any Section or Portion of the Works after issue of the Taking-Over Certificate relating thereto. Provided that access by the Contractor to any Section or Portion of the Works for the aforesaid purposes shall not be allowed when such access is inconsistent with the Employer''s reasonable security restrictions.","general_condition":"Save as may in the opinion of the Supervision Consultant be necessary for the purpose of remedying any defect of which the Supervision Consultant may have given notice under Clause 21.2 (Defects after Taking Over) or making any search required by the Supervision Consultant under Clause 21.7 (Contractor to Search) or finishing any work related to any respects in which the relevant Section or Portion of the Works was not completed at the time of issue of the Taking-Over Certificate in respect thereof, the Contractor shall not, and shall procure that its employees, representatives, agents, Subcontractors and visitors do not, enter any Section or Portion of the Works after issue of the Taking-Over Certificate relating thereto. Provided that access by the Contractor to any Section or Portion of the Works for the aforesaid purposes shall not be allowed when such access is inconsistent with the Employer''s reasonable security restrictions.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Save as may in the opinion of the Supervision Consultant be necessary for the purpose of remedying any defect of which the Supervision Consultant may have given notice under Clause 21.2 (Defects after...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":123}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 124, '{"itemType":"CLAUSE","clause_number":"21.6","clause_title":"Delay in Remedying Defects","clause_text":"If after the Supervision Consultant has given the Contractor any notice under Clause 21.2 (Defects after Taking Over) the Contractor does not in the opinion of the Supervision Consultant commence as soon as practicable or thereafter carry out with due expedition and without delay all such work as may be necessary to remedy to the satisfaction of the Supervision Consultant the defect concerned, then (without prejudice to any other rights the Employer may have by virtue of the said failure of the Contractor): (a) The Employer may itself or may employ and pay any other person, firm or company to carry out and complete that work at the Contractor''s risk. If any of that work is work the Cost of which, in accordance with Clause 21.3 (Cost of Remedying Defects), the Contractor would in the opinion of the Supervision Consultant have been bound to bear, then all Costs which the Employer may incur as a result of or in connection with itself carrying out and completing such work or employing and paying that other person, firm or company so to do shall be borne by the Contractor. In such event the Project Manager shall determine such Costs and the Employer may deduct the Costs so incurred from any monies due or which may become due to the Contractor or recover the same as a debt due from the Contractor; or (b) If the defect is such that the Employer has been deprived of substantially the whole of the benefit of the Works or a part thereof, it may terminate the Contract in respect of such parts of the Works as cannot be put to the intended use. The Employer shall be entitled to recover all sums paid in respect of such parts of the Works plus financing costs and the Cost of dismantling the same, clearing the Site and returning Plant to the Contractor or otherwise disposing of it in accordance with the Contractor''s instructions. Such sums, costs and Cost shall be determined by the Project Manager and the Employer may deduct the same from any monies due or which may become due to the Contractor or recover the same as a debt due from the Contractor.","general_condition":"If after the Supervision Consultant has given the Contractor any notice under Clause 21.2 (Defects after Taking Over) the Contractor does not in the opinion of the Supervision Consultant commence as soon as practicable or thereafter carry out with due expedition and without delay all such work as may be necessary to remedy to the satisfaction of the Supervision Consultant the defect concerned, then (without prejudice to any other rights the Employer may have by virtue of the said failure of the Contractor): (a) The Employer may itself or may employ and pay any other person, firm or company to carry out and complete that work at the Contractor''s risk. If any of that work is work the Cost of which, in accordance with Clause 21.3 (Cost of Remedying Defects), the Contractor would in the opinion of the Supervision Consultant have been bound to bear, then all Costs which the Employer may incur as a result of or in connection with itself carrying out and completing such work or employing and paying that other person, firm or company so to do shall be borne by the Contractor. In such event the Project Manager shall determine such Costs and the Employer may deduct the Costs so incurred from any monies due or which may become due to the Contractor or recover the same as a debt due from the Contractor; or (b) If the defect is such that the Employer has been deprived of substantially the whole of the benefit of the Works or a part thereof, it may terminate the Contract in respect of such parts of the Works as cannot be put to the intended use. The Employer shall be entitled to recover all sums paid in respect of such parts of the Works plus financing costs and the Cost of dismantling the same, clearing the Site and returning Plant to the Contractor or otherwise disposing of it in accordance with the Contractor''s instructions. Such sums, costs and Cost shall be determined by the Project Manager and the Employer may deduct the same from any monies due or which may become due to the Contractor or recover the same as a debt due from the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If after the Supervision Consultant has given the Contractor any notice under Clause 21.2 (Defects after Taking Over) the Contractor does not in the opinion of the Supervision Consultant commence as s...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":124}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 125, '{"itemType":"CLAUSE","clause_number":"21.7","clause_title":"Contractor to Search","clause_text":"The Contractor shall, if required by the Supervision Consultant in writing, search under the directions of the Supervision Consultant for the cause of any defect appearing during the progress of the Works or during the Defects Liability Period for any Section or Portion thereof. Unless such defect shall be one of the Costs of remedying which the Contractor is liable to bear under Clause 21.3 (Cost of Remedying Defects), the Cost of the work carried out by the Contractor in searching as aforesaid shall be determined by the Project Manager and the Contractor shall be paid such Cost in accordance with Clause 26 (Interim and Final Certificates). If such defect shall be one of the Costs of remedying which the Contractor is liable to bear as aforesaid, the Cost of the work carried out in searching as aforesaid shall be borne by the Contractor.","general_condition":"The Contractor shall, if required by the Supervision Consultant in writing, search under the directions of the Supervision Consultant for the cause of any defect appearing during the progress of the Works or during the Defects Liability Period for any Section or Portion thereof. Unless such defect shall be one of the Costs of remedying which the Contractor is liable to bear under Clause 21.3 (Cost of Remedying Defects), the Cost of the work carried out by the Contractor in searching as aforesaid shall be determined by the Project Manager and the Contractor shall be paid such Cost in accordance with Clause 26 (Interim and Final Certificates). If such defect shall be one of the Costs of remedying which the Contractor is liable to bear as aforesaid, the Cost of the work carried out in searching as aforesaid shall be borne by the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall, if required by the Supervision Consultant in writing, search under the directions of the Supervision Consultant for the cause of any defect appearing during the progress of the W...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":125}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 126, '{"itemType":"CLAUSE","clause_number":"21.8","clause_title":"Application to Extended Warranties","clause_text":"Clauses 21.4 (Further Tests on Completion) to 21.7 (Contractor to Search) inclusive shall also apply in relation to any Extended Warranty but with such changes and only such changes therein as may be necessary for the purpose.","general_condition":"Clauses 21.4 (Further Tests on Completion) to 21.7 (Contractor to Search) inclusive shall also apply in relation to any Extended Warranty but with such changes and only such changes therein as may be necessary for the purpose.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Clauses 21.4 (Further Tests on Completion) to 21.7 (Contractor to Search) inclusive shall also apply in relation to any Extended Warranty but with such changes and only such changes therein as may be ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":126}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 127, '{"itemType":"CLAUSE","clause_number":"22.1","clause_title":"Variation Orders","clause_text":"The Employer may from time to time during the currency of the Contract by written order (herein referred to as a \"Variation Order\") direct the Contractor to alter, amend, omit, add to or otherwise vary the Works or any part thereof or to change any sequence or timing of construction of any part of the Works specified in the Contract and the Contractor shall be bound to carry out such Variation Order as if the provisions of the same had been incorporated into the Contract. The Contractor shall not alter, amend, omit, add to or otherwise vary the Works or any part thereof or change any sequence or timing of construction of any part of the Works specified in the Contract in any way whatsoever except as directed by a Variation Order. The effect, if any, of all such Variation Orders shall be valued in accordance with Clause 22.3 (Valuation of Variation Orders) provided that where the issue of a Variation Order is necessitated by some default of or breach of contract by the Contractor or for which it is responsible, any additional Costs attributable to such default or breach shall be borne by the Contractor.","general_condition":"The Employer may from time to time during the currency of the Contract by written order (herein referred to as a \"Variation Order\") direct the Contractor to alter, amend, omit, add to or otherwise vary the Works or any part thereof or to change any sequence or timing of construction of any part of the Works specified in the Contract and the Contractor shall be bound to carry out such Variation Order as if the provisions of the same had been incorporated into the Contract. The Contractor shall not alter, amend, omit, add to or otherwise vary the Works or any part thereof or change any sequence or timing of construction of any part of the Works specified in the Contract in any way whatsoever except as directed by a Variation Order. The effect, if any, of all such Variation Orders shall be valued in accordance with Clause 22.3 (Valuation of Variation Orders) provided that where the issue of a Variation Order is necessitated by some default of or breach of contract by the Contractor or for which it is responsible, any additional Costs attributable to such default or breach shall be borne by the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Employer may from time to time during the currency of the Contract by written order (herein referred to as a \"Variation Order\") direct the Contractor to alter, amend, omit, add to or otherwise var...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":127}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 128, '{"itemType":"CLAUSE","clause_number":"22.2","clause_title":"Effect on Programme","clause_text":"Forthwith after receiving any Variation Order the Contractor shall proceed with the carrying out thereof and shall within the period required by the Project Manager notify the Project Manager in writing whether in the Contractor''s opinion the Variation Order will result in any need to revise the Programme and, if in the Contractor''s opinion there is such a need, the said written notification shall be accompanied by a proposed revised programme for the approval of the Project Manager. The Contractor shall not delay so carrying out the Variation Order pending determination in accordance with Clause 22.3 (Valuation of Variation Orders) of the value of the Variation Order or pending approval of any proposed revised programme. Nor shall the Contractor delay pending the grant of any consequent extension of the Time for Completion to which the Contractor may be entitled in accordance with Clause 19.2 (Extensions of Time).","general_condition":"Forthwith after receiving any Variation Order the Contractor shall proceed with the carrying out thereof and shall within the period required by the Project Manager notify the Project Manager in writing whether in the Contractor''s opinion the Variation Order will result in any need to revise the Programme and, if in the Contractor''s opinion there is such a need, the said written notification shall be accompanied by a proposed revised programme for the approval of the Project Manager. The Contractor shall not delay so carrying out the Variation Order pending determination in accordance with Clause 22.3 (Valuation of Variation Orders) of the value of the Variation Order or pending approval of any proposed revised programme. Nor shall the Contractor delay pending the grant of any consequent extension of the Time for Completion to which the Contractor may be entitled in accordance with Clause 19.2 (Extensions of Time).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Forthwith after receiving any Variation Order the Contractor shall proceed with the carrying out thereof and shall within the period required by the Project Manager notify the Project Manager in writi...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":128}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 129, '{"itemType":"CLAUSE","clause_number":"22.3","clause_title":"Valuation of Variation Orders","clause_text":"As soon as practicable after the issue of any Variation Order the value thereof shall be determined as follows: (a) the Variation Order shall be valued at rates and prices contained in the Contract insofar as such rates and prices shall, in the opinion of the Employer be appropriate and applicable; and (b) insofar as the Variation Order cannot be valued in accordance with Clause 22.3(a) above the Employer and the Contractor shall endeavour to agree a suitable valuation; or (c) in the event of failure to agree as aforesaid the Employer shall fix a valuation which, in its opinion, is appropriate and shall notify the Contractor accordingly with a copy to the Project Manager. Until such time as a valuation is agreed or fixed, the Project Manager shall after consultation with the Employer and the Contractor determine a provisional valuation to enable on-account payments to be included in certificates issued in accordance with Clause 26 (Interim and Final Certificates). The Contractor shall submit to the Employer all such documents, evidence and calculations as the Employer shall require for the purpose of agreeing a suitable valuation under Clause 22.3(b) above. Provided that where any Variation Order renders abortive any work already carried out by the Contractor (which work it is reasonable for the Contractor so to have carried out having regard to the requirements of the then current Programme) due account shall be taken thereof in valuing that Variation Order. Pending determination in accordance with this Clause 22.3 (Valuation of Variation Orders) of the value of the Variation Order, and if the Employer or the Project Manager considers it to be appropriate, the Contractor shall keep records of the Cost of carrying out the Variation Order and of time expended thereon. Such records shall be open to inspection by the Project Manager and the Employer at all reasonable times.","general_condition":"As soon as practicable after the issue of any Variation Order the value thereof shall be determined as follows: (a) the Variation Order shall be valued at rates and prices contained in the Contract insofar as such rates and prices shall, in the opinion of the Employer be appropriate and applicable; and (b) insofar as the Variation Order cannot be valued in accordance with Clause 22.3(a) above the Employer and the Contractor shall endeavour to agree a suitable valuation; or (c) in the event of failure to agree as aforesaid the Employer shall fix a valuation which, in its opinion, is appropriate and shall notify the Contractor accordingly with a copy to the Project Manager. Until such time as a valuation is agreed or fixed, the Project Manager shall after consultation with the Employer and the Contractor determine a provisional valuation to enable on-account payments to be included in certificates issued in accordance with Clause 26 (Interim and Final Certificates). The Contractor shall submit to the Employer all such documents, evidence and calculations as the Employer shall require for the purpose of agreeing a suitable valuation under Clause 22.3(b) above. Provided that where any Variation Order renders abortive any work already carried out by the Contractor (which work it is reasonable for the Contractor so to have carried out having regard to the requirements of the then current Programme) due account shall be taken thereof in valuing that Variation Order. Pending determination in accordance with this Clause 22.3 (Valuation of Variation Orders) of the value of the Variation Order, and if the Employer or the Project Manager considers it to be appropriate, the Contractor shall keep records of the Cost of carrying out the Variation Order and of time expended thereon. Such records shall be open to inspection by the Project Manager and the Employer at all reasonable times.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"As soon as practicable after the issue of any Variation Order the value thereof shall be determined as follows: (a) the Variation Order shall be valued at rates and prices contained in the Contract in...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":129}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 130, '{"itemType":"CLAUSE","clause_number":"22.4","clause_title":"Dayworks","clause_text":"(a) The Employer may, if in his opinion it is necessary or desirable, issue an instruction that any varied work shall be carried out on a daywork basis. The Contractor shall then be paid for such varied work in accordance with the Dayworks Bill forming part of the Bills of Quantities and at the rates and prices stated in such Bill. (b) In respect of any part of the Works carried out on a daywork basis, the Contractor shall, during the continuance of such part of the Works, submit to the Project Manager each day in duplicate daywork sheets signed by the Contractor''s Representative, in such form as the Project Manager shall require, giving the following details and such other details as the Project Manager may require: (ii) the name and occupation of and the hours properly and necessarily worked by all workmen employed in carrying out such work; (iii) a description of and the hours properly and necessarily worked by all plant and equipment used in carrying out such work; and (iv) a description and the quantity of all Plant properly and necessarily used in the carrying out of such work. The Project Manager shall arrange for the Supervision Consultant to consider such daywork sheets, to make any adjustments that the Supervision Consultant considers necessary and to thereafter sign one copy, adjusted as aforesaid, and return the said copy to the Contractor. (c) At the end of each month the Contractor shall submit to the Project Manager in duplicate statements signed by the Contractor''s Representative, in such form as the Project Manager shall require, giving full details of any part of the Works carried out on a daywork basis during that month including but not limited to details of all workmen, Plant, plant and equipment used in carrying out such part of the Works and agreed by the Supervision Consultant in accordance with paragraph (b) of this Clause 22.4 such details being priced in accordance with the Dayworks Bill forming part of the Bills of Quantities. Such statements shall be accompanied by all relevant quotations, invoices, vouchers, receipts and any other documents and evidence which the Project Manager shall require in order to substantiate such statements. (d) The Contractor shall not be entitled to any payment for work carried out on a daywork basis unless the daywork sheets and statements and accompanying documents and evidence referred to in paragraphs (b) and (c) respectively of this Clause 22.4 have been fully and punctually rendered. Provided always that if the Employer considers that for any reason the sending of such daywork sheets and statements and accompanying documents and evidence by the Contractor in accordance with the said paragraphs was impracticable, he shall nevertheless be entitled to authorise payment for the relevant part of the Works, either on a daywork basis or at such value as shall, in his opinion, be fair and reasonable.","general_condition":"(a) The Employer may, if in his opinion it is necessary or desirable, issue an instruction that any varied work shall be carried out on a daywork basis. The Contractor shall then be paid for such varied work in accordance with the Dayworks Bill forming part of the Bills of Quantities and at the rates and prices stated in such Bill. (b) In respect of any part of the Works carried out on a daywork basis, the Contractor shall, during the continuance of such part of the Works, submit to the Project Manager each day in duplicate daywork sheets signed by the Contractor''s Representative, in such form as the Project Manager shall require, giving the following details and such other details as the Project Manager may require: (ii) the name and occupation of and the hours properly and necessarily worked by all workmen employed in carrying out such work; (iii) a description of and the hours properly and necessarily worked by all plant and equipment used in carrying out such work; and (iv) a description and the quantity of all Plant properly and necessarily used in the carrying out of such work. The Project Manager shall arrange for the Supervision Consultant to consider such daywork sheets, to make any adjustments that the Supervision Consultant considers necessary and to thereafter sign one copy, adjusted as aforesaid, and return the said copy to the Contractor. (c) At the end of each month the Contractor shall submit to the Project Manager in duplicate statements signed by the Contractor''s Representative, in such form as the Project Manager shall require, giving full details of any part of the Works carried out on a daywork basis during that month including but not limited to details of all workmen, Plant, plant and equipment used in carrying out such part of the Works and agreed by the Supervision Consultant in accordance with paragraph (b) of this Clause 22.4 such details being priced in accordance with the Dayworks Bill forming part of the Bills of Quantities. Such statements shall be accompanied by all relevant quotations, invoices, vouchers, receipts and any other documents and evidence which the Project Manager shall require in order to substantiate such statements. (d) The Contractor shall not be entitled to any payment for work carried out on a daywork basis unless the daywork sheets and statements and accompanying documents and evidence referred to in paragraphs (b) and (c) respectively of this Clause 22.4 have been fully and punctually rendered. Provided always that if the Employer considers that for any reason the sending of such daywork sheets and statements and accompanying documents and evidence by the Contractor in accordance with the said paragraphs was impracticable, he shall nevertheless be entitled to authorise payment for the relevant part of the Works, either on a daywork basis or at such value as shall, in his opinion, be fair and reasonable.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) The Employer may, if in his opinion it is necessary or desirable, issue an instruction that any varied work shall be carried out on a daywork basis. The Contractor shall then be paid for such vari...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":130}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 131, '{"itemType":"CLAUSE","clause_number":"22.5","clause_title":"Value Engineering","clause_text":"The Contractor may at any time submit to the Employer for his consideration (with a copy to the Project Manager) a written proposal which (in the Contractor''s opinion) would, if implemented by way of a Variation Order issued by the Employer pursuant to Clause 22.1 (Variation Orders): (b) improve the performance of the Works or any part thereof; (c) reduce the cost to the Employer of carrying out, maintaining or operating the Works or any part thereof; or The said proposal shall include a description of the proposed work to be performed and a programme for its execution, the Contractor''s proposal for any necessary revisions to the programme and to the Time for Completion and the Contractor''s proposal for the valuation of the Variation Order. The Cost of preparing the said proposal and any modifications thereto and of attending meetings with the Employer and/or the Project Manager and/or the Supervision Consultant in relation thereto shall be borne by the Contractor.","general_condition":"The Contractor may at any time submit to the Employer for his consideration (with a copy to the Project Manager) a written proposal which (in the Contractor''s opinion) would, if implemented by way of a Variation Order issued by the Employer pursuant to Clause 22.1 (Variation Orders): (b) improve the performance of the Works or any part thereof; (c) reduce the cost to the Employer of carrying out, maintaining or operating the Works or any part thereof; or The said proposal shall include a description of the proposed work to be performed and a programme for its execution, the Contractor''s proposal for any necessary revisions to the programme and to the Time for Completion and the Contractor''s proposal for the valuation of the Variation Order. The Cost of preparing the said proposal and any modifications thereto and of attending meetings with the Employer and/or the Project Manager and/or the Supervision Consultant in relation thereto shall be borne by the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor may at any time submit to the Employer for his consideration (with a copy to the Project Manager) a written proposal which (in the Contractor''s opinion) would, if implemented by way of ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":131}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 132, '{"itemType":"CLAUSE","clause_number":"23.1","clause_title":"Notice of Claims","clause_text":"Notwithstanding any other provision of the Contract, if the Contractor intends to make a claim against the Employer for any additional payment in connection with or arising out of the Contract or the Works or any Section or Portion thereof other than a claim for a payment of or on account of any Variation Order issued pursuant to Clause 22.1 (Variation Orders), the Contractor shall give notice in writing thereof to the Project Manager, with a copy to the Employer, as soon as possible and in any event within twenty eight (28) days after the event or circumstance giving rise to the claim has first occurred. Such notice shall contain full and detailed particulars of and information concerning such claim insofar as those particulars are or that information is then known to it or reasonably available to it. Such particulars and information shall without limitation include the grounds upon which such claim is based, the Contractor''s then estimate as to the amount of the aforesaid claim and details of the build-up of such estimate. The claims to which this Clause 23.1 applies shall without limitation include any claims for extra Cost under Clause 18.1 (Suspension Order) and in those cases the notice given under this Clause 23.1 shall provided it is given within the period of twenty eight (28) days referred to in the said Clause 18.1 fulfill the requirements of that Clause concerning notice of intention to claim.","general_condition":"Notwithstanding any other provision of the Contract, if the Contractor intends to make a claim against the Employer for any additional payment in connection with or arising out of the Contract or the Works or any Section or Portion thereof other than a claim for a payment of or on account of any Variation Order issued pursuant to Clause 22.1 (Variation Orders), the Contractor shall give notice in writing thereof to the Project Manager, with a copy to the Employer, as soon as possible and in any event within twenty eight (28) days after the event or circumstance giving rise to the claim has first occurred. Such notice shall contain full and detailed particulars of and information concerning such claim insofar as those particulars are or that information is then known to it or reasonably available to it. Such particulars and information shall without limitation include the grounds upon which such claim is based, the Contractor''s then estimate as to the amount of the aforesaid claim and details of the build-up of such estimate. The claims to which this Clause 23.1 applies shall without limitation include any claims for extra Cost under Clause 18.1 (Suspension Order) and in those cases the notice given under this Clause 23.1 shall provided it is given within the period of twenty eight (28) days referred to in the said Clause 18.1 fulfill the requirements of that Clause concerning notice of intention to claim.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Notwithstanding any other provision of the Contract, if the Contractor intends to make a claim against the Employer for any additional payment in connection with or arising out of the Contract or the ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":132}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 133, '{"itemType":"CLAUSE","clause_number":"23.2","clause_title":"Notice of Further Particulars or Information","clause_text":"As soon as the Contractor becomes aware of any particulars of or other information concerning any claim of which notice has been given to the Project Manager under Clause 23.1 (Notice of Claims) additional to the particulars and information contained in the aforesaid notice or to any further particulars or information previously notified to the Project Manager under this Clause 23.2, the Contractor shall give notice in writing thereof to the Project Manager containing full details including without limitation any revision to the Contractor''s previous estimate as to the amount of the aforesaid claim and details of the revision.","general_condition":"As soon as the Contractor becomes aware of any particulars of or other information concerning any claim of which notice has been given to the Project Manager under Clause 23.1 (Notice of Claims) additional to the particulars and information contained in the aforesaid notice or to any further particulars or information previously notified to the Project Manager under this Clause 23.2, the Contractor shall give notice in writing thereof to the Project Manager containing full details including without limitation any revision to the Contractor''s previous estimate as to the amount of the aforesaid claim and details of the revision.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"As soon as the Contractor becomes aware of any particulars of or other information concerning any claim of which notice has been given to the Project Manager under Clause 23.1 (Notice of Claims) addit...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":133}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 134, '{"itemType":"CLAUSE","clause_number":"23.3","clause_title":"Contemporary Records","clause_text":"Whenever an event or circumstance occurs which gives rise to a claim as provided in Clause 23.1 (Notice of Claims) the Contractor shall keep full and detailed contemporary records of and relating to all matters relevant to any such claim either on the Site or at another location acceptable to the Project Manager. The Project Manager shall at any time during normal working hours of the Contractor be entitled to inspect such contemporary records and to require the Contractor to supply it with copies of any of the same.","general_condition":"Whenever an event or circumstance occurs which gives rise to a claim as provided in Clause 23.1 (Notice of Claims) the Contractor shall keep full and detailed contemporary records of and relating to all matters relevant to any such claim either on the Site or at another location acceptable to the Project Manager. The Project Manager shall at any time during normal working hours of the Contractor be entitled to inspect such contemporary records and to require the Contractor to supply it with copies of any of the same.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Whenever an event or circumstance occurs which gives rise to a claim as provided in Clause 23.1 (Notice of Claims) the Contractor shall keep full and detailed contemporary records of and relating to a...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":134}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 135, '{"itemType":"CLAUSE","clause_number":"23.4","clause_title":"Current Estimates","clause_text":"The Project Manager shall at any time or times be entitled to require the Contractor to deliver to the Project Manager in writing its then latest estimate as to the amount of any claim of which notice has been given to the Project Manager under Clause 23.1 (Notice of Claims).","general_condition":"The Project Manager shall at any time or times be entitled to require the Contractor to deliver to the Project Manager in writing its then latest estimate as to the amount of any claim of which notice has been given to the Project Manager under Clause 23.1 (Notice of Claims).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Project Manager shall at any time or times be entitled to require the Contractor to deliver to the Project Manager in writing its then latest estimate as to the amount of any claim of which notice...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":135}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 136, '{"itemType":"CLAUSE","clause_number":"23.5","clause_title":"Comprehensive Claim","clause_text":"Within twenty eight (28) days, or such extended time as the Project Manager may allow, of giving notice under Clause 23.1 (Notice of Claims), the Contractor shall submit to the Project Manager a comprehensive account of the claim giving full and detailed particulars of and information concerning such claim. Such particulars and information shall without limitation include the grounds upon which the claim is based, the final amount of the claim and the build-up thereof and the contemporary records required under Clause 23.3 (Contemporary Records). Such particulars and information shall incorporate as applicable the particulars and information previously submitted under Clause 23.1 (Notice of Claims), Clause 23.2 (Notice of Further Particulars or Information) and Clause 23.3 (Contemporary Records). The Contractor shall thereafter promptly submit such further particulars and information of its claim as the Project Manager may require to assess the validity of the claim.","general_condition":"Within twenty eight (28) days, or such extended time as the Project Manager may allow, of giving notice under Clause 23.1 (Notice of Claims), the Contractor shall submit to the Project Manager a comprehensive account of the claim giving full and detailed particulars of and information concerning such claim. Such particulars and information shall without limitation include the grounds upon which the claim is based, the final amount of the claim and the build-up thereof and the contemporary records required under Clause 23.3 (Contemporary Records). Such particulars and information shall incorporate as applicable the particulars and information previously submitted under Clause 23.1 (Notice of Claims), Clause 23.2 (Notice of Further Particulars or Information) and Clause 23.3 (Contemporary Records). The Contractor shall thereafter promptly submit such further particulars and information of its claim as the Project Manager may require to assess the validity of the claim.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Within twenty eight (28) days, or such extended time as the Project Manager may allow, of giving notice under Clause 23.1 (Notice of Claims), the Contractor shall submit to the Project Manager a compr...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":136}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 137, '{"itemType":"CLAUSE","clause_number":"23.6","clause_title":"Failure to Comply","clause_text":"If the Contractor fails to comply with any of the provisions of Clause 23.1 (Notice of Claims) in respect of any such claim as is referred to therein, the Contractor shall not under any circumstances be entitled to any additional payment in respect of the claim concerned and the Employer shall be discharged from any and all liability in connection therewith. Otherwise, if the Contractor fails to comply with any of the provisions of Clause 23.2 (Notice of Further Particulars or Information), Clause 23.3 (Contemporary Records), Clause 23.4 (Current Estimates), Clause 23.5 (Comprehensive Claim) or any other Clauses of these Conditions of Contract which may be applicable in respect of any such claim as is referred to therein, then (without prejudice to any other rights the Employer may have in respect of the said failure) the Contractor shall be entitled to payment in respect of the claim concerned only to the extent that the Project Manager is not thereby prejudiced in fully investigating the claim and provided that the Contractor''s entitlement to payment shall not exceed such amount as the Project Manager considers to be verified by contemporary records and is nevertheless able to establish that the same is fair and reasonable and should in accordance with the provisions of the Contract be paid by the Employer.","general_condition":"If the Contractor fails to comply with any of the provisions of Clause 23.1 (Notice of Claims) in respect of any such claim as is referred to therein, the Contractor shall not under any circumstances be entitled to any additional payment in respect of the claim concerned and the Employer shall be discharged from any and all liability in connection therewith. Otherwise, if the Contractor fails to comply with any of the provisions of Clause 23.2 (Notice of Further Particulars or Information), Clause 23.3 (Contemporary Records), Clause 23.4 (Current Estimates), Clause 23.5 (Comprehensive Claim) or any other Clauses of these Conditions of Contract which may be applicable in respect of any such claim as is referred to therein, then (without prejudice to any other rights the Employer may have in respect of the said failure) the Contractor shall be entitled to payment in respect of the claim concerned only to the extent that the Project Manager is not thereby prejudiced in fully investigating the claim and provided that the Contractor''s entitlement to payment shall not exceed such amount as the Project Manager considers to be verified by contemporary records and is nevertheless able to establish that the same is fair and reasonable and should in accordance with the provisions of the Contract be paid by the Employer.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If the Contractor fails to comply with any of the provisions of Clause 23.1 (Notice of Claims) in respect of any such claim as is referred to therein, the Contractor shall not under any circumstances ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":137}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 138, '{"itemType":"CLAUSE","clause_number":"23.7","clause_title":"Determination of Claims","clause_text":"When the Project Manager has received the comprehensive account of the claim as required by Clause 23.5 (Comprehensive Claim) and such further particulars and information as he may require under such Clause, the Project Manager shall, after due consultation with the Employer, the Supervision Consultant and the Contractor, determine the amount, if any, to which the Contractor is entitled and the Contractor shall be paid that amount in accordance with Clause 26 (Interim and Final Certificates). The Project Manager shall notify the Contractor of any determination made under this Clause 23.7 with a copy to the Employer.","general_condition":"When the Project Manager has received the comprehensive account of the claim as required by Clause 23.5 (Comprehensive Claim) and such further particulars and information as he may require under such Clause, the Project Manager shall, after due consultation with the Employer, the Supervision Consultant and the Contractor, determine the amount, if any, to which the Contractor is entitled and the Contractor shall be paid that amount in accordance with Clause 26 (Interim and Final Certificates). The Project Manager shall notify the Contractor of any determination made under this Clause 23.7 with a copy to the Employer.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"When the Project Manager has received the comprehensive account of the claim as required by Clause 23.5 (Comprehensive Claim) and such further particulars and information as he may require under such ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":138}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 139, '{"itemType":"CLAUSE","clause_number":"23.8","clause_title":"Exclusion of Small Claims","clause_text":"Notwithstanding anything contained in the foregoing provisions of this Clause 23 (Claims) or elsewhere in the Contract, any such claim as is referred to in Clause 23.1 (Notice of Claims) shall only be valid if: (a) it concerns a matter which, in the opinion of the Project Manager, could not have been reasonably foreseen by an experienced contractor prior to the latest date for submission of the Tender; and (b) such claim is otherwise fully substantiated under the terms, conditions and provisions of the Contract to the satisfaction of the Project Manager; and (c) in any event, such substantiated claim (when taken together only with any other such substantiated claim which, in the opinion of the Project Manager, is clearly related to and evolves out of the same cause) is not less than the amount stated in the Particular Conditions.","general_condition":"Notwithstanding anything contained in the foregoing provisions of this Clause 23 (Claims) or elsewhere in the Contract, any such claim as is referred to in Clause 23.1 (Notice of Claims) shall only be valid if: (a) it concerns a matter which, in the opinion of the Project Manager, could not have been reasonably foreseen by an experienced contractor prior to the latest date for submission of the Tender; and (b) such claim is otherwise fully substantiated under the terms, conditions and provisions of the Contract to the satisfaction of the Project Manager; and (c) in any event, such substantiated claim (when taken together only with any other such substantiated claim which, in the opinion of the Project Manager, is clearly related to and evolves out of the same cause) is not less than the amount stated in the Particular Conditions.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Notwithstanding anything contained in the foregoing provisions of this Clause 23 (Claims) or elsewhere in the Contract, any such claim as is referred to in Clause 23.1 (Notice of Claims) shall only be...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":139}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 140, '{"itemType":"CLAUSE","clause_number":"24.1","clause_title":"Obligation and Objection","clause_text":"The Contractor shall enter into a subcontract for the applicable specified part of the Works with any Nominated Subcontractor. However, notwithstanding that any person might otherwise fall within the definition in Clause 1.1 (Definitions) of \"Nominated Subcontractor\", he shall not so fall and the Contractor shall not be under any obligation to grant any subcontract to him if: (a) he shall in relation to the whole or any part of the Works to be subcontracted to him decline to grant to the Employer or the Contractor any guarantee or warranty which it may be stipulated in the Contract that he should so grant; or (b) the Contractor shall have any other objection to him which in the opinion of the Project Manager is reasonable in the circumstances.","general_condition":"The Contractor shall enter into a subcontract for the applicable specified part of the Works with any Nominated Subcontractor. However, notwithstanding that any person might otherwise fall within the definition in Clause 1.1 (Definitions) of \"Nominated Subcontractor\", he shall not so fall and the Contractor shall not be under any obligation to grant any subcontract to him if: (a) he shall in relation to the whole or any part of the Works to be subcontracted to him decline to grant to the Employer or the Contractor any guarantee or warranty which it may be stipulated in the Contract that he should so grant; or (b) the Contractor shall have any other objection to him which in the opinion of the Project Manager is reasonable in the circumstances.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall enter into a subcontract for the applicable specified part of the Works with any Nominated Subcontractor. However, notwithstanding that any person might otherwise fall within the ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":140}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 141, '{"itemType":"CLAUSE","clause_number":"24.2","clause_title":"Delayed Payments to Nominated Subcontractors","clause_text":"Before issuing any Interim Certificate or Final Certificate under Clause 26 (Interim and Final Certificates) which includes any amount in respect of work done and/or Plant provided by any Nominated Subcontractor, the Project Manager shall be entitled to require from the Contractor reasonable evidence that all amounts due to such Nominated Subcontractor in respect of work done and/or Plant provided and covered by previous Interim Certificates or Final Certificates, less applicable deductions for retention or otherwise, have been received by such Nominated Subcontractor. Unless the Contractor (a) submits such reasonable evidence to the Project Manager; or (b) (i) satisfies the Project Manager in writing that the Contractor has reasonable cause for withholding or refusing to pay the said amounts (less applicable deductions); and (ii) produces to the Project Manager reasonable evidence that the Contractor has informed such Nominated Subcontractor in writing of that reasonable cause; the Employer may (at its sole discretion) pay to such Nominated Subcontractor direct, upon the certificate of the Project Manager, part or all of such amounts (less applicable deductions) as are due to such Nominated Subcontractor and for which the Contractor has failed to comply with the requirements of paragraphs (a) or (b) above. In such event, the Employer shall be entitled to deduct any amount so paid direct to the Nominated Subcontractor by the Employer from any sums due or which may become due from the Employer to the Contractor or to recover the same as a debt due from the Contractor. Provided that the Employer shall, in no circumstances where it exercises its discretion to pay a Nominated Subcontractor direct as aforesaid, be obliged to pay any amount to a Nominated Subcontractor in excess of amounts available for deduction as set out in this Clause 24.2. Notwithstanding the foregoing, the Employer expressly excludes all liability to the Contractor for any wrongful or incorrect payment direct to any Nominated Subcontractor by the Employer.","general_condition":"Before issuing any Interim Certificate or Final Certificate under Clause 26 (Interim and Final Certificates) which includes any amount in respect of work done and/or Plant provided by any Nominated Subcontractor, the Project Manager shall be entitled to require from the Contractor reasonable evidence that all amounts due to such Nominated Subcontractor in respect of work done and/or Plant provided and covered by previous Interim Certificates or Final Certificates, less applicable deductions for retention or otherwise, have been received by such Nominated Subcontractor. Unless the Contractor (a) submits such reasonable evidence to the Project Manager; or (b) (i) satisfies the Project Manager in writing that the Contractor has reasonable cause for withholding or refusing to pay the said amounts (less applicable deductions); and (ii) produces to the Project Manager reasonable evidence that the Contractor has informed such Nominated Subcontractor in writing of that reasonable cause; the Employer may (at its sole discretion) pay to such Nominated Subcontractor direct, upon the certificate of the Project Manager, part or all of such amounts (less applicable deductions) as are due to such Nominated Subcontractor and for which the Contractor has failed to comply with the requirements of paragraphs (a) or (b) above. In such event, the Employer shall be entitled to deduct any amount so paid direct to the Nominated Subcontractor by the Employer from any sums due or which may become due from the Employer to the Contractor or to recover the same as a debt due from the Contractor. Provided that the Employer shall, in no circumstances where it exercises its discretion to pay a Nominated Subcontractor direct as aforesaid, be obliged to pay any amount to a Nominated Subcontractor in excess of amounts available for deduction as set out in this Clause 24.2. Notwithstanding the foregoing, the Employer expressly excludes all liability to the Contractor for any wrongful or incorrect payment direct to any Nominated Subcontractor by the Employer.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Before issuing any Interim Certificate or Final Certificate under Clause 26 (Interim and Final Certificates) which includes any amount in respect of work done and/or Plant provided by any Nominated Su...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":141}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 142, '{"itemType":"CLAUSE","clause_number":"25.1","clause_title":"Quantities","clause_text":"The quantities set out in the Bills of Quantities are estimated quantities and are not to be taken as the actual and correct quantities of the Works which the Contractor is required to carry out or for the purposes of Clause 25.2 (Works to be Measured).","general_condition":"The quantities set out in the Bills of Quantities are estimated quantities and are not to be taken as the actual and correct quantities of the Works which the Contractor is required to carry out or for the purposes of Clause 25.2 (Works to be Measured).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The quantities set out in the Bills of Quantities are estimated quantities and are not to be taken as the actual and correct quantities of the Works which the Contractor is required to carry out or fo...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":142}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 143, '{"itemType":"CLAUSE","clause_number":"25.2","clause_title":"Works to be Measured","clause_text":"(a) The Project Manager shall, except as otherwise stated in the Contract, ascertain and determine by measurement the value of the Works in accordance with the Contract and the Contractor shall be paid that value in accordance with Clause 26 (Interim and Final Certificates). (b) The Project Manager shall determine the documentation to be used for measurement purposes provided that where the Project Manager considers it necessary to measure any part of the Works on Site, reasonable notice shall be given to the Contractor, who shall: (i) promptly either attend or send another qualified representative to assist the Project Manager (or a person on his behalf) in making the measurement; and If the Contractor fails to attend or send a representative, the measurement made on Site by (or on behalf of) the Project Manager shall be taken as accurate. Wherever the Project Manager determines that any part of the Works is to be measured from records prepared by the Supervision Consultant, the Contractor shall, as and when requested and within the period specified by the Supervision Consultant, attend to examine and agree such records with the Supervision Consultant and shall sign the same when agreed. If the Contractor does not attend within the period specified by the Supervision Consultant, the records shall be taken as accurate. If the Contractor examines and disagrees the records and/or does not sign them as agreed, then the Contractor shall give notice to the Supervision Consultant of the respects in which the records are claimed to be inaccurate. After receiving this notice, the Supervision Consultant shall review the records and either confirm or vary them and notify the Contractor accordingly. If the Contractor does not so give notice to the Supervision Consultant within the said period specified by the Supervision Consultant, they shall be taken as accurate. (c) The Project Manager shall, upon completion of the measurement by (or on behalf of) the Project Manager of any part of the Works, deliver to the Contractor a copy of such measurement for agreement by the Contractor. The Contractor shall, within such period of time after receipt of the said measurement as shall be required by the Project Manager, either sign the same when agreed or notify the Project Manager of those parts of such measurement which are claimed by it to be inaccurate. If the Project Manager receives a notice from the Contractor within the period specified by the Project Manager claiming that parts of such measurement are inaccurate, the Project Manager (or a person on his behalf) shall review such parts and shall either confirm or vary them and notify the Contractor accordingly. If, in respect of the measurement made by (or on behalf of) the Project Manager of any part of the Works delivered to it by the Project Manager, the Contractor does not within the period specified by the Project Manager either sign the same as agreed or give notice claiming that parts of such measurement are inaccurate, such measurement shall be taken as accurate.","general_condition":"(a) The Project Manager shall, except as otherwise stated in the Contract, ascertain and determine by measurement the value of the Works in accordance with the Contract and the Contractor shall be paid that value in accordance with Clause 26 (Interim and Final Certificates). (b) The Project Manager shall determine the documentation to be used for measurement purposes provided that where the Project Manager considers it necessary to measure any part of the Works on Site, reasonable notice shall be given to the Contractor, who shall: (i) promptly either attend or send another qualified representative to assist the Project Manager (or a person on his behalf) in making the measurement; and If the Contractor fails to attend or send a representative, the measurement made on Site by (or on behalf of) the Project Manager shall be taken as accurate. Wherever the Project Manager determines that any part of the Works is to be measured from records prepared by the Supervision Consultant, the Contractor shall, as and when requested and within the period specified by the Supervision Consultant, attend to examine and agree such records with the Supervision Consultant and shall sign the same when agreed. If the Contractor does not attend within the period specified by the Supervision Consultant, the records shall be taken as accurate. If the Contractor examines and disagrees the records and/or does not sign them as agreed, then the Contractor shall give notice to the Supervision Consultant of the respects in which the records are claimed to be inaccurate. After receiving this notice, the Supervision Consultant shall review the records and either confirm or vary them and notify the Contractor accordingly. If the Contractor does not so give notice to the Supervision Consultant within the said period specified by the Supervision Consultant, they shall be taken as accurate. (c) The Project Manager shall, upon completion of the measurement by (or on behalf of) the Project Manager of any part of the Works, deliver to the Contractor a copy of such measurement for agreement by the Contractor. The Contractor shall, within such period of time after receipt of the said measurement as shall be required by the Project Manager, either sign the same when agreed or notify the Project Manager of those parts of such measurement which are claimed by it to be inaccurate. If the Project Manager receives a notice from the Contractor within the period specified by the Project Manager claiming that parts of such measurement are inaccurate, the Project Manager (or a person on his behalf) shall review such parts and shall either confirm or vary them and notify the Contractor accordingly. If, in respect of the measurement made by (or on behalf of) the Project Manager of any part of the Works delivered to it by the Project Manager, the Contractor does not within the period specified by the Project Manager either sign the same as agreed or give notice claiming that parts of such measurement are inaccurate, such measurement shall be taken as accurate.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) The Project Manager shall, except as otherwise stated in the Contract, ascertain and determine by measurement the value of the Works in accordance with the Contract and the Contractor shall be pai...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":143}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 144, '{"itemType":"CLAUSE","clause_number":"25.3","clause_title":"Method of Measurement","clause_text":"The Works shall be measured in accordance with the Method of Measurement stated in the Bills of Quantities.","general_condition":"The Works shall be measured in accordance with the Method of Measurement stated in the Bills of Quantities.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Works shall be measured in accordance with the Method of Measurement stated in the Bills of Quantities.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":144}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 145, '{"itemType":"CLAUSE","clause_number":"25.4","clause_title":"Valuation","clause_text":"Subject to the provisions of Clause 22.3 (Valuation of Variation Orders) in relation to the valuation of Variation Orders, the rates and prices contained in the Bills of Quantities shall be used in valuing the Works measured in accordance with Clauses 25.2 (Works to be Measured) and 25.3 (Method of Measurement). Where the said Bills do not contain a rate or price for a particular measured item the Employer and the Contractor shall endeavour to agree a suitable rate or price. In the event of failure to agree as aforesaid the Employer shall fix a rate or price which, in his opinion, is appropriate and shall notify the Contractor accordingly with a copy to the Project Manager. The Contractor shall submit to the Employer all such documents, evidence and calculations as the Employer shall require for the purpose of agreeing a suitable rate or price as aforesaid.","general_condition":"Subject to the provisions of Clause 22.3 (Valuation of Variation Orders) in relation to the valuation of Variation Orders, the rates and prices contained in the Bills of Quantities shall be used in valuing the Works measured in accordance with Clauses 25.2 (Works to be Measured) and 25.3 (Method of Measurement). Where the said Bills do not contain a rate or price for a particular measured item the Employer and the Contractor shall endeavour to agree a suitable rate or price. In the event of failure to agree as aforesaid the Employer shall fix a rate or price which, in his opinion, is appropriate and shall notify the Contractor accordingly with a copy to the Project Manager. The Contractor shall submit to the Employer all such documents, evidence and calculations as the Employer shall require for the purpose of agreeing a suitable rate or price as aforesaid.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Subject to the provisions of Clause 22.3 (Valuation of Variation Orders) in relation to the valuation of Variation Orders, the rates and prices contained in the Bills of Quantities shall be used in va...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":145}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 146, '{"itemType":"CLAUSE","clause_number":"26.1","clause_title":"Application for Interim Certificates","clause_text":"(a) After the Commencement Date, the Contractor shall submit to the Project Manager in triplicate in such form and with such detail as the Project Manager shall require an Interim Certificate application signed by the Contractor''s Representative for an Interim Certificate for the Advance Payment. (b) After the end of each calendar month the Contractor shall submit to the Project Manager in triplicate in such form and with such detail as the Project Manager shall require an Interim Certificate application signed by the Contractor''s Representative for an Interim Certificate stating: (i) the estimated Contract value of the Works carried out on Site up to the end of that month; (ii) ninety per cent (90%) of the estimated net direct cost delivered to Site of all Plant on Site at the end of that month intended exclusively for incorporation in the Works but not yet so incorporated; less however any such Plant which: (1) has been delivered to Site in breach of paragraph (b) of Clause 11.9 (Delivery to Site) unless such breach has by that time been waived by the Project Manager in writing; or (2) it was not reasonable to deliver to Site by that time having regard to the requirements of the Programme; or (3) has not by that time been placed in appropriate storage and properly protected to the satisfaction of the Project Manager and the Supervision Consultant; (iii) any other amounts to which the Contractor considers itself to be entitled at the end of that month under any term, condition or provision of the Contract or otherwise in connection with or arising out of the Works or any Section or Portion thereof. Such Interim Certificate application shall show separately the Contract values and amounts within paragraphs (i), (ii) and (iii) of this Sub-Clause which relate to work done and/or Plant provided by (or other entitlements of) each Nominated Subcontractor. (c) After the issue of a Taking-Over Certificate for a Section or Portion of the Works, the Contractor may submit to the Project Manager in triplicate in such form and with such detail as the Project Manager shall require an Interim Certificate application signed by the Contractor''s Representative for an Interim Certificate stating the amount equal to fifty per cent (50%) of the amount of retention held in respect of the said Section or Portion as part of the Project Manager''s computation of the amount which the Contractor was entitled to be paid as shown in the latest Interim Certificate to be issued in accordance with Clause 26.2(b) (Issue of Interim Certificates). (d) The Interim Certificate applications referred to in Sub-Clauses (a), (b) and (c) of this Clause 26.1 shall be accompanied by all relevant time sheets, quotations, invoices, vouchers, accounts, receipts and any other documents and evidence which the Project Manager shall require in order to substantiate such application. (e) The Contractor shall submit to the Project Manager within twenty eight (28) days after the Commencement Date a proposed breakdown of each lump sum price in the Bills of Quantities. The Project Manager may take account of the said breakdown when considering each Interim Certificate application and Final Certificate application but shall not be bound by it.","general_condition":"(a) After the Commencement Date, the Contractor shall submit to the Project Manager in triplicate in such form and with such detail as the Project Manager shall require an Interim Certificate application signed by the Contractor''s Representative for an Interim Certificate for the Advance Payment. (b) After the end of each calendar month the Contractor shall submit to the Project Manager in triplicate in such form and with such detail as the Project Manager shall require an Interim Certificate application signed by the Contractor''s Representative for an Interim Certificate stating: (i) the estimated Contract value of the Works carried out on Site up to the end of that month; (ii) ninety per cent (90%) of the estimated net direct cost delivered to Site of all Plant on Site at the end of that month intended exclusively for incorporation in the Works but not yet so incorporated; less however any such Plant which: (1) has been delivered to Site in breach of paragraph (b) of Clause 11.9 (Delivery to Site) unless such breach has by that time been waived by the Project Manager in writing; or (2) it was not reasonable to deliver to Site by that time having regard to the requirements of the Programme; or (3) has not by that time been placed in appropriate storage and properly protected to the satisfaction of the Project Manager and the Supervision Consultant; (iii) any other amounts to which the Contractor considers itself to be entitled at the end of that month under any term, condition or provision of the Contract or otherwise in connection with or arising out of the Works or any Section or Portion thereof. Such Interim Certificate application shall show separately the Contract values and amounts within paragraphs (i), (ii) and (iii) of this Sub-Clause which relate to work done and/or Plant provided by (or other entitlements of) each Nominated Subcontractor. (c) After the issue of a Taking-Over Certificate for a Section or Portion of the Works, the Contractor may submit to the Project Manager in triplicate in such form and with such detail as the Project Manager shall require an Interim Certificate application signed by the Contractor''s Representative for an Interim Certificate stating the amount equal to fifty per cent (50%) of the amount of retention held in respect of the said Section or Portion as part of the Project Manager''s computation of the amount which the Contractor was entitled to be paid as shown in the latest Interim Certificate to be issued in accordance with Clause 26.2(b) (Issue of Interim Certificates). (d) The Interim Certificate applications referred to in Sub-Clauses (a), (b) and (c) of this Clause 26.1 shall be accompanied by all relevant time sheets, quotations, invoices, vouchers, accounts, receipts and any other documents and evidence which the Project Manager shall require in order to substantiate such application. (e) The Contractor shall submit to the Project Manager within twenty eight (28) days after the Commencement Date a proposed breakdown of each lump sum price in the Bills of Quantities. The Project Manager may take account of the said breakdown when considering each Interim Certificate application and Final Certificate application but shall not be bound by it.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) After the Commencement Date, the Contractor shall submit to the Project Manager in triplicate in such form and with such detail as the Project Manager shall require an Interim Certificate applicat...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":146}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 147, '{"itemType":"CLAUSE","clause_number":"26.2","clause_title":"Issue of Interim Certificates","clause_text":"(a) The Project Manager shall consider the Interim Certificate application submitted to him in accordance with Sub-Clause (a) of Clause 26.1 (Application for Interim Certificates) and provided that the Project Manager has received from the Contractor the Performance Bond and the Advance Payment Bond due to be obtained and delivered in accordance with Clause 7.1 (Provision of Bonds) the Project Manager shall prepare an Interim Certificate for the payment of the Advance Payment. (b) The Project Manager shall consider each Interim Certificate application (and accompanying documents and evidence) submitted to him in accordance with Sub-Clause (b) of Clause 26.1 (Application for Interim Certificates) and from the information contained therein and the accompanying documents and evidence, the Bills of Quantities and any other information which he may deem relevant shall prepare an Interim Certificate stating the total amount which the Contractor is, in the opinion of the Project Manager, entitled to be paid, such amount being computed in the following manner: (i) the Project Manager''s estimate of the Contract value of the Works carried out on Site up to the end of the relevant month; plus (ii) ninety per cent (90%) of the Project Manager''s estimate of the net direct cost delivered to Site, as proven to the satisfaction of the Project Manager, of all Plant on Site at the end of the relevant month intended exclusively for incorporation in the Works but not yet so incorporated; less however any such Plant within any of sub-paragraphs (1) to (3) inclusive of paragraph (ii) of Sub-Clause (b) of Clause 26.1 (Application for Interim Certificates); plus (iii) any other amounts to which the Project Manager considers the Contractor to be entitled under any term, condition or provision of the Contract or otherwise in connection with or arising out of the Works or any Section or Portion thereof; less (iv) the amount calculated by multiplying the total of paragraphs (i) to (iii) inclusive of this Sub-Clause (b) by the percentage that the Advance Payment is of the Contract Sum as partial recovery of the Advance Payment; less (v) the amount calculated by multiplying the total of paragraphs (i) to (iii) inclusive of this Sub-Clause (b) by the percentage stated in the Particular Conditions as retention; less (vi) any amounts which the Project Manager considers the Employer or the Project Manager entitled to deduct or otherwise recover from the Contractor under any term, condition or provision of the Contract or otherwise except where such amounts have been recovered from the Contractor by the Employer as a debt due from the Contractor or have been deducted by the Employer from monies due to the Contractor; and less (vii) except in the case of the first Interim Certificate issued under this Sub-Clause (b), the nett total of paragraphs (i) to (vi) inclusive of this Sub-Clause (b) as applied to the immediately previous Interim Certificate issued under this Sub-Clause (b). (c) The Project Manager shall consider each Interim Certificate application (and accompanying documents and evidence) submitted to him in accordance with Sub-Clause (c) of Clause 26.1 (Application for Interim Certificates) and, in respect of the Section or Portion of the Works to which the said Interim Certificate application relates, shall prepare an Interim Certificate for the payment of fifty per cent (50%) of the amount of retention held in respect of the said Section or Portion as part of the Project Manager''s computation of the amount which the Contractor was entitled to be paid as shown in the latest Interim Certificate to be issued in accordance with Clause 26.2(b) (Issue of Interim Certificates). (d) Notwithstanding the foregoing, the maximum deduction to be made under this Clause 26.2 with respect to the Advance Payment shall be equal to the amount of such Advance Payment. If, however, at any time or times in the opinion of the Project Manager such deduction at the percentage stipulated in this Clause 26.2 will be insufficient to recover to the Employer the full amount of such Advance Payment the Project Manager may increase such deduction to such percentage as will in his opinion be sufficient for that purpose. (e) The balance of the retention held under this Clause 26.2 (Issue of Interim Certificates) shall be included in the calculation of the final nett amounts remaining payable to the Contractor or the Employer as the case may be in accordance with Clause 26.4 (Issue of Final Certificates). (f) Without in any way limiting the Project Manager''s right to exclude from any Interim Certificate any amount which in his opinion the Contractor is not entitled to be paid as a result of the application of any term, condition or provision of the Contract, the Project Manager shall have power to exclude from any Interim Certificate any work or Plant which in the Supervision Consultant''s opinion is defective or otherwise not in accordance with the Contract. The Project Manager''s aforesaid right shall also not be in any way limited or prejudiced by the inclusion of any particular amount in any previous Interim Certificate and accordingly such amount may be wholly or partly excluded in any subsequent Interim Certificate if in the Project Manager''s opinion at the time of the subsequent Certificate the Contractor is not entitled to be paid the same. The Project Manager may in any Interim Certificate make any correction or modification that should properly be made in respect of any previous Interim Certificate. (g) Within seven (7) days after receipt by the Project Manager from the Contractor of the Interim Certificate application duly completed in accordance with Sub-Clause (a) of Clause 26.1 (Application for Interim Certificates) and accompanied by all such documents and evidence relevant thereto as are referred to in Sub-Clause (d) of the said Clause 26.1 or within seven (7) days after receipt by the Project Manager from the Contractor of the Performance Bond and the Advance Payment Bond due to be obtained and delivered in accordance with Clause 7.1 (Provision of Bonds), whichever date of receipt is the later, the Project Manager shall complete his preparation of the Interim Certificate, sign and submit the same to the Employer and a copy to the Contractor. (h) Within thirty (30) days after receipt by the Project Manager from the Contractor of any Interim Certificate application duly completed in accordance with Sub-Clauses (b) or (c) of Clause 26.1 (Application for Interim Certificates), and accompanied by all such documents and evidence relevant thereto as are referred to in Sub-Clause (d) of Clause 26.1 (Application for Interim Certificates) and provided that the Project Manager has received from the Contractor the Performance Bond due to be obtained and delivered in accordance with Clause 7.1 (Provision of Bonds) the Project Manager shall complete his preparation of the Interim Certificate, sign and submit the same to the Employer and a copy to the Contractor. (i) Each Interim Certificate is provisional only and shall not be relied upon as evidence of any matter or thing stated therein or omitted therefrom nor shall it in any way prejudice or otherwise affect the rights or obligations of the Employer, the Project Manager, the Supervision Consultant or the Contractor under the terms of the Contract.","general_condition":"(a) The Project Manager shall consider the Interim Certificate application submitted to him in accordance with Sub-Clause (a) of Clause 26.1 (Application for Interim Certificates) and provided that the Project Manager has received from the Contractor the Performance Bond and the Advance Payment Bond due to be obtained and delivered in accordance with Clause 7.1 (Provision of Bonds) the Project Manager shall prepare an Interim Certificate for the payment of the Advance Payment. (b) The Project Manager shall consider each Interim Certificate application (and accompanying documents and evidence) submitted to him in accordance with Sub-Clause (b) of Clause 26.1 (Application for Interim Certificates) and from the information contained therein and the accompanying documents and evidence, the Bills of Quantities and any other information which he may deem relevant shall prepare an Interim Certificate stating the total amount which the Contractor is, in the opinion of the Project Manager, entitled to be paid, such amount being computed in the following manner: (i) the Project Manager''s estimate of the Contract value of the Works carried out on Site up to the end of the relevant month; plus (ii) ninety per cent (90%) of the Project Manager''s estimate of the net direct cost delivered to Site, as proven to the satisfaction of the Project Manager, of all Plant on Site at the end of the relevant month intended exclusively for incorporation in the Works but not yet so incorporated; less however any such Plant within any of sub-paragraphs (1) to (3) inclusive of paragraph (ii) of Sub-Clause (b) of Clause 26.1 (Application for Interim Certificates); plus (iii) any other amounts to which the Project Manager considers the Contractor to be entitled under any term, condition or provision of the Contract or otherwise in connection with or arising out of the Works or any Section or Portion thereof; less (iv) the amount calculated by multiplying the total of paragraphs (i) to (iii) inclusive of this Sub-Clause (b) by the percentage that the Advance Payment is of the Contract Sum as partial recovery of the Advance Payment; less (v) the amount calculated by multiplying the total of paragraphs (i) to (iii) inclusive of this Sub-Clause (b) by the percentage stated in the Particular Conditions as retention; less (vi) any amounts which the Project Manager considers the Employer or the Project Manager entitled to deduct or otherwise recover from the Contractor under any term, condition or provision of the Contract or otherwise except where such amounts have been recovered from the Contractor by the Employer as a debt due from the Contractor or have been deducted by the Employer from monies due to the Contractor; and less (vii) except in the case of the first Interim Certificate issued under this Sub-Clause (b), the nett total of paragraphs (i) to (vi) inclusive of this Sub-Clause (b) as applied to the immediately previous Interim Certificate issued under this Sub-Clause (b). (c) The Project Manager shall consider each Interim Certificate application (and accompanying documents and evidence) submitted to him in accordance with Sub-Clause (c) of Clause 26.1 (Application for Interim Certificates) and, in respect of the Section or Portion of the Works to which the said Interim Certificate application relates, shall prepare an Interim Certificate for the payment of fifty per cent (50%) of the amount of retention held in respect of the said Section or Portion as part of the Project Manager''s computation of the amount which the Contractor was entitled to be paid as shown in the latest Interim Certificate to be issued in accordance with Clause 26.2(b) (Issue of Interim Certificates). (d) Notwithstanding the foregoing, the maximum deduction to be made under this Clause 26.2 with respect to the Advance Payment shall be equal to the amount of such Advance Payment. If, however, at any time or times in the opinion of the Project Manager such deduction at the percentage stipulated in this Clause 26.2 will be insufficient to recover to the Employer the full amount of such Advance Payment the Project Manager may increase such deduction to such percentage as will in his opinion be sufficient for that purpose. (e) The balance of the retention held under this Clause 26.2 (Issue of Interim Certificates) shall be included in the calculation of the final nett amounts remaining payable to the Contractor or the Employer as the case may be in accordance with Clause 26.4 (Issue of Final Certificates). (f) Without in any way limiting the Project Manager''s right to exclude from any Interim Certificate any amount which in his opinion the Contractor is not entitled to be paid as a result of the application of any term, condition or provision of the Contract, the Project Manager shall have power to exclude from any Interim Certificate any work or Plant which in the Supervision Consultant''s opinion is defective or otherwise not in accordance with the Contract. The Project Manager''s aforesaid right shall also not be in any way limited or prejudiced by the inclusion of any particular amount in any previous Interim Certificate and accordingly such amount may be wholly or partly excluded in any subsequent Interim Certificate if in the Project Manager''s opinion at the time of the subsequent Certificate the Contractor is not entitled to be paid the same. The Project Manager may in any Interim Certificate make any correction or modification that should properly be made in respect of any previous Interim Certificate. (g) Within seven (7) days after receipt by the Project Manager from the Contractor of the Interim Certificate application duly completed in accordance with Sub-Clause (a) of Clause 26.1 (Application for Interim Certificates) and accompanied by all such documents and evidence relevant thereto as are referred to in Sub-Clause (d) of the said Clause 26.1 or within seven (7) days after receipt by the Project Manager from the Contractor of the Performance Bond and the Advance Payment Bond due to be obtained and delivered in accordance with Clause 7.1 (Provision of Bonds), whichever date of receipt is the later, the Project Manager shall complete his preparation of the Interim Certificate, sign and submit the same to the Employer and a copy to the Contractor. (h) Within thirty (30) days after receipt by the Project Manager from the Contractor of any Interim Certificate application duly completed in accordance with Sub-Clauses (b) or (c) of Clause 26.1 (Application for Interim Certificates), and accompanied by all such documents and evidence relevant thereto as are referred to in Sub-Clause (d) of Clause 26.1 (Application for Interim Certificates) and provided that the Project Manager has received from the Contractor the Performance Bond due to be obtained and delivered in accordance with Clause 7.1 (Provision of Bonds) the Project Manager shall complete his preparation of the Interim Certificate, sign and submit the same to the Employer and a copy to the Contractor. (i) Each Interim Certificate is provisional only and shall not be relied upon as evidence of any matter or thing stated therein or omitted therefrom nor shall it in any way prejudice or otherwise affect the rights or obligations of the Employer, the Project Manager, the Supervision Consultant or the Contractor under the terms of the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) The Project Manager shall consider the Interim Certificate application submitted to him in accordance with Sub-Clause (a) of Clause 26.1 (Application for Interim Certificates) and provided that th...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":147}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 148, '{"itemType":"CLAUSE","clause_number":"26.3","clause_title":"Application for Final Certificates","clause_text":"When: (a) at least fourteen (14) days have elapsed after the expiration of the Defects Liability Period for a Section or Portion of the Works and the Contractor has in relation thereto performed all its obligations under Clause 21 (Defects), has finished any work related to the said Section or Portion of the Works which had not been completed at the time of issue of the Taking-Over Certificate in respect thereof, and has to the satisfaction of the Project Manager and the Supervision Consultant observed and performed all its other obligations and liabilities (including without limitation those under paragraph (b) of Clause 5.7 (As-Built Drawings, Operations and Maintenance Manuals) and Clause 25A (Extended Warranties from Subcontractors) but excluding any which have not yet arisen under any Extended Warranty still continuing at the time of submission of the relevant Final Certificate application) relating to the said Section or Portion of the Works on its part to be so observed or performed in accordance with the Contract; and (b) the Contractor has prepared and submitted to the Project Manager in triplicate a final account (together with all such documents and evidence as the Project Manager may require in order to substantiate the same) in respect of the said Section or Portion of the Works which shows and breaks down in such form and with such detail as the Project Manager may require all amounts which in the Contractor''s opinion are payable under the Contract in respect of the said Section or Portion of the Works and such final account has been adjusted as appropriate and agreed and signed by the Project Manager and the Contractor; the Contractor shall submit to the Project Manager in triplicate a Final Certificate application signed by the Contractor''s Representative for that Section or Portion of the Works which confirms the due performance of all the requirements of paragraphs (a) and (b) of this Clause. The application for the last Final Certificate shall be accompanied by a written discharge, signed by the Contractor''s Representative, confirming that the total of the final accounts for all Sections and Portions of the Works represents full and final settlement of all monies due to the Contractor under or in connection with the Contract.","general_condition":"When: (a) at least fourteen (14) days have elapsed after the expiration of the Defects Liability Period for a Section or Portion of the Works and the Contractor has in relation thereto performed all its obligations under Clause 21 (Defects), has finished any work related to the said Section or Portion of the Works which had not been completed at the time of issue of the Taking-Over Certificate in respect thereof, and has to the satisfaction of the Project Manager and the Supervision Consultant observed and performed all its other obligations and liabilities (including without limitation those under paragraph (b) of Clause 5.7 (As-Built Drawings, Operations and Maintenance Manuals) and Clause 25A (Extended Warranties from Subcontractors) but excluding any which have not yet arisen under any Extended Warranty still continuing at the time of submission of the relevant Final Certificate application) relating to the said Section or Portion of the Works on its part to be so observed or performed in accordance with the Contract; and (b) the Contractor has prepared and submitted to the Project Manager in triplicate a final account (together with all such documents and evidence as the Project Manager may require in order to substantiate the same) in respect of the said Section or Portion of the Works which shows and breaks down in such form and with such detail as the Project Manager may require all amounts which in the Contractor''s opinion are payable under the Contract in respect of the said Section or Portion of the Works and such final account has been adjusted as appropriate and agreed and signed by the Project Manager and the Contractor; the Contractor shall submit to the Project Manager in triplicate a Final Certificate application signed by the Contractor''s Representative for that Section or Portion of the Works which confirms the due performance of all the requirements of paragraphs (a) and (b) of this Clause. The application for the last Final Certificate shall be accompanied by a written discharge, signed by the Contractor''s Representative, confirming that the total of the final accounts for all Sections and Portions of the Works represents full and final settlement of all monies due to the Contractor under or in connection with the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"When: (a) at least fourteen (14) days have elapsed after the expiration of the Defects Liability Period for a Section or Portion of the Works and the Contractor has in relation thereto performed all i...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":148}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 149, '{"itemType":"CLAUSE","clause_number":"26.4","clause_title":"Issue of Final Certificates","clause_text":"Following receipt of each Final Certificate application in accordance with Clause 26.3 (Application for Final Certificates) the Project Manager shall prepare a Final Certificate for the relevant Section or Portion of the Works which shall state the amounts shown by the agreed final account as payable to the Contractor in respect of the said Section or Portion of the Works, shall show the total deduction to be made from those amounts by virtue of payments already made to the Contractor pursuant to Interim Certificates or otherwise and amounts payable to the Employer from the Contractor under any term, condition or provision of the Contract or otherwise in connection with or arising out of the Works including those amounts which the Contract specifically provides shall be recoverable from the Contractor by the Employer as a debt due from the Contractor or may be deducted by the Employer from any monies due or which may become due to the Contractor, and shall show the final nett amounts accordingly remaining payable to the Contractor or the Employer as the case may be. If at the time of preparation of any Final Certificate there is under any term, condition or provision of the Contract or otherwise in connection with or arising out of the Works any amount to which the Project Manager considers the Contractor to be entitled or which the Project Manager considers the Employer or the Project Manager entitled to deduct or otherwise recover from the Contractor, and if such amount does not in the Project Manager''s opinion relate specifically to any particular Section(s) or Portion(s) of the Works and has not been included in any previous Final Certificate, the Project Manager shall include the same at the end of the Final Certificate then being prepared as an addition to or deduction from (as appropriate) the figures which would otherwise be the final nett amounts shown by that Final Certificate. Within thirty (30) days after receipt from the Contractor of any Final Certificate application duly completed in accordance with Clause 26.3 (Application for Final Certificates) and provided that all the requirements of Clause 26.3 (Application for Final Certificates) have been performed in respect of the relevant Section or Portion of the Works, the Project Manager shall complete his preparation of the relevant Final Certificate in draft, and submit copies of the same to the Employer and to the Supervision Consultant. Thereafter the Project Manager shall consult with the Employer, the Supervision Consultant and the Contractor as necessary, and when the Employer and the Supervision Consultant agree with the contents of the proposed Final Certificate the Project Manager, the Employer and the Supervision Consultant shall sign the Final Certificate and the Project Manager shall issue it to the Contractor. If all the requirements of Clause 26.3 (Application for Final Certificates) have not been performed in respect of the relevant Section or Portion of the Works the Project Manager shall not prepare a Final Certificate but shall, within thirty (30) days after receipt from the Contractor of the Final Certificate application, give written notice to the Contractor specifying in the opinion of the Project Manager and/or the Employer and/or the Supervision Consultant those requirements of the said Clause 26.3 which have not been performed. Once all such requirements have been performed the Contractor shall repeat the submission of the Final Certificate application in accordance with the said Clause 26.3. A Final Certificate issued in accordance with this Clause for any Section or Portion of the Works shall (subject as hereinafter provided) be conclusive evidence of the matters stated in paragraph (a) of Clause 26.3 (Application for Final Certificates), of the final nett amounts remaining payable to the Contractor or the Employer (as the case may be) in respect of the said Section or Portion of the Works, and of any addition to or deduction from those final nett amounts to be made as provided in this Clause provided that: (i) no Final Certificate shall prejudice the continued validity and enforceability in accordance with its terms of any Extended Warranty or any warranty implied by law in relation to the relevant Section or Portion of the Works or any part thereof; (ii) no Final Certificate shall relieve the Contractor of any and all of its continuing liabilities and obligations provided by the Contract including but not limited to those provided in Clause 12.10 (Inherent Defects) and Clause 15 (Secrecy); (iii) no Final Certificate shall be conclusive evidence as aforesaid if there has been fraud or dishonesty on the part of the Contractor; and (iv) no Final Certificate shall be conclusive evidence as aforesaid with respect to any matter or thing of which either party has either before or within ninety (90) days after the date of issue of that Final Certificate given written notice of dispute to the other party in accordance with Clause 32 (Notices) specifying in detail the matter or thing disputed. The last Final Certificate issued in accordance with this Clause shall, in addition to being (subject as provided above) conclusive evidence of the matters stated above, be (subject as hereinafter provided) conclusive evidence of the performance and observance by the Employer of each and every obligation and liability on the Employer''s part to be so performed or observed under or in accordance with the Contract or otherwise in relation to the Works or any Section or Portion thereof provided that the last Final Certificate shall not itself be conclusive evidence of the payment by the Employer of any amount shown therein as remaining payable to the Contractor.","general_condition":"Following receipt of each Final Certificate application in accordance with Clause 26.3 (Application for Final Certificates) the Project Manager shall prepare a Final Certificate for the relevant Section or Portion of the Works which shall state the amounts shown by the agreed final account as payable to the Contractor in respect of the said Section or Portion of the Works, shall show the total deduction to be made from those amounts by virtue of payments already made to the Contractor pursuant to Interim Certificates or otherwise and amounts payable to the Employer from the Contractor under any term, condition or provision of the Contract or otherwise in connection with or arising out of the Works including those amounts which the Contract specifically provides shall be recoverable from the Contractor by the Employer as a debt due from the Contractor or may be deducted by the Employer from any monies due or which may become due to the Contractor, and shall show the final nett amounts accordingly remaining payable to the Contractor or the Employer as the case may be. If at the time of preparation of any Final Certificate there is under any term, condition or provision of the Contract or otherwise in connection with or arising out of the Works any amount to which the Project Manager considers the Contractor to be entitled or which the Project Manager considers the Employer or the Project Manager entitled to deduct or otherwise recover from the Contractor, and if such amount does not in the Project Manager''s opinion relate specifically to any particular Section(s) or Portion(s) of the Works and has not been included in any previous Final Certificate, the Project Manager shall include the same at the end of the Final Certificate then being prepared as an addition to or deduction from (as appropriate) the figures which would otherwise be the final nett amounts shown by that Final Certificate. Within thirty (30) days after receipt from the Contractor of any Final Certificate application duly completed in accordance with Clause 26.3 (Application for Final Certificates) and provided that all the requirements of Clause 26.3 (Application for Final Certificates) have been performed in respect of the relevant Section or Portion of the Works, the Project Manager shall complete his preparation of the relevant Final Certificate in draft, and submit copies of the same to the Employer and to the Supervision Consultant. Thereafter the Project Manager shall consult with the Employer, the Supervision Consultant and the Contractor as necessary, and when the Employer and the Supervision Consultant agree with the contents of the proposed Final Certificate the Project Manager, the Employer and the Supervision Consultant shall sign the Final Certificate and the Project Manager shall issue it to the Contractor. If all the requirements of Clause 26.3 (Application for Final Certificates) have not been performed in respect of the relevant Section or Portion of the Works the Project Manager shall not prepare a Final Certificate but shall, within thirty (30) days after receipt from the Contractor of the Final Certificate application, give written notice to the Contractor specifying in the opinion of the Project Manager and/or the Employer and/or the Supervision Consultant those requirements of the said Clause 26.3 which have not been performed. Once all such requirements have been performed the Contractor shall repeat the submission of the Final Certificate application in accordance with the said Clause 26.3. A Final Certificate issued in accordance with this Clause for any Section or Portion of the Works shall (subject as hereinafter provided) be conclusive evidence of the matters stated in paragraph (a) of Clause 26.3 (Application for Final Certificates), of the final nett amounts remaining payable to the Contractor or the Employer (as the case may be) in respect of the said Section or Portion of the Works, and of any addition to or deduction from those final nett amounts to be made as provided in this Clause provided that: (i) no Final Certificate shall prejudice the continued validity and enforceability in accordance with its terms of any Extended Warranty or any warranty implied by law in relation to the relevant Section or Portion of the Works or any part thereof; (ii) no Final Certificate shall relieve the Contractor of any and all of its continuing liabilities and obligations provided by the Contract including but not limited to those provided in Clause 12.10 (Inherent Defects) and Clause 15 (Secrecy); (iii) no Final Certificate shall be conclusive evidence as aforesaid if there has been fraud or dishonesty on the part of the Contractor; and (iv) no Final Certificate shall be conclusive evidence as aforesaid with respect to any matter or thing of which either party has either before or within ninety (90) days after the date of issue of that Final Certificate given written notice of dispute to the other party in accordance with Clause 32 (Notices) specifying in detail the matter or thing disputed. The last Final Certificate issued in accordance with this Clause shall, in addition to being (subject as provided above) conclusive evidence of the matters stated above, be (subject as hereinafter provided) conclusive evidence of the performance and observance by the Employer of each and every obligation and liability on the Employer''s part to be so performed or observed under or in accordance with the Contract or otherwise in relation to the Works or any Section or Portion thereof provided that the last Final Certificate shall not itself be conclusive evidence of the payment by the Employer of any amount shown therein as remaining payable to the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Following receipt of each Final Certificate application in accordance with Clause 26.3 (Application for Final Certificates) the Project Manager shall prepare a Final Certificate for the relevant Secti...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":149}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 150, '{"itemType":"CLAUSE","clause_number":"27.1","clause_title":"Time of Payment","clause_text":"Subject to Clause 27.2 (Employer''s Right of Set-Off): (a) within fourteen (14) days after the date of issue by the Project Manager of an Interim Certificate in accordance with Sub-Clause (g) of Clause 26.2 (Issue of Interim Certificates); or (b) within thirty (30) days after the date of issue by the Project Manager of any Interim Certificate in accordance with Sub-Clause (h) of Clause 26.2 (Issue of Interim Certificates) or any Final Certificate in accordance with Clause 26.4 (Issue of Final Certificates); the Employer shall pay to the Contractor or the Contractor shall pay to the Employer (as the circumstances may require) the nett amount or final nett amount(s) shown by the relevant Interim Certificate or Final Certificate.","general_condition":"Subject to Clause 27.2 (Employer''s Right of Set-Off): (a) within fourteen (14) days after the date of issue by the Project Manager of an Interim Certificate in accordance with Sub-Clause (g) of Clause 26.2 (Issue of Interim Certificates); or (b) within thirty (30) days after the date of issue by the Project Manager of any Interim Certificate in accordance with Sub-Clause (h) of Clause 26.2 (Issue of Interim Certificates) or any Final Certificate in accordance with Clause 26.4 (Issue of Final Certificates); the Employer shall pay to the Contractor or the Contractor shall pay to the Employer (as the circumstances may require) the nett amount or final nett amount(s) shown by the relevant Interim Certificate or Final Certificate.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Subject to Clause 27.2 (Employer''s Right of Set-Off): (a) within fourteen (14) days after the date of issue by the Project Manager of an Interim Certificate in accordance with Sub-Clause (g) of Clause...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":150}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 151, '{"itemType":"CLAUSE","clause_number":"27.2","clause_title":"Employer''s Right of Set-Off","clause_text":"The Employer shall be entitled to deduct and recover from any payment under Clause 27.1 (Time of Payment) any amounts due to the Employer from the Contractor.","general_condition":"The Employer shall be entitled to deduct and recover from any payment under Clause 27.1 (Time of Payment) any amounts due to the Employer from the Contractor.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Employer shall be entitled to deduct and recover from any payment under Clause 27.1 (Time of Payment) any amounts due to the Employer from the Contractor.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":151}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 152, '{"itemType":"CLAUSE","clause_number":"27.3","clause_title":"Currency of Payment","clause_text":"All payments to the Contractor by the Employer and to the Employer by the Contractor shall be made in the Local Currency.","general_condition":"All payments to the Contractor by the Employer and to the Employer by the Contractor shall be made in the Local Currency.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"All payments to the Contractor by the Employer and to the Employer by the Contractor shall be made in the Local Currency.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":152}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 153, '{"itemType":"CLAUSE","clause_number":"27.4","clause_title":"Place of Payment","clause_text":"All payments to be made under or in accordance with the Contract or otherwise in relation to the Works or any Section or Portion thereof shall be made to such bank account of the relevant payee in the Country as the payee may designate.","general_condition":"All payments to be made under or in accordance with the Contract or otherwise in relation to the Works or any Section or Portion thereof shall be made to such bank account of the relevant payee in the Country as the payee may designate.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"All payments to be made under or in accordance with the Contract or otherwise in relation to the Works or any Section or Portion thereof shall be made to such bank account of the relevant payee in the...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":153}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 154, '{"itemType":"CLAUSE","clause_number":"27.5","clause_title":"Third Party Payments","clause_text":"(a) No commission, fee, payment, gift or other benefit of a like or similar kind has been or will in the future be made by the Contractor to any third party (which expression shall without limitation include any consultant, servant, agent, employee, sponsor or distributor of the Employer) in connection with or arising out of the Contract. (b) If the Contractor breaches paragraph (a) of this Clause 27.5 at any time or if the Employer reasonably believes that the Contractor has breached paragraph (a) of this Clause 27.5 at any time, the Employer may terminate the Contractor''s employment under Clause 31.1 (Employer may Terminate Contractor''s Employment) and recover damages from the Contractor whether for consequential loss or otherwise. (c) Nothing herein shall prevent the Contractor from making bona fide payments to its Country sponsor or agent in accordance with a properly registered and recorded agreement, or to its employees or Subcontractors.","general_condition":"(a) No commission, fee, payment, gift or other benefit of a like or similar kind has been or will in the future be made by the Contractor to any third party (which expression shall without limitation include any consultant, servant, agent, employee, sponsor or distributor of the Employer) in connection with or arising out of the Contract. (b) If the Contractor breaches paragraph (a) of this Clause 27.5 at any time or if the Employer reasonably believes that the Contractor has breached paragraph (a) of this Clause 27.5 at any time, the Employer may terminate the Contractor''s employment under Clause 31.1 (Employer may Terminate Contractor''s Employment) and recover damages from the Contractor whether for consequential loss or otherwise. (c) Nothing herein shall prevent the Contractor from making bona fide payments to its Country sponsor or agent in accordance with a properly registered and recorded agreement, or to its employees or Subcontractors.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) No commission, fee, payment, gift or other benefit of a like or similar kind has been or will in the future be made by the Contractor to any third party (which expression shall without limitation ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":154}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 155, '{"itemType":"CLAUSE","clause_number":"28.1","clause_title":"Fixed Rates and Prices","clause_text":"The rates and prices in the Bills of Quantities are fixed for the duration of the Contract and shall not be subject to any adjustment in respect of rise or fall in the cost of labour, materials, Plant, Contractor''s Equipment, transport or any other matters affecting the cost of execution of the Contract.","general_condition":"The rates and prices in the Bills of Quantities are fixed for the duration of the Contract and shall not be subject to any adjustment in respect of rise or fall in the cost of labour, materials, Plant, Contractor''s Equipment, transport or any other matters affecting the cost of execution of the Contract.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The rates and prices in the Bills of Quantities are fixed for the duration of the Contract and shall not be subject to any adjustment in respect of rise or fall in the cost of labour, materials, Plant...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":155}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 156, '{"itemType":"CLAUSE","clause_number":"29.1","clause_title":"Frustration","clause_text":"Notwithstanding any other provision of this Clause 29 (Frustration and Force Majeure), if an event or circumstance outside the control of both parties arises after the date of the Contract rendering in the opinion of the Project Manager performance of the Contract impossible such that under the law governing the Contract specified in Clause 4.2 (Governing Law) the Contract is frustrated then upon notice by either party to the other party of such event or circumstance both parties shall be released from further performance, the Contract shall be deemed to have been terminated under this Clause without prejudice to any rights or obligations which may have accrued due between the parties prior to such termination and the sum payable by the Employer to the Contractor shall be determined and paid in accordance with Clause 29.8 (Payment on Frustration or Force Majeure) however obligations under the Contract which survive such termination including without limitation Clause 12.10 (Inherent Defects), Clause 15 (Secrecy) and Clause 25A (Extended Warranties from Subcontractors) shall continue notwithstanding any such termination.","general_condition":"Notwithstanding any other provision of this Clause 29 (Frustration and Force Majeure), if an event or circumstance outside the control of both parties arises after the date of the Contract rendering in the opinion of the Project Manager performance of the Contract impossible such that under the law governing the Contract specified in Clause 4.2 (Governing Law) the Contract is frustrated then upon notice by either party to the other party of such event or circumstance both parties shall be released from further performance, the Contract shall be deemed to have been terminated under this Clause without prejudice to any rights or obligations which may have accrued due between the parties prior to such termination and the sum payable by the Employer to the Contractor shall be determined and paid in accordance with Clause 29.8 (Payment on Frustration or Force Majeure) however obligations under the Contract which survive such termination including without limitation Clause 12.10 (Inherent Defects), Clause 15 (Secrecy) and Clause 25A (Extended Warranties from Subcontractors) shall continue notwithstanding any such termination.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Notwithstanding any other provision of this Clause 29 (Frustration and Force Majeure), if an event or circumstance outside the control of both parties arises after the date of the Contract rendering i...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":156}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 157, '{"itemType":"CLAUSE","clause_number":"29.2","clause_title":"Definition of Force Majeure","clause_text":"In the Contract, \"Force Majeure\" means an exceptional event or circumstance: (b) which such party could not reasonably have provided against before entering into the Contract; and (c) which, having arisen, such party could not reasonably have avoided or overcome; and (d) which is not substantially attributable to the other party. Force Majeure may include but is not limited to, exceptional events or circumstances of the kind listed below, provided that conditions (a) to (d) above are satisfied: (i) (insofar as they directly interfere with the progress of the Works and relate to and directly affect the Country) war, or (unless it is solely restricted to employees of the Contractor or of its Subcontractors and arising from the conduct of the Works) riot, commotion or disorder in the Country; or (ii) ionising radiation or contamination by radio-activity from any nuclear fuel or from any nuclear waste from the combustion of nuclear fuel, radio-active toxic explosives or other hazardous properties of any explosive nuclear assembly or nuclear components thereof.","general_condition":"In the Contract, \"Force Majeure\" means an exceptional event or circumstance: (b) which such party could not reasonably have provided against before entering into the Contract; and (c) which, having arisen, such party could not reasonably have avoided or overcome; and (d) which is not substantially attributable to the other party. Force Majeure may include but is not limited to, exceptional events or circumstances of the kind listed below, provided that conditions (a) to (d) above are satisfied: (i) (insofar as they directly interfere with the progress of the Works and relate to and directly affect the Country) war, or (unless it is solely restricted to employees of the Contractor or of its Subcontractors and arising from the conduct of the Works) riot, commotion or disorder in the Country; or (ii) ionising radiation or contamination by radio-activity from any nuclear fuel or from any nuclear waste from the combustion of nuclear fuel, radio-active toxic explosives or other hazardous properties of any explosive nuclear assembly or nuclear components thereof.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"In the Contract, \"Force Majeure\" means an exceptional event or circumstance: (b) which such party could not reasonably have provided against before entering into the Contract; and (c) which, having ar...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":157}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 158, '{"itemType":"CLAUSE","clause_number":"29.3","clause_title":"Notice of Force Majeure","clause_text":"If a party is or will be prevented from performing any of its obligations under the Contract by Force Majeure, then it shall give notice to the other party of the exceptional event or circumstance constituting the Force Majeure and shall specify the obligations, the performance of which is or will be prevented. The notice shall be given within fourteen (14) days after the party became aware, or should have become aware, of the relevant exceptional event or circumstance constituting Force Majeure.","general_condition":"If a party is or will be prevented from performing any of its obligations under the Contract by Force Majeure, then it shall give notice to the other party of the exceptional event or circumstance constituting the Force Majeure and shall specify the obligations, the performance of which is or will be prevented. The notice shall be given within fourteen (14) days after the party became aware, or should have become aware, of the relevant exceptional event or circumstance constituting Force Majeure.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If a party is or will be prevented from performing any of its obligations under the Contract by Force Majeure, then it shall give notice to the other party of the exceptional event or circumstance con...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":158}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 159, '{"itemType":"CLAUSE","clause_number":"29.4","clause_title":"Effect of Force Majeure","clause_text":"Neither party shall be considered to be in default or in breach of its obligations under the Contract to the extent that performance of such obligations is prevented by any exceptional event or circumstance constituting Force Majeure which arises after the date of the Contract provided that it has given notice to the other party in accordance with Clause 29.3 (Notice of Force Majeure).","general_condition":"Neither party shall be considered to be in default or in breach of its obligations under the Contract to the extent that performance of such obligations is prevented by any exceptional event or circumstance constituting Force Majeure which arises after the date of the Contract provided that it has given notice to the other party in accordance with Clause 29.3 (Notice of Force Majeure).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Neither party shall be considered to be in default or in breach of its obligations under the Contract to the extent that performance of such obligations is prevented by any exceptional event or circum...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":159}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 160, '{"itemType":"CLAUSE","clause_number":"29.5","clause_title":"Duty to Minimise Delay","clause_text":"The Contractor shall at all times use all reasonable endeavours to prevent any delay being caused by any exceptional event or circumstance constituting Force Majeure, to minimise any such delay as may be caused thereby and to do all that may be reasonably required to the satisfaction of the Project Manager and the Supervision Consultant, to proceed with the Works.","general_condition":"The Contractor shall at all times use all reasonable endeavours to prevent any delay being caused by any exceptional event or circumstance constituting Force Majeure, to minimise any such delay as may be caused thereby and to do all that may be reasonably required to the satisfaction of the Project Manager and the Supervision Consultant, to proceed with the Works.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Contractor shall at all times use all reasonable endeavours to prevent any delay being caused by any exceptional event or circumstance constituting Force Majeure, to minimise any such delay as may...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":160}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 161, '{"itemType":"CLAUSE","clause_number":"29.6","clause_title":"Force Majeure affecting Subcontractor","clause_text":"If any Force Majeure prevents or will prevent a Subcontractor of the Contractor from carrying out any of its obligations under its subcontract, then such event or circumstances shall not excuse the Contractor''s non-performance or entitle it to relief under such Clauses.","general_condition":"If any Force Majeure prevents or will prevent a Subcontractor of the Contractor from carrying out any of its obligations under its subcontract, then such event or circumstances shall not excuse the Contractor''s non-performance or entitle it to relief under such Clauses.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If any Force Majeure prevents or will prevent a Subcontractor of the Contractor from carrying out any of its obligations under its subcontract, then such event or circumstances shall not excuse the Co...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":161}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 162, '{"itemType":"CLAUSE","clause_number":"29.7","clause_title":"Termination in Consequence of Force Majeure","clause_text":"(a) If the carrying out of substantially all the Works in progress is prevented for a continuous period of not less than one hundred and eighty two (182) days by reason of Force Majeure of which notice has been given under Clause 29.3 (Notice of Force Majeure) then, notwithstanding that the Contractor may by reason thereof have been granted an extension of the Time for Completion, either party shall be entitled to serve upon the other twenty eight (28) days'' notice to terminate the Contract. (b) In the event that either party gives notice of termination for Force Majeure in accordance with the provisions of Sub-Clause (a) of this Clause 29.7 (and provided that the Contract has not been terminated for frustration in accordance with the provisions of Clause 29.1 (Frustration)) such notice shall operate to terminate the Contract at the end of the twenty eight (28) day period referred to in the said Sub-Clause (a) but without prejudice to: (i) the obligation of the Employer to pay to the Contractor a sum determined in accordance with Clause 29.8 (Payment on Frustration or Force Majeure); and (ii) any rights or obligations which may have accrued due between the parties prior to such termination.","general_condition":"(a) If the carrying out of substantially all the Works in progress is prevented for a continuous period of not less than one hundred and eighty two (182) days by reason of Force Majeure of which notice has been given under Clause 29.3 (Notice of Force Majeure) then, notwithstanding that the Contractor may by reason thereof have been granted an extension of the Time for Completion, either party shall be entitled to serve upon the other twenty eight (28) days'' notice to terminate the Contract. (b) In the event that either party gives notice of termination for Force Majeure in accordance with the provisions of Sub-Clause (a) of this Clause 29.7 (and provided that the Contract has not been terminated for frustration in accordance with the provisions of Clause 29.1 (Frustration)) such notice shall operate to terminate the Contract at the end of the twenty eight (28) day period referred to in the said Sub-Clause (a) but without prejudice to: (i) the obligation of the Employer to pay to the Contractor a sum determined in accordance with Clause 29.8 (Payment on Frustration or Force Majeure); and (ii) any rights or obligations which may have accrued due between the parties prior to such termination.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"(a) If the carrying out of substantially all the Works in progress is prevented for a continuous period of not less than one hundred and eighty two (182) days by reason of Force Majeure of which notic...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":162}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 163, '{"itemType":"CLAUSE","clause_number":"29.8","clause_title":"Payment on Frustration or Force Majeure","clause_text":"If the Contract is terminated or deemed terminated under Clause 29.7 (Termination in Consequence of Force Majeure) or Clause 29.1 (Frustration) the Contractor shall promptly cease all further work, except for such work as may be instructed by the Employer for the protection of life or property or for the safety of the Works, submit to the Project Manager all Contractor''s Drawings, comply with any instructions of the Employer in relation to Plant not yet delivered to Site, remove from the Site, with the prior written consent of the Project Manager, all Contractor''s Equipment and submit to the Project Manager in triplicate an Interim Certificate application prepared and accompanied in accordance with Clause 26.1 (Application for Interim Certificates) up to the time of termination or deemed termination. The Project Manager shall consider the Interim Certificate application, prepare an Interim Certificate up to such time and issue the same in accordance with Clause 26.2 (Issue of Interim Certificates) and payment thereof shall be made in accordance with Clause 27 (Payment). Thereafter, as soon as the required documents and evidence have become available the Contractor shall prepare and submit to the Project Manager in triplicate a final account (together with all such documents and evidence as the Project Manager may require in order to substantiate the same) which shows and breaks down in such form and with such detail as the Project Manager may require all amounts which in the Contractor''s opinion represent: (a) the Contract value of the Works carried out in accordance with the Contract up to the time of termination; (b) any expenditure, not otherwise included in the final account, reasonably incurred by the Contractor prior to the time of termination in the expectation of the Contract continuing and of carrying out the Works; (c) any amounts, not otherwise included in the final account, to which the Contractor is entitled or which the Employer or the Project Manager is entitled to deduct or otherwise recover, in each case, under any term, condition or provision of the Contract or otherwise in connection with or arising out of the Works; and (d) the reasonable cost not already allowed and paid for (and as certified by the Project Manager) of repatriation of the Contractor''s employees wholly engaged at the time of such termination on or in connection with the Works within the Country. Such final account shall be adjusted to reflect the amounts in respect of the foregoing items which in the Project Manager''s opinion are appropriate and the adjusted final account shall be agreed to and signed by the Project Manager and the Contractor. Thereupon the Contractor shall submit to the Project Manager in triplicate a Final Certificate application (prepared and accompanied in accordance with Clause 26.3 (Application for Final Certificates)) from which the Project Manager shall prepare and issue a Final Certificate in accordance with Clause 26.4 (Issue of Final Certificates) and payment thereof shall be made in accordance with Clause 27 (Payment). Notwithstanding the said Clause 26.4, where this Clause operates the Final Certificate shall not be evidence of the matters stated in paragraph (a) of Clause 26.3 (Application for Final Certificates).","general_condition":"If the Contract is terminated or deemed terminated under Clause 29.7 (Termination in Consequence of Force Majeure) or Clause 29.1 (Frustration) the Contractor shall promptly cease all further work, except for such work as may be instructed by the Employer for the protection of life or property or for the safety of the Works, submit to the Project Manager all Contractor''s Drawings, comply with any instructions of the Employer in relation to Plant not yet delivered to Site, remove from the Site, with the prior written consent of the Project Manager, all Contractor''s Equipment and submit to the Project Manager in triplicate an Interim Certificate application prepared and accompanied in accordance with Clause 26.1 (Application for Interim Certificates) up to the time of termination or deemed termination. The Project Manager shall consider the Interim Certificate application, prepare an Interim Certificate up to such time and issue the same in accordance with Clause 26.2 (Issue of Interim Certificates) and payment thereof shall be made in accordance with Clause 27 (Payment). Thereafter, as soon as the required documents and evidence have become available the Contractor shall prepare and submit to the Project Manager in triplicate a final account (together with all such documents and evidence as the Project Manager may require in order to substantiate the same) which shows and breaks down in such form and with such detail as the Project Manager may require all amounts which in the Contractor''s opinion represent: (a) the Contract value of the Works carried out in accordance with the Contract up to the time of termination; (b) any expenditure, not otherwise included in the final account, reasonably incurred by the Contractor prior to the time of termination in the expectation of the Contract continuing and of carrying out the Works; (c) any amounts, not otherwise included in the final account, to which the Contractor is entitled or which the Employer or the Project Manager is entitled to deduct or otherwise recover, in each case, under any term, condition or provision of the Contract or otherwise in connection with or arising out of the Works; and (d) the reasonable cost not already allowed and paid for (and as certified by the Project Manager) of repatriation of the Contractor''s employees wholly engaged at the time of such termination on or in connection with the Works within the Country. Such final account shall be adjusted to reflect the amounts in respect of the foregoing items which in the Project Manager''s opinion are appropriate and the adjusted final account shall be agreed to and signed by the Project Manager and the Contractor. Thereupon the Contractor shall submit to the Project Manager in triplicate a Final Certificate application (prepared and accompanied in accordance with Clause 26.3 (Application for Final Certificates)) from which the Project Manager shall prepare and issue a Final Certificate in accordance with Clause 26.4 (Issue of Final Certificates) and payment thereof shall be made in accordance with Clause 27 (Payment). Notwithstanding the said Clause 26.4, where this Clause operates the Final Certificate shall not be evidence of the matters stated in paragraph (a) of Clause 26.3 (Application for Final Certificates).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If the Contract is terminated or deemed terminated under Clause 29.7 (Termination in Consequence of Force Majeure) or Clause 29.1 (Frustration) the Contractor shall promptly cease all further work, ex...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":163}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 164, '{"itemType":"CLAUSE","clause_number":"29.9","clause_title":"Definition of War","clause_text":"For the purposes of this Clause 29 (Frustration and Force Majeure) the term \"war\" shall mean war and other hostilities (whether war be declared or not), invasion, act of foreign enemies, rebellion, revolution, insurrection or military or usurped power or civil war.","general_condition":"For the purposes of this Clause 29 (Frustration and Force Majeure) the term \"war\" shall mean war and other hostilities (whether war be declared or not), invasion, act of foreign enemies, rebellion, revolution, insurrection or military or usurped power or civil war.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"For the purposes of this Clause 29 (Frustration and Force Majeure) the term \"war\" shall mean war and other hostilities (whether war be declared or not), invasion, act of foreign enemies, rebellion, re...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":164}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 165, '{"itemType":"CLAUSE","clause_number":"30.1","clause_title":"Notice to Correct","clause_text":"If the Contractor fails to carry out any obligation under the Contract, the Project Manager may by written notice require the Contractor to make good the failure and to remedy it within a specified time.","general_condition":"If the Contractor fails to carry out any obligation under the Contract, the Project Manager may by written notice require the Contractor to make good the failure and to remedy it within a specified time.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If the Contractor fails to carry out any obligation under the Contract, the Project Manager may by written notice require the Contractor to make good the failure and to remedy it within a specified ti...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":165}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 166, '{"itemType":"CLAUSE","clause_number":"30.2","clause_title":"Definition of Default","clause_text":"If the Project Manager shall certify in writing that in his opinion the Contractor: (a) has failed to comply with a notice under Clause 30.1 (Notice to Correct); or (b) has failed to comply with Clause 7 (Bonds) in respect of the Performance Bond; or (c) is in breach of any of the provisions of Sub-Clause 27.5(a) (Third Party Payments); or (e) without reasonable excuse has failed to commence the Works within seven (7) days after the Commencement Date or thereafter has suspended the progress of the Works or any Section or Portion thereof for an aggregate of not less than twenty eight (28) days; or (f) despite previous written warnings from the Project Manager, is not carrying out the Works or any Section or Portion thereof in accordance with the Contract or is refusing or failing to observe or perform any other term, provision or condition of the Contract; or (g) (i) without the prior written consent of the Employer has (otherwise than as permitted by Clause 3.1 (Assignment)) assigned or purported to assign the Contract or any part thereof or any benefit, obligation or interest therein or thereunder; or (ii) has or has purported to sublet the whole of the Works; or (iii) without the prior written consent of the Project Manager and the Supervision Consultant in any case where in accordance with Clause 3.2 (Subletting) such consent is requisite has or has purported to sublet any part of the Works; or (h) has for a period of not less than twenty eight (28) days refused or failed to act in accordance with any order of the Supervision Consultant under Clause 11.7 (Rejection and Remedy); or (i) has so failed to complete within the Time for Completion that the aggregate amount of all such payments as are due by the Contractor to the Employer under paragraph (a)(i) of Clause 19.3 (Liquidated Damages) shall at any time have reached the amount stated in the Particular Conditions; or (j) has compounded with or negotiated for any composition with or called any meeting of its creditors generally, has had a receiver of all or any of its assets appointed, has committed any act of bankruptcy or insolvency, has had a receiving order made against it, has had an administration order made against it, has entered into any liquidation (other than a voluntary liquidation for the purposes of amalgamation or reconstruction on terms previously approved in writing by the Employer) or has taken or suffered any action analogous to any of the foregoing under the laws of any applicable jurisdiction; or (k) is in breach of any of the provisions of Clause 15.1 (General Covenant); then, subject to the Employer giving fourteen (14) days'' prior written notice to the Contractor, the Employer may enter upon the Site and the Works, and expel the Contractor therefrom. The Contractor shall then forthwith leave the Site and the Works and submit to the Project Manager all Contractor''s Drawings. Provided that the Contractor shall comply immediately with any instructions included in the notice for the protection of life or property or for the safety of the Works or in relation to Plant not yet delivered to Site.","general_condition":"If the Project Manager shall certify in writing that in his opinion the Contractor: (a) has failed to comply with a notice under Clause 30.1 (Notice to Correct); or (b) has failed to comply with Clause 7 (Bonds) in respect of the Performance Bond; or (c) is in breach of any of the provisions of Sub-Clause 27.5(a) (Third Party Payments); or (e) without reasonable excuse has failed to commence the Works within seven (7) days after the Commencement Date or thereafter has suspended the progress of the Works or any Section or Portion thereof for an aggregate of not less than twenty eight (28) days; or (f) despite previous written warnings from the Project Manager, is not carrying out the Works or any Section or Portion thereof in accordance with the Contract or is refusing or failing to observe or perform any other term, provision or condition of the Contract; or (g) (i) without the prior written consent of the Employer has (otherwise than as permitted by Clause 3.1 (Assignment)) assigned or purported to assign the Contract or any part thereof or any benefit, obligation or interest therein or thereunder; or (ii) has or has purported to sublet the whole of the Works; or (iii) without the prior written consent of the Project Manager and the Supervision Consultant in any case where in accordance with Clause 3.2 (Subletting) such consent is requisite has or has purported to sublet any part of the Works; or (h) has for a period of not less than twenty eight (28) days refused or failed to act in accordance with any order of the Supervision Consultant under Clause 11.7 (Rejection and Remedy); or (i) has so failed to complete within the Time for Completion that the aggregate amount of all such payments as are due by the Contractor to the Employer under paragraph (a)(i) of Clause 19.3 (Liquidated Damages) shall at any time have reached the amount stated in the Particular Conditions; or (j) has compounded with or negotiated for any composition with or called any meeting of its creditors generally, has had a receiver of all or any of its assets appointed, has committed any act of bankruptcy or insolvency, has had a receiving order made against it, has had an administration order made against it, has entered into any liquidation (other than a voluntary liquidation for the purposes of amalgamation or reconstruction on terms previously approved in writing by the Employer) or has taken or suffered any action analogous to any of the foregoing under the laws of any applicable jurisdiction; or (k) is in breach of any of the provisions of Clause 15.1 (General Covenant); then, subject to the Employer giving fourteen (14) days'' prior written notice to the Contractor, the Employer may enter upon the Site and the Works, and expel the Contractor therefrom. The Contractor shall then forthwith leave the Site and the Works and submit to the Project Manager all Contractor''s Drawings. Provided that the Contractor shall comply immediately with any instructions included in the notice for the protection of life or property or for the safety of the Works or in relation to Plant not yet delivered to Site.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If the Project Manager shall certify in writing that in his opinion the Contractor: (a) has failed to comply with a notice under Clause 30.1 (Notice to Correct); or (b) has failed to comply with Claus...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":166}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 167, '{"itemType":"CLAUSE","clause_number":"30.3","clause_title":"Termination following Default","clause_text":"The Employer may within fourteen (14) days after the expiry of the fourteen (14) day notice referred to in Clause 30.2 (Definition of Default) give a further written notice to the Contractor which shall be effective to terminate the Contract immediately. Provided that in the case of paragraphs (c), (j) or (k) of Clause 30.2 (Definition of Default), the Employer may by written notice terminate the Contract immediately. Any termination under this Clause 30.3 shall be without prejudice to: (a) any rights for damages or compensation to which the Employer may be entitled under any applicable law; and (b) any rights or obligations which may have accrued due between the parties prior to such termination.","general_condition":"The Employer may within fourteen (14) days after the expiry of the fourteen (14) day notice referred to in Clause 30.2 (Definition of Default) give a further written notice to the Contractor which shall be effective to terminate the Contract immediately. Provided that in the case of paragraphs (c), (j) or (k) of Clause 30.2 (Definition of Default), the Employer may by written notice terminate the Contract immediately. Any termination under this Clause 30.3 shall be without prejudice to: (a) any rights for damages or compensation to which the Employer may be entitled under any applicable law; and (b) any rights or obligations which may have accrued due between the parties prior to such termination.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Employer may within fourteen (14) days after the expiry of the fourteen (14) day notice referred to in Clause 30.2 (Definition of Default) give a further written notice to the Contractor which sha...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":167}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 168, '{"itemType":"CLAUSE","clause_number":"30.4","clause_title":"Continuation following Default","clause_text":"Unless the Contract is terminated in accordance with Clause 30.3 (Termination following Default) the same shall continue in force and the entry by the Employer and expulsion of the Contractor shall not void the Contract, release the Contractor from any of its obligations or liabilities under the Contract or adversely affect any of the rights or powers conferred on the Employer, the Project Manager or the Supervision Consultant by the Contract. Following such entry and expulsion the Employer may (without prejudice to any other remedy) itself complete the Works and/or employ any other contractor or contractors to complete the Works at the Contractor''s risk and without any obligation to submit any Taking-Over Certificate or Final Certificate to the Contractor. For this purpose the Employer and/or any such other contractor may have free use of any of the Contractor''s Equipment without being responsible to the Contractor for loss or damage, wear and tear thereof. Further, the Employer may at any time sell any of the Contractor''s Equipment (for the purpose of which sale the Employer is hereby irrevocably appointed as the agent of the Contractor) and apply the nett proceeds of sale in or towards satisfaction of any monies due or which may become due from the Contractor. Following such entry and expulsion, the Contractor shall comply immediately at its own Cost with any instruction given by the Project Manager to remove from the Site and the Works any Contractor''s Equipment. Such instruction by the Project Manager may be given at any time following such entry and expulsion.","general_condition":"Unless the Contract is terminated in accordance with Clause 30.3 (Termination following Default) the same shall continue in force and the entry by the Employer and expulsion of the Contractor shall not void the Contract, release the Contractor from any of its obligations or liabilities under the Contract or adversely affect any of the rights or powers conferred on the Employer, the Project Manager or the Supervision Consultant by the Contract. Following such entry and expulsion the Employer may (without prejudice to any other remedy) itself complete the Works and/or employ any other contractor or contractors to complete the Works at the Contractor''s risk and without any obligation to submit any Taking-Over Certificate or Final Certificate to the Contractor. For this purpose the Employer and/or any such other contractor may have free use of any of the Contractor''s Equipment without being responsible to the Contractor for loss or damage, wear and tear thereof. Further, the Employer may at any time sell any of the Contractor''s Equipment (for the purpose of which sale the Employer is hereby irrevocably appointed as the agent of the Contractor) and apply the nett proceeds of sale in or towards satisfaction of any monies due or which may become due from the Contractor. Following such entry and expulsion, the Contractor shall comply immediately at its own Cost with any instruction given by the Project Manager to remove from the Site and the Works any Contractor''s Equipment. Such instruction by the Project Manager may be given at any time following such entry and expulsion.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Unless the Contract is terminated in accordance with Clause 30.3 (Termination following Default) the same shall continue in force and the entry by the Employer and expulsion of the Contractor shall no...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":168}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 169, '{"itemType":"CLAUSE","clause_number":"30.5","clause_title":"Valuation at Entry and Expulsion","clause_text":"Where Clause 30.4 (Continuation following Default) applies, the Project Manager shall, as soon as may be practicable after such entry by the Employer and expulsion of the Contractor, make such investigation and enquiries as he may think necessary and from the results thereof shall prepare and sign a final account which in his opinion shows all amounts which should have been within paragraphs (a) and (c) of Clause 29.8 (Payment on Frustration or Force Majeure) had the entry and expulsion been a termination under Clause 29 (Frustration and Force Majeure).","general_condition":"Where Clause 30.4 (Continuation following Default) applies, the Project Manager shall, as soon as may be practicable after such entry by the Employer and expulsion of the Contractor, make such investigation and enquiries as he may think necessary and from the results thereof shall prepare and sign a final account which in his opinion shows all amounts which should have been within paragraphs (a) and (c) of Clause 29.8 (Payment on Frustration or Force Majeure) had the entry and expulsion been a termination under Clause 29 (Frustration and Force Majeure).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Where Clause 30.4 (Continuation following Default) applies, the Project Manager shall, as soon as may be practicable after such entry by the Employer and expulsion of the Contractor, make such investi...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":169}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 170, '{"itemType":"CLAUSE","clause_number":"30.6","clause_title":"Payments after Entry and Expulsion","clause_text":"Where Clause 30.4 (Continuation following Default) applies, the Employer shall not after the entry and expulsion be liable to make any payment whatsoever to the Contractor, whether in connection with the Contract or the Works or for any other reason of any kind until the expiration of the last date of expiry of a Defects Liability Period or any extension thereof for a Section or Portion of the Works, and thereafter until the Cost of completing the Works and all other Costs incurred by the Employer in relation thereto have been ascertained and the amount thereof certified by the Project Manager. The Employer shall (without prejudice to his right to recover from the Contractor damages arising out of the Contractor''s default together with damages for all consequential loss) then be liable to pay to the Contractor only an amount certified by the Project Manager and calculated as being: (a) the amount shown by the final account prepared in accordance with Clause 30.5 (Valuation at Entry and Expulsion); less (b) the total of all payments already made to the Contractor pursuant to Interim Certificates or otherwise and to any Nominated Subcontractor under Clause 24.2 (Delayed Payments to Nominated Subcontractors); less (c) any amount by which the Costs of completing the Works and other Costs incurred by the Employer in relation thereto exceed the difference between the amount which would have been payable to the Contractor upon due completion of the Works by it and the amounts shown by the final account; and less (d) any amount which is or becomes payable under Clause 19.3 (Liquidated Damages). If the foregoing calculation should produce a negative result then the Contractor shall upon demand pay a sum equal to that result to the Employer and such sum shall be deemed a debt due from the Contractor to the Employer and be recoverable accordingly.","general_condition":"Where Clause 30.4 (Continuation following Default) applies, the Employer shall not after the entry and expulsion be liable to make any payment whatsoever to the Contractor, whether in connection with the Contract or the Works or for any other reason of any kind until the expiration of the last date of expiry of a Defects Liability Period or any extension thereof for a Section or Portion of the Works, and thereafter until the Cost of completing the Works and all other Costs incurred by the Employer in relation thereto have been ascertained and the amount thereof certified by the Project Manager. The Employer shall (without prejudice to his right to recover from the Contractor damages arising out of the Contractor''s default together with damages for all consequential loss) then be liable to pay to the Contractor only an amount certified by the Project Manager and calculated as being: (a) the amount shown by the final account prepared in accordance with Clause 30.5 (Valuation at Entry and Expulsion); less (b) the total of all payments already made to the Contractor pursuant to Interim Certificates or otherwise and to any Nominated Subcontractor under Clause 24.2 (Delayed Payments to Nominated Subcontractors); less (c) any amount by which the Costs of completing the Works and other Costs incurred by the Employer in relation thereto exceed the difference between the amount which would have been payable to the Contractor upon due completion of the Works by it and the amounts shown by the final account; and less (d) any amount which is or becomes payable under Clause 19.3 (Liquidated Damages). If the foregoing calculation should produce a negative result then the Contractor shall upon demand pay a sum equal to that result to the Employer and such sum shall be deemed a debt due from the Contractor to the Employer and be recoverable accordingly.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Where Clause 30.4 (Continuation following Default) applies, the Employer shall not after the entry and expulsion be liable to make any payment whatsoever to the Contractor, whether in connection with ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":170}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 171, '{"itemType":"CLAUSE","clause_number":"30.7","clause_title":"Assignment of Benefit of Agreement","clause_text":"Unless prohibited by law, the Contractor shall, if so instructed by the Project Manager, after such entry by the Employer referred to in Clause 30.2 (Definition of Default), within fourteen (14) days of the receipt of such instruction assign to the Employer the benefit (and at the Employer''s option, the burden) of any agreement for the provision of any Plant, Contractor''s Equipment or services and/or for the carrying out of any work for the purposes of the Contract which the Contractor may have entered into with any Subcontractors.","general_condition":"Unless prohibited by law, the Contractor shall, if so instructed by the Project Manager, after such entry by the Employer referred to in Clause 30.2 (Definition of Default), within fourteen (14) days of the receipt of such instruction assign to the Employer the benefit (and at the Employer''s option, the burden) of any agreement for the provision of any Plant, Contractor''s Equipment or services and/or for the carrying out of any work for the purposes of the Contract which the Contractor may have entered into with any Subcontractors.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Unless prohibited by law, the Contractor shall, if so instructed by the Project Manager, after such entry by the Employer referred to in Clause 30.2 (Definition of Default), within fourteen (14) days ...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":171}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 172, '{"itemType":"CLAUSE","clause_number":"31.1","clause_title":"Termination for Convenience","clause_text":"The Employer may at any time by written notice to the Contractor terminate the Contract for the Employer''s convenience.","general_condition":"The Employer may at any time by written notice to the Contractor terminate the Contract for the Employer''s convenience.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"The Employer may at any time by written notice to the Contractor terminate the Contract for the Employer''s convenience.","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":172}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 173, '{"itemType":"CLAUSE","clause_number":"31.2","clause_title":"Default of Employer","clause_text":"If the Employer: (a) has failed to pay to the Contractor the nett amount or final nett amount shown by any Interim Certificate or Final Certificate within thirty (30) days after expiry of the period of thirty (30) days under Sub-Clause (b) of Clause 27.1 (Time of Payment) and such failure is not due to the making by the Employer of any deduction or recovery from the Contractor which the Employer may be entitled to make under the Contract or otherwise; or (b) has compounded with or negotiated for any composition with or called any meeting of its creditors generally, has had a receiver of all or any of its assets appointed, has had a receiving order made against it, has had an administration order made against it, has entered into any liquidation (other than a voluntary liquidation for the purposes of amalgamation or reconstruction) or has taken or suffered any action analogous to any of the foregoing under the laws of the Country; then, subject (in any case where the event concerned is remediable) to the Contractor giving fourteen (14) days prior written notice to the Employer with a copy to the Project Manager and the event concerned not having been remedied before expiry of that notice, the Contractor may within fourteen (14) days after such expiry give a further written notice to the Employer which shall be effective to terminate the Contract immediately.","general_condition":"If the Employer: (a) has failed to pay to the Contractor the nett amount or final nett amount shown by any Interim Certificate or Final Certificate within thirty (30) days after expiry of the period of thirty (30) days under Sub-Clause (b) of Clause 27.1 (Time of Payment) and such failure is not due to the making by the Employer of any deduction or recovery from the Contractor which the Employer may be entitled to make under the Contract or otherwise; or (b) has compounded with or negotiated for any composition with or called any meeting of its creditors generally, has had a receiver of all or any of its assets appointed, has had a receiving order made against it, has had an administration order made against it, has entered into any liquidation (other than a voluntary liquidation for the purposes of amalgamation or reconstruction) or has taken or suffered any action analogous to any of the foregoing under the laws of the Country; then, subject (in any case where the event concerned is remediable) to the Contractor giving fourteen (14) days prior written notice to the Employer with a copy to the Project Manager and the event concerned not having been remedied before expiry of that notice, the Contractor may within fourteen (14) days after such expiry give a further written notice to the Employer which shall be effective to terminate the Contract immediately.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If the Employer: (a) has failed to pay to the Contractor the nett amount or final nett amount shown by any Interim Certificate or Final Certificate within thirty (30) days after expiry of the period o...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":173}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 174, '{"itemType":"CLAUSE","clause_number":"31.3","clause_title":"Payment on Termination","clause_text":"If the Contract is terminated under Clause 31.1 (Termination for Convenience) or Clause 31.2 (Default of Employer) such termination shall be without prejudice to any rights or obligations which may have accrued due between the parties prior to such termination. Following such termination, the Contractor shall promptly cease all further work, except for such work as may be instructed by the Employer for the protection of life or property or for the safety of the Works, submit to the Project Manager all Contractor''s Drawings, comply with any instructions of the Employer in relation to Plant not yet delivered to Site, remove from the Site, with the prior consent of the Project Manager, all Contractor''s Equipment and submit to the Project Manager in triplicate an Interim Certificate application prepared and accompanied in accordance with Clause 26.1 (Application for Interim Certificates) up to the time of termination. The Project Manager shall consider the Interim Certificate application, prepare an Interim Certificate up to such time and issue the same in accordance with Clause 26.2 (Issue of Interim Certificates) and payment thereof shall be made in accordance with Clause 27 (Payment). Thereafter, as soon as the required documents and evidence have become available the Contractor shall prepare and submit to the Project Manager in triplicate a final account (together with all such documents and evidence as the Project Manager may require in order to substantiate the same) which shows and breaks down in such form and with such detail as the Project Manager may require all amounts which in the Contractor''s opinion represent the items listed in paragraphs (a) to (d) inclusive of Clause 29.8 (Payment on Frustration or Force Majeure) together with the amount which in the Contractor''s opinion represents any other reasonably foreseeable loss or damage to the Contractor arising out of or in connection with such termination but excluding any loss of use or profit or indirect or consequential loss. Such final account shall be adjusted as appropriate to reflect the amounts in respect of the foregoing items which in the Project Manager''s opinion are fair and reasonable and the adjusted final account shall be agreed to and signed by the Project Manager and the Contractor. Thereupon the Contractor shall submit to the Project Manager in triplicate a Final Certificate application (prepared and accompanied in accordance with Clause 26.3 (Application for Final Certificates)) from which the Project Manager shall prepare and issue a Final Certificate in accordance with Clause 26.4 (Issue of Final Certificates) and payment thereof shall be made in accordance with Clause 27 (Payment). Notwithstanding the said Clause 26.4, where this Clause operates the Final Certificate shall not be evidence of the matters stated in paragraph (a) of Clause 26.3 (Application for Final Certificates).","general_condition":"If the Contract is terminated under Clause 31.1 (Termination for Convenience) or Clause 31.2 (Default of Employer) such termination shall be without prejudice to any rights or obligations which may have accrued due between the parties prior to such termination. Following such termination, the Contractor shall promptly cease all further work, except for such work as may be instructed by the Employer for the protection of life or property or for the safety of the Works, submit to the Project Manager all Contractor''s Drawings, comply with any instructions of the Employer in relation to Plant not yet delivered to Site, remove from the Site, with the prior consent of the Project Manager, all Contractor''s Equipment and submit to the Project Manager in triplicate an Interim Certificate application prepared and accompanied in accordance with Clause 26.1 (Application for Interim Certificates) up to the time of termination. The Project Manager shall consider the Interim Certificate application, prepare an Interim Certificate up to such time and issue the same in accordance with Clause 26.2 (Issue of Interim Certificates) and payment thereof shall be made in accordance with Clause 27 (Payment). Thereafter, as soon as the required documents and evidence have become available the Contractor shall prepare and submit to the Project Manager in triplicate a final account (together with all such documents and evidence as the Project Manager may require in order to substantiate the same) which shows and breaks down in such form and with such detail as the Project Manager may require all amounts which in the Contractor''s opinion represent the items listed in paragraphs (a) to (d) inclusive of Clause 29.8 (Payment on Frustration or Force Majeure) together with the amount which in the Contractor''s opinion represents any other reasonably foreseeable loss or damage to the Contractor arising out of or in connection with such termination but excluding any loss of use or profit or indirect or consequential loss. Such final account shall be adjusted as appropriate to reflect the amounts in respect of the foregoing items which in the Project Manager''s opinion are fair and reasonable and the adjusted final account shall be agreed to and signed by the Project Manager and the Contractor. Thereupon the Contractor shall submit to the Project Manager in triplicate a Final Certificate application (prepared and accompanied in accordance with Clause 26.3 (Application for Final Certificates)) from which the Project Manager shall prepare and issue a Final Certificate in accordance with Clause 26.4 (Issue of Final Certificates) and payment thereof shall be made in accordance with Clause 27 (Payment). Notwithstanding the said Clause 26.4, where this Clause operates the Final Certificate shall not be evidence of the matters stated in paragraph (a) of Clause 26.3 (Application for Final Certificates).","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"If the Contract is terminated under Clause 31.1 (Termination for Convenience) or Clause 31.2 (Default of Employer) such termination shall be without prejudice to any rights or obligations which may ha...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":174}'::jsonb),
  ('cfb1883c-bf70-410d-afe2-8273467ea099', 'GENERAL', 175, '{"itemType":"CLAUSE","clause_number":"32.1","clause_title":"Notices","clause_text":"Any notice, consent, approval, certificate, determination or request required to be given or issued pursuant to the provisions of the Contract shall be delivered by hand (against receipt), sent by mail, registered mail or courier or transmitted by telefax to that other party at the address stated in the Particular Conditions (or to such other address as the other party has specified by giving fifteen (15) days'' prior written notice). Provided that: (a) if the recipient has not stated otherwise when requesting an approval or consent, it may be sent to the address from which the request was issued; and (b) if a notice, consent, approval, certificate, determination or request has been delivered, sent or transmitted (as applicable) in accordance with the above but received on a non-working day or after business hours in the place of receipt it will only be deemed to be given or issued on the next working day in that place.","general_condition":"Any notice, consent, approval, certificate, determination or request required to be given or issued pursuant to the provisions of the Contract shall be delivered by hand (against receipt), sent by mail, registered mail or courier or transmitted by telefax to that other party at the address stated in the Particular Conditions (or to such other address as the other party has specified by giving fifteen (15) days'' prior written notice). Provided that: (a) if the recipient has not stated otherwise when requesting an approval or consent, it may be sent to the address from which the request was issued; and (b) if a notice, consent, approval, certificate, determination or request has been delivered, sent or transmitted (as applicable) in accordance with the above but received on a non-working day or after business hours in the place of receipt it will only be deemed to be given or issued on the next working day in that place.","particular_condition":"","condition_type":"General","has_time_frame":false,"time_frames":[],"comparison":[{"color":"green","comment":"General Condition from Atrium contract.","excerpt_general":"Any notice, consent, approval, certificate, determination or request required to be given or issued pursuant to the provisions of the Contract shall be delivered by hand (against receipt), sent by mai...","excerpt_particular":"","type":"NO_CHANGE"}],"orderIndex":175}'::jsonb);
